INSERT INTO `chitietsanpham` (`id`, `masp`, `nhacungcap`, `nhaxuatban`, `tacgia`, `hinhthuc`, `nguoidich`, `mota`, `noidung`, `namxuatban`, `trongluong`, `sotrang`) VALUES
(39, 1, '1980 Books', 'NXB Đại học kinh tế Quốc dân', 'Scott Gerber, Ryan Pauph', 'Bìa mềm', 'Hà Tiến Hưng', '<p>Scott Gerber l&agrave; nh&agrave; đồng s&aacute;ng lập, CEO của The Commmunity Company. Anh l&agrave; cố vấn đặc biệt cho Forbes Media, một diễn giả được y&ecirc;u th&iacute;ch, một nh&agrave; b&igrave;nh luận tr&ecirc;n truyền h&igrave;nh về kinh doanh, v&agrave; l&agrave; t&aacute;c giả của cuốn Never Get a &ldquo;Real&rdquo; Job</p>\r\n', '<p>Những cơ hội tuyệt vời trong kinh doanh sẽ xuất hiện khi gặp được những con người ph&ugrave; hợp.</p>\r\n\r\n<p>Ho&agrave;n to&agrave;n xuất ph&aacute;t từ thực tiễn &ldquo;Kỹ năng đ&agrave;m ph&aacute;n hiệu quả trong kinh doanh&rdquo; sẽ gi&uacute;p bạn:</p>\r\n\r\n<p>Chuẩn bị kỹ lưỡng cho cuộc đ&agrave;m ph&aacute;n</p>\r\n\r\n<p>Lắng nghe kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>L&agrave;m chủ cảm x&uacute;c khi giao tiếp</p>\r\n\r\n<p>Kỹ năng thuyết phục kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>Tư duy chiến lược trong kinh doanh</p>\r\n\r\n<p>Kỹ năng đặt c&acirc;u hỏi trong đ&agrave;m ph&aacute;n</p>\r\n\r\n<p>D&ugrave; bạn mới chỉ bắt đầu khởi nghiệp hay đ&atilde; ph&aacute;t triển th&agrave;nh c&ocirc;ng rực rỡ th&igrave; Scott v&agrave; Ryan vẫn sẽ dạy cho bạn c&aacute;ch để ph&aacute;t triển mối quan hệ x&atilde; hội đỉnh cao nhất.</p>\r\n\r\n<p>V&agrave;i n&eacute;t về t&aacute;c giả:</p>\r\n\r\n<p>Scott Gerber l&agrave; nh&agrave; đồng s&aacute;ng lập, CEO của The Commmunity Company. Anh l&agrave; cố vấn đặc biệt cho Forbes Media, một diễn giả được y&ecirc;u th&iacute;ch, một nh&agrave; b&igrave;nh luận tr&ecirc;n truyền h&igrave;nh về kinh doanh, v&agrave; l&agrave; t&aacute;c giả của cuốn Never Get a &ldquo;Real&rdquo; Job</p>\r\n\r\n<p>Ryan Pauph l&agrave; nh&agrave; đồng s&aacute;ng lập v&agrave; l&agrave; COO của The Commmunity Company. Được mệnh danh l&agrave; &ldquo;huyền thoại trong cộng đồng trực tuyến kiến tạo thế giới&rdquo;, Ryan tập trung v&agrave;o việc tạo ra những trải nghiệm kết nối mạnh mẽ giữa c&aacute;c th&agrave;nh vi&ecirc;n v&agrave; mang lại kết quả kinh doanh t&iacute;ch cực cho h&agrave;ng ng&agrave;n doanh nh&acirc;n tr&ecirc;n cả nước.</p>\r\n', 2019, 350, 336),
(40, 2, '1980 Books', 'NXB Lao Động', 'Mackenzie Kyle', 'Bìa mềm', 'Trương Thị Quế Anh', '<p>Với b&uacute;t ph&aacute;p t&agrave;i hoa, Mackenzie Kyle đ&atilde; lồng gh&eacute;p những nguy&ecirc;n l&yacute; thu phục l&ograve;ng người nhằm n&acirc;ng cao hiệu suất v&agrave; cải thiện chất lượng hoạt động doanh nghiệp vốn kh&ocirc; khan, cứng nhắc v&agrave;o cuốn &ldquo;tiểu thuyết kinh doanh&rdquo; c&oacute; một kh&ocirc;ng hai mang t&ecirc;n: &ldquo;Thuật đắc nh&acirc;n t&acirc;m n&acirc;ng tầm hiệu suất&rdquo;.</p>\r\n', '<p>Thuật Đắc Nh&acirc;n T&acirc;m N&acirc;ng Tầm Hiệu Suất</p>\r\n\r\n<p>Chưa bao giờ, thuật đắc nh&acirc;n t&acirc;m, hay b&iacute; quyết thấu hiểu l&ograve;ng người để tạo động lực cho từng c&aacute; nh&acirc;n, khai mở tiềm năng đội nh&oacute;m v&agrave; tăng cường sức mạnh kết nối trong tổ chức lại cấp thiết đến thế. Để được l&ograve;ng thi&ecirc;n hạ với tư c&aacute;ch doanh nghiệp l&agrave; b&agrave;i to&aacute;n h&oacute;c b&uacute;a đối với mọi doanh nghiệp.</p>\r\n\r\n<p>Với b&uacute;t ph&aacute;p t&agrave;i hoa, Mackenzie Kyle đ&atilde; lồng gh&eacute;p những nguy&ecirc;n l&yacute; thu phục l&ograve;ng người nhằm n&acirc;ng cao hiệu suất v&agrave; cải thiện chất lượng hoạt động doanh nghiệp vốn kh&ocirc; khan, cứng nhắc v&agrave;o cuốn &ldquo;tiểu thuyết kinh doanh&rdquo; c&oacute; một kh&ocirc;ng hai mang t&ecirc;n: &ldquo;Thuật đắc nh&acirc;n t&acirc;m n&acirc;ng tầm hiệu suất&rdquo;.</p>\r\n\r\n<p>Qua h&agrave;ng loạt c&acirc;u chuyện mang t&iacute;nh c&aacute; nh&acirc;n gần gũi, t&aacute;c phẩm đưa ra nguy&ecirc;n l&yacute; đắc nh&acirc;n t&acirc;m căn bản gi&uacute;p thấu hiểu v&agrave; khai mở động lực s&acirc;u k&iacute;n trong mỗi con người. Mackenzie Kyle chứng minh tầm quan trọng của việc li&ecirc;n kết động lực đối với th&agrave;nh c&ocirc;ng trong kinh doanh v&agrave; cuộc sống.</p>\r\n\r\n<p>Sở hữu &quot;Thuật đắc nh&acirc;n t&acirc;m n&acirc;ng tầm hiệu suất&quot;, v&agrave; bạn sẽ c&oacute; c&acirc;u trả lời cho b&agrave;i to&aacute;n: L&agrave;m thế n&agrave;o để săn t&igrave;m động lực v&agrave; th&uacute;c đẩy h&agrave;nh vi trong thời đại 4.0 đầy biến động?</p>\r\n', 2018, 350, 324),
(41, 3, '1980 Books', 'NXB Phụ Nữ', 'Jessica Higgins', 'Bìa mềm', 'Phạm Lan Anh', '<p>Kh&ocirc;ng thiếu những lời khuy&ecirc;n về việc l&agrave;m thế n&agrave;o để đạt hiệu quả giao tiếp trong c&ocirc;ng việc. Vấn đề l&agrave; bạn c&oacute; biết phương ph&aacute;p n&agrave;o thực sự được chứng minh l&agrave; c&oacute; hiệu quả v&agrave; bạn n&ecirc;n h&agrave;nh động như n&agrave;o để c&oacute; kết quả tốt nhất.</p>\r\n', '<p>Kh&ocirc;ng thiếu những lời khuy&ecirc;n về việc l&agrave;m thế n&agrave;o để đạt hiệu quả giao tiếp trong c&ocirc;ng việc. Vấn đề l&agrave; bạn c&oacute; biết phương ph&aacute;p n&agrave;o thực sự được chứng minh l&agrave; c&oacute; hiệu quả v&agrave; bạn n&ecirc;n h&agrave;nh động như n&agrave;o để c&oacute; kết quả tốt nhất.</p>\r\n\r\n<p>Ho&agrave;n to&agrave;n xuất ph&aacute;t từ thực tiễn &ldquo;Nghệ thuật giao tiếp nơi c&ocirc;ng sở&rdquo; sẽ gi&uacute;p bạn:</p>\r\n\r\n<p>Thuyết tr&igrave;nh trước đ&aacute;m đ&ocirc;ng</p>\r\n\r\n<p>Đ&agrave;m ph&aacute;n chuy&ecirc;n &nbsp;nghiệp</p>\r\n\r\n<p>Tạo dựng mối quan hệ tốt đẹp với đồng nghiệp</p>\r\n\r\n<p>X&acirc;y dựng tinh thần l&agrave;m việc nh&oacute;m hiệu quả</p>\r\n\r\n<p>L&agrave;m chủ ng&ocirc;n ngữ cơ thể</p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y truyền tải th&ocirc;ng điệp r&otilde; r&agrave;ng về việc n&ecirc;n l&agrave;m v&agrave; kh&ocirc;ng n&ecirc;n l&agrave;m. Bất cứ ai muốn l&agrave;m việc tốt hơn, bất kể cấp bậc hay tuổi t&aacute;c, đều n&ecirc;n c&oacute; cuốn s&aacute;ch n&agrave;y trong tay.</p>\r\n', 2019, 280, 252),
(42, 4, '1980 Books', 'NXB Đại học kinh tế Quốc dân', 'Martin Krengel', 'Bìa mềm', 'Ngân Hà', '<p>Học đại học thế n&agrave;o cho hiệu quả? Rất nhiều t&acirc;n sinh vi&ecirc;n nghĩ họ chỉ cần &aacute;p dụng y nguy&ecirc;n c&aacute;ch học từ thời phổ th&ocirc;ng l&agrave; đ&atilde; đủ. Nhưng khối lượng kiến thức ở cấp đại học ho&agrave;n to&agrave;n vượt trội so với những g&igrave; bạn từng trải qua. Học đại học kh&ocirc;ng chỉ l&agrave; &ocirc;m s&aacute;ch vở đối ph&oacute; với c&aacute;c b&agrave;i kiểm tra, m&agrave; bạn c&ograve;n phải d&agrave;nh nhiều thời gian để tham gia c&aacute;c hoạt động ngo&agrave;i giờ học (tham gia c&acirc;u lạc bộ, c&aacute;c hoạt động ngoại kh&oacute;a, dự c&aacute;c buổi hội thảo, huấn luyện...). Qu&aacute; nhiều điều bạn muốn l&agrave;m, nhưng quĩ thời gian chỉ c&oacute; hạn. Để giữ cho bản th&acirc;n kh&ocirc;ng bị rơi v&agrave;o trạng th&aacute;i mệt mỏi, giảm &yacute; ch&iacute;, ch&uacute;ng ta phải l&agrave;m g&igrave;?</p>\r\n', '<p>Đại Học Hay Học &ldquo;Đại&rdquo;</p>\r\n\r\n<p>Học đại học thế n&agrave;o cho hiệu quả? Rất nhiều t&acirc;n sinh vi&ecirc;n nghĩ họ chỉ cần &aacute;p dụng y nguy&ecirc;n c&aacute;ch học từ thời phổ th&ocirc;ng l&agrave; đ&atilde; đủ. Nhưng khối lượng kiến thức ở cấp đại học ho&agrave;n to&agrave;n vượt trội so với những g&igrave; bạn từng trải qua. Học đại học kh&ocirc;ng chỉ l&agrave; &ocirc;m s&aacute;ch vở đối ph&oacute; với c&aacute;c b&agrave;i kiểm tra, m&agrave; bạn c&ograve;n phải d&agrave;nh nhiều thời gian để tham gia c&aacute;c hoạt động ngo&agrave;i giờ học (tham gia c&acirc;u lạc bộ, c&aacute;c hoạt động ngoại kh&oacute;a, dự c&aacute;c buổi hội thảo, huấn luyện...). Qu&aacute; nhiều điều bạn muốn l&agrave;m, nhưng quĩ thời gian chỉ c&oacute; hạn. Để giữ cho bản th&acirc;n kh&ocirc;ng bị rơi v&agrave;o trạng th&aacute;i mệt mỏi, giảm &yacute; ch&iacute;, ch&uacute;ng ta phải l&agrave;m g&igrave;?</p>\r\n\r\n<p>T&aacute;c giả, chuy&ecirc;n gia gi&aacute;o dục người Đức Martin Krengel quan niệm: &quot;H&atilde;y học tập v&agrave; nghi&ecirc;n cứu hết m&igrave;nh nhưng vẫn kh&ocirc;ng qu&ecirc;n những cuộc vui!&quot;. &Ocirc;ng viết cuốn &ldquo;Đại học hay học &lsquo;đại&rsquo;&rdquo;, đ&uacute;c kết lại những trải nghiệm thực tế ch&acirc;n thực, để gợi &yacute; cho c&aacute;c bạn c&aacute;ch học tập hiệu quả v&agrave; đầy hứng th&uacute;. Xuy&ecirc;n suốt 10 chương s&aacute;ch, bạn sẽ nhận diện được c&aacute;c vấn đề quan trọng, x&aacute;c định r&otilde; những điều cần l&agrave;m, v&agrave; bắt tay v&agrave;o h&agrave;nh động. Đặc biệt, những b&iacute; k&iacute;p trong s&aacute;ch kh&ocirc;ng giới hạn trong lĩnh vực học tập, m&agrave; c&ograve;n l&agrave; &quot;kỹ năng sinh tồn&quot; để bạn thay đổi nếp sống, quản l&yacute; thời gian kh&ocirc;n kh&eacute;o hơn v&agrave; sắp xếp c&aacute;c hoạt động ngoại kh&oacute;a thật ph&ugrave; hợp. N&oacute;i t&oacute;m lại, đời sống đại học của bạn sẽ trở n&ecirc;n tươi mới v&agrave; đầy ắp niềm vui, kh&ocirc;ng c&oacute; chỗ cho sự nh&agrave;m ch&aacute;n v&agrave; những &aacute;p lực kh&ocirc;ng cần c&oacute; - với sự trợ gi&uacute;p thiết thực đến từ cuốn s&aacute;ch của Martin Krengel.</p>\r\n\r\n<p>H&atilde;y để cuốn cẩm nang &ldquo;Đại học hay học &lsquo;đại&rsquo;&rdquo; biến qu&atilde;ng thời gian tr&ecirc;n giảng đường đại học th&agrave;nh những kỷ niệm tuyệt vời kh&ocirc;ng thể qu&ecirc;n của bạn!&nbsp;</p>\r\n', 2017, 500, 460),
(43, 5, '1980 Books', 'NXB Thế Giới', 'Nhiều Tác Giả', 'Bìa mềm', 'null', '<p>&ldquo;Chiến lược của kẻ thắng&rdquo; tổng hợp 31 b&agrave;i học qu&acirc;n sự, đ&uacute;c kết từ lịch sử Đ&ocirc;ng t&acirc;y kim cổ trải d&agrave;i suốt ba ng&agrave;n năm. Những anh h&ugrave;ng mạnh nhất, lỗi lạc nhất lịch sử đều để lại cho hậu thế những b&agrave;i học để đời, từ Hannibal, Caesar, Gia C&aacute;t Lượng đến Napol&eacute;on, Oda Nobunaga, Toyotomi Hideyoshi, Tokugawa leyasu&hellip; Tất cả c&aacute;c kỹ năng tư duy, h&agrave;nh động của những vị tướng xuất sắc ấy đ&atilde; l&agrave;m thay đổi cả một giai đoạn lịch sử.</p>\r\n', '<p>&ldquo;Chiến lược của kẻ thắng&rdquo; tổng hợp 31 b&agrave;i học qu&acirc;n sự, đ&uacute;c kết từ lịch sử Đ&ocirc;ng t&acirc;y kim cổ trải d&agrave;i suốt ba ng&agrave;n năm. Những anh h&ugrave;ng mạnh nhất, lỗi lạc nhất lịch sử đều để lại cho hậu thế những b&agrave;i học để đời, từ Hannibal, Caesar, Gia C&aacute;t Lượng đến Napol&eacute;on, Oda Nobunaga, Toyotomi Hideyoshi, Tokugawa leyasu&hellip; Tất cả c&aacute;c kỹ năng tư duy, h&agrave;nh động của những vị tướng xuất sắc ấy đ&atilde; l&agrave;m thay đổi cả một giai đoạn lịch sử.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ thay đổi trường đoạn lịch sử, những chiến thắng, s&aacute;ch lược ấy c&ograve;n t&aacute;c động tới chiến lược quản trị kinh doanh ng&agrave;y nay. Chủ nghĩa to&agrave;n cầu đang len lỏi v&agrave;o từng doanh nghiệp, Microft, Google, Amazon, Toyota, Uniqlo, 7-Eleven&hellip; Họ kh&ocirc;ng đứng b&ecirc;n ngo&agrave;i cuộc cạnh tranh thương mại. Họ cần t&igrave;m cho m&igrave;nh ch&igrave;a kh&oacute;a để th&agrave;nh c&ocirc;ng v&agrave; đứng vững trong d&ograve;ng chảy xu thế đ&oacute;.</p>\r\n', 2017, 200, 56),
(44, 6, '1980 Books', 'NXB Lao Động', 'Suzuki Hiroki', 'Bìa mềm', 'Nhật Linh', '<p>&ldquo;Chiến lược của kẻ thắng&rdquo; tổng hợp 31 b&agrave;i học qu&acirc;n sự, đ&uacute;c kết từ lịch sử Đ&ocirc;ng t&acirc;y kim cổ trải d&agrave;i suốt ba ng&agrave;n năm. Những anh h&ugrave;ng mạnh nhất, lỗi lạc nhất lịch sử đều để lại cho hậu thế những b&agrave;i học để đời, từ Hannibal, Caesar, Gia C&aacute;t Lượng đến Napol&eacute;on, Oda Nobunaga, Toyotomi Hideyoshi, Tokugawa leyasu&hellip; Tất cả c&aacute;c kỹ năng tư duy, h&agrave;nh động của những vị tướng xuất sắc ấy đ&atilde; l&agrave;m thay đổi cả một giai đoạn lịch sử.</p>\r\n', '<p>&ldquo;Chiến lược của kẻ thắng&rdquo; tổng hợp 31 b&agrave;i học qu&acirc;n sự, đ&uacute;c kết từ lịch sử Đ&ocirc;ng t&acirc;y kim cổ trải d&agrave;i suốt ba ng&agrave;n năm. Những anh h&ugrave;ng mạnh nhất, lỗi lạc nhất lịch sử đều để lại cho hậu thế những b&agrave;i học để đời, từ Hannibal, Caesar, Gia C&aacute;t Lượng đến Napol&eacute;on, Oda Nobunaga, Toyotomi Hideyoshi, Tokugawa leyasu&hellip; Tất cả c&aacute;c kỹ năng tư duy, h&agrave;nh động của những vị tướng xuất sắc ấy đ&atilde; l&agrave;m thay đổi cả một giai đoạn lịch sử.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ thay đổi trường đoạn lịch sử, những chiến thắng, s&aacute;ch lược ấy c&ograve;n t&aacute;c động tới chiến lược quản trị kinh doanh ng&agrave;y nay. Chủ nghĩa to&agrave;n cầu đang len lỏi v&agrave;o từng doanh nghiệp, Microft, Google, Amazon, Toyota, Uniqlo, 7-Eleven&hellip; Họ kh&ocirc;ng đứng b&ecirc;n ngo&agrave;i cuộc cạnh tranh thương mại. Họ cần t&igrave;m cho m&igrave;nh ch&igrave;a kh&oacute;a để th&agrave;nh c&ocirc;ng v&agrave; đứng vững trong d&ograve;ng chảy xu thế đ&oacute;.</p>\r\n', 2019, 400, 376),
(45, 7, 'NXB Trẻ', 'NXB Trẻ', 'Nguyễn Đức Soát ', 'Bìa mềm', 'Không có', '<p>Cuốn s&aacute;ch l&agrave; những ghi ch&eacute;p của Trung tướng - Anh h&ugrave;ng lực lượng vũ trang Nguyễn Đức So&aacute;t suốt 7 năm 1966-1972<br />\n<br />\n&quot;từ một anh l&iacute;nh bắt đầu học l&aacute;i m&aacute;y bay MiG-21 cho đến khi trở th&agrave;nh một trong những phi c&ocirc;ng c&oacute; t&agrave;i xạ k&iacute;ch giỏi nhất của Kh&ocirc;ng qu&acirc;n Việt Nam, đ&atilde; bắn hạ 6 m&aacute;y bay Mỹ v&agrave; được phong tặng danh hiệu cao qu&yacute; Anh h&ugrave;ng Lực lượng Vũ trang Nh&acirc;n d&acirc;n khi mới 27 tuổi.<br />\n&nbsp;</p>\n', '<p>Chuyện của một người nhưng đọc l&ecirc;n sẽ thấy tinh thần, kh&iacute; ph&aacute;ch của một thế hệ thanh ni&ecirc;n Việt Nam trong cuộc chiến đấu khốc liệt, kh&ocirc;ng c&acirc;n sức với Kh&ocirc;ng qu&acirc;n Mỹ h&ugrave;ng mạnh; thấy được những chiến c&ocirc;ng oanh liệt cũng như tổn thất kh&ocirc;ng g&igrave; b&ugrave; đắp nổi của chiến tranh; thấy được cuộc sống, t&igrave;nh bạn, t&igrave;nh y&ecirc;u, t&igrave;nh đồng ch&iacute; thật đẹp &hellip;<br />\n<br />\nTrong cuốn s&aacute;ch n&agrave;y kh&ocirc;ng chỉ thuần những d&ograve;ng nhật k&yacute; của t&aacute;c giả. Để bạn đọc tiếp cận văn bản thuận tiện hơn, t&aacute;c giả đ&atilde; viết th&ecirc;m những dẫn nhập bối cảnh sự kiện, tương quan lực lượng ta - địch, số phận những nh&acirc;n vật ch&iacute;nh trong nhật k&yacute;, những hồi ức v&agrave; những chi&ecirc;m nghiệm khi đ&atilde; c&oacute; độ l&ugrave;i lịch sử&hellip; - &aacute;nh s&aacute;ng của chủ nghĩa anh h&ugrave;ng c&aacute;ch mạng trong trắng, tận hiến tự - nhi&ecirc;n - &yacute; - thức cho Tổ quốc, nh&acirc;n d&acirc;n, Đảng v&agrave; qu&acirc;n đội, vượt l&ecirc;n mọi thử th&aacute;ch khắc nghiệt, mọi khổ đau v&agrave; mất m&aacute;t để đi đến ng&agrave;y chiến thắng.&quot; (Hữu Việt)</p>\n\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</p>\n', 2018, 353, 440),
(46, 8, 'Phanbook', 'NXB Đà Nẵng', 'Edward Snowden', 'Bìa mềm', 'Không có', '<p>Bị Theo D&otilde;i - B&iacute; Mật An Ninh Mạng</p>\n\n<p><strong><em>&ldquo;Những g&igrave; ta quan t&acirc;m, c&aacute;c hoạt động ta l&agrave;m, những địa điểm ta đến, bao điều ta mong muốn - mọi điều về bản th&acirc;n m&agrave; ta đ&atilde; tiết lộ, d&ugrave; c&oacute; &yacute; thức hay kh&ocirc;ng, đều đang bị l&eacute;n l&uacute;t theo d&otilde;i&hellip;&rdquo; &ndash; Edward Snowden.</em></strong></p>\n', '<p>Bị Theo D&otilde;i - B&iacute; Mật An Ninh Mạng</p>\n\n<p><strong><em>&ldquo;Những g&igrave; ta quan t&acirc;m, c&aacute;c hoạt động ta l&agrave;m, những địa điểm ta đến, bao điều ta mong muốn - mọi điều về bản th&acirc;n m&agrave; ta đ&atilde; tiết lộ, d&ugrave; c&oacute; &yacute; thức hay kh&ocirc;ng, đều đang bị l&eacute;n l&uacute;t theo d&otilde;i&hellip;&rdquo; &ndash; Edward Snowden.</em></strong></p>\n\n<p>PHANBOOK &amp; NXB Đ&agrave; Nẵng tr&acirc;n trọng giới thiệu!</p>\n\n<p><strong>VỀ T&Aacute;C PHẨM</strong></p>\n\n<p><strong><em>Bị theo d&otilde;i&nbsp;</em></strong><strong><em>&ndash;</em></strong><strong><em>&nbsp;B&iacute; mật an ninh mạng</em></strong>(tựa gốc:&nbsp;<em>Permanent Record</em>) l&agrave; tự truyện của Edward Snowden. Cuốn s&aacute;ch m&ocirc; tả lại thời thơ ấu của Snowden v&agrave; thời gian anh l&agrave;m việc tại Cơ quan T&igrave;nh b&aacute;o Trung ương (CIA), Cơ quan An ninh Quốc gia (NSA) cũng như những nguy&ecirc;n tắc tinh thần v&agrave; đạo đức đ&atilde; khiến anh tiết lộ hoạt động gi&aacute;m s&aacute;t h&agrave;ng loạt của c&aacute;c cơ quan n&agrave;y.</p>\n\n<p>Ở tuổi 29, anh trở th&agrave;nh nh&acirc;n vật g&acirc;y ch&uacute; &yacute; nhất của giới truyền th&ocirc;ng trong năm 2013 khi những tiết lộ của anh đ&atilde; tạo n&ecirc;n cuộc tranh luận tr&ecirc;n quy m&ocirc; to&agrave;n cầu về hoạt động gi&aacute;m s&aacute;t. Cuốn hồi k&yacute; n&agrave;y được ra đời sau s&aacute;u năm anh lưu vong tại Nga v&igrave; bị buộc tội trộm cắp t&agrave;i sản quốc gia.</p>\n\n<p>Với c&aacute;ch tường thuật d&iacute; dỏm, duy&ecirc;n d&aacute;ng, đầy hấp dẫn, Snowden nhấn mạnh &ldquo;quyền tự do&rdquo; v&agrave; &ldquo;quyền ri&ecirc;ng tư&rdquo; của con người trong thời đại kỹ thuật số. Sự thật l&agrave; tất cả ch&uacute;ng ta đều đang bị theo d&otilde;i theo một c&aacute;ch n&agrave;o đ&oacute; chỉ bằng những c&uacute; nhấp chuột tr&ecirc;n Internet.</p>\n\n<p><strong><em>Bị theo d&otilde;i</em></strong>&nbsp;sẽ c&oacute; những tiết lộ r&uacute;ng động về b&iacute; mật của c&aacute;c hoạt động do th&aacute;m n&agrave;y v&agrave; chắc hẳn sẽ khiến ch&uacute;ng ta lưu t&acirc;m hơn về quyền tự do c&aacute; nh&acirc;n trong kh&ocirc;ng gian mạng.</p>\n\n<p>Trong v&ograve;ng chưa đầy một th&aacute;ng ph&aacute;t h&agrave;nh, cuốn s&aacute;ch nhanh ch&oacute;ng trở th&agrave;nh một trong những tựa s&aacute;ch b&aacute;n chạy nhất của New York Times v&agrave; In &amp; E-Book Nonfiction.</p>\n\n<p><strong><em>Bị theo d&otilde;i</em></strong>&nbsp;dự kiến ph&aacute;t h&agrave;nh v&agrave;o trung tuần th&aacute;ng 05/2020.</p>\n\n<p><strong>VỀ T&Aacute;C GIẢ</strong></p>\n\n<p><strong>EDWARD SNOWDEN</strong></p>\n\n<p>Sinh tại th&agrave;nh phố Elizabeth, Bắc Caroline. Anh l&agrave; một kỹ sư hệ thống m&aacute;y t&iacute;nh l&agrave;nh nghề, từng l&agrave;m sĩ quan của Cơ quan T&igrave;nh b&aacute;o Trung ương (CIA) v&agrave; l&agrave;m nh&agrave; thầu cho Cơ quan An ninh Quốc gia Hoa Kỳ (NSA). Snowden từng được gợi cảm hứng cho rất nhiều bộ phim nổi tiếng:&nbsp;<em>Snowden</em>&nbsp;(2016),&nbsp;<em>Verax</em>&nbsp;(2013),&nbsp;<em>Citizenfour</em>&nbsp;(2014).</p>\n\n<p>Anh đ&atilde; nhận được nhiều giải thưởng về dịch vụ c&ocirc;ng , bao gồm giải Right Livelihood, giải &quot;Người thổi c&ograve;i&quot; của Đức, giải Ridenhour cho C&acirc;u chuyện Sự thật v&agrave; huy chương Carl von Ossietzky từ Li&ecirc;n đo&agrave;n Nh&acirc;n quyền Quốc tế.</p>\n\n<p>Hiện anh l&agrave; chủ tịch hội đồng quản trị của Tổ chức Tự do B&aacute;o ch&iacute;.</p>\n', 2020, 380, 373),
(47, 9, 'Phụ Nữ', 'NXB Phụ Nữ Việt Nam', 'Đào Hải Ninh', 'Bìa mềm', 'Không có', '<p>Bộ s&aacute;ch 2 cuốn&nbsp;<strong><em>Con về</em></strong>&nbsp;v&agrave;&nbsp;<strong><em>Con về kh&ocirc;ng phải ph&eacute;p m&agrave;u</em></strong>&nbsp;l&agrave; những cuốn s&aacute;ch được ghi lại&nbsp; từ trải nghiệm của ch&iacute;nh người trong cuộc l&agrave; cha mẹ c&oacute; con tự kỷ &ndash; Đ&agrave;o Hải Ninh v&agrave; những người bạn đồng h&agrave;nh.</p>\n', '<p>Bộ s&aacute;ch 2 cuốn&nbsp;<strong><em>Con về</em></strong>&nbsp;v&agrave;&nbsp;<strong><em>Con về kh&ocirc;ng phải ph&eacute;p m&agrave;u</em></strong>&nbsp;l&agrave; những cuốn s&aacute;ch được ghi lại&nbsp; từ trải nghiệm của ch&iacute;nh người trong cuộc l&agrave; cha mẹ c&oacute; con tự kỷ &ndash; Đ&agrave;o Hải Ninh v&agrave; những người bạn đồng h&agrave;nh.</p>\n\n<p>15 năm trước, chị Đ&agrave;o Hải Ninh l&agrave; một người phụ nữ của c&ocirc;ng việc đầy năng động, chị l&agrave;m trong lĩnh vực t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng v&agrave; c&oacute; vị tr&iacute; nhiều người mơ ước. Chị lu&ocirc;n l&agrave; người người tự tin, chủ động lập kế hoạch cho nhưng bước tiến của cuộc đời m&igrave;nh. Ngay cả việc sinh con chị cũng l&ecirc;n kế hoạch kĩ c&agrave;ng v&agrave; cầu to&agrave;n với kết quả như mong đợi, một gia đ&igrave;nh th&agrave;nh đạt vợ chồng đều c&oacute; địa vị trong x&atilde; hội với hai đứa con đủ nếp đủ tẻ. Cuộc sống của chị cứ thuận lợi như vậy cho đến khi c&ocirc; con g&aacute;i thứ 2 của chị sau những tiếng bi b&ocirc; đầu đời rồi rơi v&agrave;o &ldquo;c&acirc;m&rdquo; hẳn - con kh&ocirc;ng thể ph&aacute;t ra &acirc;m thanh v&agrave; thường xuy&ecirc;n c&oacute; những biểu hiện của trẻ tăng động. Chị vẫn cho đ&oacute; l&agrave; b&igrave;nh thường, con chị c&oacute; thể chỉ chậm n&oacute;i m&agrave; th&ocirc;i, cho đến khi một người bạn l&agrave; b&aacute;c sĩ đến nh&agrave; chơi d&egrave; dặt n&oacute;i với chị v&igrave; sự bất b&igrave;nh thường của con m&igrave;nh, chị kh&ocirc;ng tin con m&igrave;nh kh&ocirc;ng b&igrave;nh thường. Đến tận l&uacute;c cầm kết quả kiểm tra của con g&aacute;i với chẩn đo&aacute;n của b&aacute;c sĩ, con chị l&agrave; trẻ tự kỷ với điểm 41/50 (theo thang điểm CARS) v&agrave; được x&aacute;c định l&agrave; &ldquo;kh&ocirc;ng thể phục hồi&rdquo;, chị mới cay đắng nhận ra rằng con m&igrave;nh mắc &ldquo;bệnh&rdquo; kh&ocirc;ng giống ai, &ldquo;bệnh&rdquo; kh&ocirc;ng thuốc chữa &ldquo;bệnh&rdquo; kh&ocirc;ng bao giờ khỏi.</p>\n', 2020, 150, 176),
(48, 10, 'Skybooks', 'NXB Phụ Nữ Việt Nam', 'Khotudien', 'Bìa mềm', 'Không có', '<p>TỪ ĐIỂN TIẾNG &ldquo;EM&rdquo; &ndash; Định nghĩa về thế giới mới!</p>\n\n<p>Bạn sẽ bất ngờ, khi cầm cuốn &ldquo;từ điển&rdquo; xinh xinh n&agrave;y tr&ecirc;n tay.</p>\n\n<p>V&agrave; sẽ c&ograve;n ngạc nhi&ecirc;n hơn nữa, khi bắt đầu đọc từng trang s&aacute;ch&hellip;</p>\n\n<p>Dĩ nhi&ecirc;n l&agrave; v&igrave; &ldquo;Từ điển tiếng &ldquo;Em&rdquo; kh&ocirc;ng phải l&agrave; một cuốn từ điển th&ocirc;ng thường rồi!</p>\n\n<p>N&oacute;i đến &ldquo;từ điển&rdquo;, xưa nay ch&uacute;ng ta đều nghĩ về một bộ s&aacute;ch đồ sộ, giải th&iacute;ch &yacute; nghĩa, c&aacute;ch d&ugrave;ng, dịch, c&aacute;ch ph&aacute;t &acirc;m, v&agrave; thường k&egrave;m theo c&aacute;c v&iacute; dụ về c&aacute;ch sử dụng từ đ&oacute;.</p>\n', '<p>TỪ ĐIỂN TIẾNG &ldquo;EM&rdquo; &ndash; Định nghĩa về thế giới mới!</p>\n\n<p>Bạn sẽ bất ngờ, khi cầm cuốn &ldquo;từ điển&rdquo; xinh xinh n&agrave;y tr&ecirc;n tay.</p>\n\n<p>V&agrave; sẽ c&ograve;n ngạc nhi&ecirc;n hơn nữa, khi bắt đầu đọc từng trang s&aacute;ch&hellip;</p>\n\n<p>Dĩ nhi&ecirc;n l&agrave; v&igrave; &ldquo;Từ điển tiếng &ldquo;Em&rdquo; kh&ocirc;ng phải l&agrave; một cuốn từ điển th&ocirc;ng thường rồi!</p>\n\n<p>N&oacute;i đến &ldquo;từ điển&rdquo;, xưa nay ch&uacute;ng ta đều nghĩ về một bộ s&aacute;ch đồ sộ, giải th&iacute;ch &yacute; nghĩa, c&aacute;ch d&ugrave;ng, dịch, c&aacute;ch ph&aacute;t &acirc;m, v&agrave; thường k&egrave;m theo c&aacute;c v&iacute; dụ về c&aacute;ch sử dụng từ đ&oacute;.</p>\n\n<p>Tuy nhi&ecirc;n, với cuốn s&aacute;ch &ldquo;Từ điển tiếng &ldquo;em&rdquo;, c&aacute;c bạn sẽ hết sức bất ngờ với những định nghĩa mới, bắt trend, s&aacute;ng tạo, th&ocirc;ng minh v&agrave; v&ocirc; c&ugrave;ng h&agrave;i hước.</p>\n\n<p><strong>Tiếng &ldquo;em&rdquo;</strong>&nbsp;<em>[danh từ]</em>&nbsp;ở đ&acirc;y l&agrave; tiếng l&ograve;ng của những người y&ecirc;u sự thật, gh&eacute;t sự giả dối</p>\n\n<p><strong>C&ocirc; đơn</strong>&nbsp;<em>[ t&iacute;nh từ ]&nbsp;</em>kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute; ai b&ecirc;n cạnh, m&agrave; l&agrave; người m&igrave;nh muốn ở cạnh lại kh&ocirc;ng hề ở b&ecirc;n</p>\n\n<p><strong>Sống lỗi</strong>&nbsp;<em>[ động từ ]&nbsp;</em>l&agrave; c&aacute;ch sống của mấy bạn c&oacute; người y&ecirc;u c&aacute;i l&agrave; bỏ b&ecirc; bạn b&egrave; liền h&agrave;...</p>\n\n<p>Nhưng đ&acirc;y kh&ocirc;ng chỉ l&agrave; cuốn s&aacute;ch chỉ biết n&oacute;i dăm ba c&acirc;u chuyện về t&igrave;nh y&ecirc;u.<br />\nC&ograve;n nhiều hơn thế!</p>\n\n<p>L&agrave; những c&acirc;u cửa miệng của giới trẻ thời nay; l&agrave; hoạt động tưởng v&ocirc; bổ nhưng cần thiết cho sự sống: ăn, ngủ, tắm, gội cũng được định nghĩa hết sức d&iacute; dỏm... V&agrave; cũng kh&ocirc;ng thiếu những &ldquo;tật xấu, th&oacute;i hư&rdquo; nghĩ đến đ&atilde; thấy &ldquo;tức c&aacute;i lồng ngực&rdquo;...</p>\n\n<p>V&agrave; bạn y&ecirc;n t&acirc;m, &ldquo;tập đo&agrave;n&rdquo; Kho Từ Điển điều h&agrave;nh bởi Thịt Kho &ndash; Hiệp Thị - 2 chủ xị cho ra đời cuốn s&aacute;ch nhỏ b&eacute; x&iacute;u xiu nhưng mới mẻ v&agrave; mặn m&agrave; v&ocirc; c&ugrave;ng n&agrave;y sẽ khiến bạn tho&aacute;t m&aacute;c &ldquo;người tối cổ&rdquo; cười cả ng&agrave;y kh&ocirc;ng ch&aacute;n, nh&igrave;n bạn b&egrave; quanh m&igrave;nh bằng &aacute;nh mắt dễ thương, tận hưởng cuộc đời với những định nghĩa ho&agrave;n to&agrave;n!!!</p>\n\n<p>Cuốn s&aacute;ch n&agrave;y giống như một chiếc hộp Pandora th&uacute; vị v&agrave; hấp dẫn người đọc, v&igrave; bạn kh&ocirc;ng thể đo&aacute;n trước được t&aacute;c giả sẽ &ldquo;định nghĩa&rdquo; c&acirc;u n&oacute;i đ&oacute; theo nghĩa n&agrave;o, cho ta th&ecirc;m th&iacute;ch th&uacute; với những ng&ocirc;n từ đ&aacute;ng y&ecirc;u sử dụng mỗi ng&agrave;y.<br />\nVậy n&ecirc;n, ngay b&acirc;y giờ, bạn đ&atilde; sẵn s&agrave;ng để mở ra những điều th&uacute; vị trong cuốn s&aacute;ch n&agrave;y chưa!!!</p>\n', 2020, 300, 280),
(49, 11, 'NXB Trẻ', 'NXB Trẻ', 'Nguyễn Nhật Ánh', 'Bìa mềm', 'Không có', '<p>Con Chim Xanh Biếc Bay Về</p>\n\n<p>Kh&ocirc;ng giống như những t&aacute;c phẩm trước đ&acirc;y lấy bối cảnh v&ugrave;ng qu&ecirc; miền Trung đầy ắp những ho&agrave;i niệm tuổi thơ dung dị, trong trẻo với c&aacute;c nh&acirc;n vật ở độ tuổi dậy th&igrave;, trong quyển s&aacute;ch mới lần n&agrave;y nh&agrave; văn Nguyễn Nhật &Aacute;nh lấy bối cảnh ch&iacute;nh l&agrave; S&agrave;i G&ograve;n &ndash; Th&agrave;nh phố Hồ Ch&iacute; Minh nơi t&aacute;c giả sinh sống (như l&agrave; một sự đền đ&aacute;p &acirc;n t&igrave;nh với mảnh đất miền Nam). C&aacute;c nh&acirc;n vật ch&iacute;nh trong truyện cũng &ldquo;lớn&rdquo; hơn, với những c&acirc;u chuyện mưu sinh lập nghiệp lắm gian nan thử th&aacute;ch của c&aacute;c sinh vi&ecirc;n trẻ đầy ho&agrave;i b&atilde;o. Tất nhi&ecirc;n kh&ocirc;ng thể thiếu những c&acirc;u chuyện t&igrave;nh cảm động, kịch t&iacute;nh v&agrave; bất ngờ khiến bạn đọc ngẩn ngơ, cười ra nước mắt. V&agrave; như trong mọi t&aacute;c phẩm Nguyễn Nhật &Aacute;nh, sự tử tế v&agrave; tinh thần hướng thượng vẫn l&agrave; điểm nhấn quan trọng trong quyển s&aacute;ch mới n&agrave;y.</p>\n', '<p>Con Chim Xanh Biếc Bay Về</p>\n\n<p>Kh&ocirc;ng giống như những t&aacute;c phẩm trước đ&acirc;y lấy bối cảnh v&ugrave;ng qu&ecirc; miền Trung đầy ắp những ho&agrave;i niệm tuổi thơ dung dị, trong trẻo với c&aacute;c nh&acirc;n vật ở độ tuổi dậy th&igrave;, trong quyển s&aacute;ch mới lần n&agrave;y nh&agrave; văn Nguyễn Nhật &Aacute;nh lấy bối cảnh ch&iacute;nh l&agrave; S&agrave;i G&ograve;n &ndash; Th&agrave;nh phố Hồ Ch&iacute; Minh nơi t&aacute;c giả sinh sống (như l&agrave; một sự đền đ&aacute;p &acirc;n t&igrave;nh với mảnh đất miền Nam). C&aacute;c nh&acirc;n vật ch&iacute;nh trong truyện cũng &ldquo;lớn&rdquo; hơn, với những c&acirc;u chuyện mưu sinh lập nghiệp lắm gian nan thử th&aacute;ch của c&aacute;c sinh vi&ecirc;n trẻ đầy ho&agrave;i b&atilde;o. Tất nhi&ecirc;n kh&ocirc;ng thể thiếu những c&acirc;u chuyện t&igrave;nh cảm động, kịch t&iacute;nh v&agrave; bất ngờ khiến bạn đọc ngẩn ngơ, cười ra nước mắt. V&agrave; như trong mọi t&aacute;c phẩm Nguyễn Nhật &Aacute;nh, sự tử tế v&agrave; tinh thần hướng thượng vẫn l&agrave; điểm nhấn quan trọng trong quyển s&aacute;ch mới n&agrave;y.</p>\n\n<p>Như một cuốn phim &ldquo;trinh th&aacute;m t&igrave;nh y&ecirc;u&rdquo;,&nbsp;<strong>Con chim xanh biếc bay về</strong>&nbsp;dẫn bạn đi hết từ bất ngờ n&agrave;y đến t&ograve; m&ograve; suy đo&aacute;n kh&aacute;c, để kết th&uacute;c bằng một nỗi h&acirc;n hoan v&ocirc; bờ sau bao phen hồi hộp nghi kỵ đến kh&oacute; thở.</p>\n\n<p>Bạn sẽ theo phe sinh vi&ecirc;n-nh&acirc;n vi&ecirc;n với những c&acirc;u thơ dịu d&agrave;ng v&agrave; đ&aacute;o để, hay phe &ocirc;ng chủ với những kỹ năng kinh doanh khởi nghiệp? V&agrave; h&atilde;y đo&aacute;n thử, điều g&igrave; khiến bạn c&oacute; thể cảm động đến rưng rưng trong cuộc sống giữa S&agrave;i G&ograve;n bộn bề?</p>\n\n<p>L&acirc;u lắm mới c&oacute; h&igrave;nh ảnh th&agrave;nh phố rộn r&agrave;ng trong t&aacute;c phẩm của Nguyễn Nhật &Aacute;nh - điều hấp dẫn kh&aacute;c thường của&nbsp;<strong>Con chim xanh biếc bay về</strong>.</p>\n\n<p>Ch&iacute;nh v&igrave; thế m&agrave; cuốn s&aacute;ch chỉ c&oacute; một c&aacute;ch đọc th&ocirc;i: một mạch từ đầu đến cuối!</p>\n', 2020, 400, 396),
(50, 12, '1980 Books', 'NXB Lao Động', 'Mackenzie Kyle', 'Bìa mềm', 'Trương Thị Quế Anh', '<p><strong>Thuật Đắc Nh&acirc;n T&acirc;m N&acirc;ng Tầm Hiệu Suất</strong></p>\n\n<p>Chưa bao giờ, thuật đắc nh&acirc;n t&acirc;m, hay b&iacute; quyết thấu hiểu l&ograve;ng người để tạo động lực cho từng c&aacute; nh&acirc;n, khai mở tiềm năng đội nh&oacute;m v&agrave; tăng cường sức mạnh kết nối trong tổ chức lại cấp thiết đến thế. Để được l&ograve;ng thi&ecirc;n hạ với tư c&aacute;ch doanh nghiệp l&agrave; b&agrave;i to&aacute;n h&oacute;c b&uacute;a đối với mọi doanh nghiệp.</p>\n', '<p><strong>Thuật Đắc Nh&acirc;n T&acirc;m N&acirc;ng Tầm Hiệu Suất</strong></p>\n\n<p>Chưa bao giờ, thuật đắc nh&acirc;n t&acirc;m, hay b&iacute; quyết thấu hiểu l&ograve;ng người để tạo động lực cho từng c&aacute; nh&acirc;n, khai mở tiềm năng đội nh&oacute;m v&agrave; tăng cường sức mạnh kết nối trong tổ chức lại cấp thiết đến thế. Để được l&ograve;ng thi&ecirc;n hạ với tư c&aacute;ch doanh nghiệp l&agrave; b&agrave;i to&aacute;n h&oacute;c b&uacute;a đối với mọi doanh nghiệp.</p>\n\n<p>Với b&uacute;t ph&aacute;p t&agrave;i hoa, Mackenzie Kyle đ&atilde; lồng gh&eacute;p những nguy&ecirc;n l&yacute; thu phục l&ograve;ng người nhằm n&acirc;ng cao hiệu suất v&agrave; cải thiện chất lượng hoạt động doanh nghiệp vốn kh&ocirc; khan, cứng nhắc v&agrave;o cuốn &ldquo;tiểu thuyết kinh doanh&rdquo; c&oacute; một kh&ocirc;ng hai mang t&ecirc;n:&nbsp;<strong>&ldquo;Thuật đắc nh&acirc;n t&acirc;m n&acirc;ng tầm hiệu suất&rdquo;</strong>.</p>\n\n<p>Qua h&agrave;ng loạt c&acirc;u chuyện mang t&iacute;nh c&aacute; nh&acirc;n gần gũi, t&aacute;c phẩm đưa ra nguy&ecirc;n l&yacute; đắc nh&acirc;n t&acirc;m căn bản gi&uacute;p thấu hiểu v&agrave; khai mở động lực s&acirc;u k&iacute;n trong mỗi con người. Mackenzie Kyle chứng minh tầm quan trọng của việc li&ecirc;n kết động lực đối với th&agrave;nh c&ocirc;ng trong kinh doanh v&agrave; cuộc sống.</p>\n\n<p>Sở hữu&nbsp;<strong>&quot;Thuật đắc nh&acirc;n t&acirc;m n&acirc;ng tầm hiệu suất&quot;</strong>, v&agrave; bạn sẽ c&oacute; c&acirc;u trả lời cho b&agrave;i to&aacute;n: L&agrave;m thế n&agrave;o để săn t&igrave;m động lực v&agrave; th&uacute;c đẩy h&agrave;nh vi trong thời đại 4.0 đầy biến động?</p>\n', 2018, 350, 324),
(51, 13, 'Skybooks', 'NXB Phụ Nữ Việt Nam', 'Kulzsc', 'Bìa mềm', 'Không có', '<p>T&ocirc; B&igrave;nh Y&ecirc;n Vẽ Hạnh Ph&uacute;c</p>\n\n<p>Sau th&agrave;nh c&ocirc;ng của cuốn s&aacute;ch đầu tay &ldquo;Phải l&ograve;ng với c&ocirc; đơn&rdquo; ch&agrave;ng họa sĩ nổi tiếng v&agrave; t&agrave;i năng Kulzsc đ&atilde; trở lại với một cuốn s&aacute;ch v&ocirc; c&ugrave;ng đặc biệt mang t&ecirc;n: &quot;T&ocirc; b&igrave;nh y&ecirc;n - vẽ hạnh ph&uacute;c&rdquo; &ndash;&nbsp; sắc n&eacute;t phong c&aacute;ch c&aacute; nh&acirc;n với một ch&uacute;t &ldquo;thơ thẩn, rất hiền&rdquo;.</p>\n', '<p>T&ocirc; B&igrave;nh Y&ecirc;n Vẽ Hạnh Ph&uacute;c</p>\n\n<p>Sau th&agrave;nh c&ocirc;ng của cuốn s&aacute;ch đầu tay &ldquo;Phải l&ograve;ng với c&ocirc; đơn&rdquo; ch&agrave;ng họa sĩ nổi tiếng v&agrave; t&agrave;i năng Kulzsc đ&atilde; trở lại với một cuốn s&aacute;ch v&ocirc; c&ugrave;ng đặc biệt mang t&ecirc;n: &quot;T&ocirc; b&igrave;nh y&ecirc;n - vẽ hạnh ph&uacute;c&rdquo; &ndash;&nbsp; sắc n&eacute;t phong c&aacute;ch c&aacute; nh&acirc;n với một ch&uacute;t &ldquo;thơ thẩn, rất hiền&rdquo;.</p>\n\n<p>Kh&ocirc;ng giống với những cuốn s&aacute;ch chỉ để đọc, &ldquo;T&ocirc; b&igrave;nh y&ecirc;n &ndash; vẽ hạnh ph&uacute;c&rdquo; &nbsp;l&agrave; một cuốn s&aacute;ch m&agrave; độc giả vừa t&igrave;m được &ldquo;Hạnh ph&uacute;c to to từ những điều nho nhỏ&rdquo; vừa được thực h&agrave;nh ngay lập tức. Một sự kết hợp mới lạ đầy th&uacute; vị giữa thể loại s&aacute;ch tản văn v&agrave; s&aacute;ch t&ocirc; m&agrave;u.</p>\n\n<p>Lật mở cuốn s&aacute;ch n&agrave;y, bạn sẽ thấy v&ocirc; v&agrave;n những điều nhỏ b&eacute; dễ thương lan t&ograve;a nguồn năng lượng t&iacute;ch cực. V&agrave; k&egrave;m theo một list những điều tuyệt vời khiến bạn kh&ocirc;ng thể bỏ lỡ:</p>\n\n<p>- Tận t&igrave;nh chỉ dẫn: 8 hướng dẫn t&ocirc; m&agrave;u đầy hứng th&uacute; từ Kulzsc<br />\n- Tranh vẽ đơn giản, gần gũi. N&eacute;t vẽ đ&aacute;ng y&ecirc;u, dễ thương<br />\n- Chủ đề xoay quanh những điều b&igrave;nh y&ecirc;n trong cuộc sống: Đọc s&aacute;ch, đi dạo, dọn dẹp nh&agrave; cửa, trồng c&acirc;y, ăn cơm mẹ nấu, nghe một bản nhạc hay, v&agrave; nghĩ về nụ cười của một ai đ&oacute;&hellip;<br />\n- Mỗi bức tranh lại l&agrave; những lời thủ thỉ, t&acirc;m t&igrave;nh của t&aacute;c giả. L&agrave; những điều nhắn gửi nho nhỏ mong bạn bớt đi những xao động:</p>\n\n<p>&ldquo;Em chọn hạnh ph&uacute;c<br />\nEm chọn b&igrave;nh y&ecirc;n<br />\nEm chọn b&ecirc;n cạnh<br />\nNhững điều an y&ecirc;n&rdquo;</p>\n\n<p>Hay đơn giản l&agrave; những gi&atilde;i b&agrave;y ngắn gọn, thay nỗi l&ograve;ng của một ai đ&oacute;: &quot;Tớ biết cậu một m&igrave;nh vẫn ổn, nhưng c&oacute; những chuyện, c&oacute; ai đ&oacute; c&ugrave;ng l&agrave;m, vẫn hơn.&quot;</p>\n\n<p><br />\nTh&ocirc;ng qua những h&igrave;nh vẽ đang đợi được lấp đầy bằng mu&ocirc;n v&agrave;n sắc m&agrave;u ấy, Kulzsc sẽ gi&uacute;p bạn - những người lớn c&ocirc; đơn, những ai đang cần bổ sung vitamin hạnh ph&uacute;c &ldquo;truy t&igrave;m&rdquo; những niềm vui bị bỏ qu&ecirc;n hay tuyệt chi&ecirc;u để đối ph&oacute; với stress.</p>\n\n<p>Bởi t&ocirc; m&agrave;u l&agrave; một h&igrave;nh thức chữa l&agrave;nh đơn giản m&agrave; hiệu quả, n&ecirc;n mỗi khi thấy bực dọc, ch&aacute;n nản, hay khiến m&igrave;nh bận rộn hơn h&atilde;y thử t&igrave;m tới &ldquo;T&ocirc; b&igrave;nh y&ecirc;n - &nbsp;vẽ hạnh ph&uacute;c&rdquo; c&ugrave;ng hộp m&agrave;u đ&atilde; cất l&acirc;u trong tủ v&agrave;... Sao kh&ocirc;ng thể l&agrave; một đ&aacute;m m&acirc;y m&agrave;u t&iacute;m, một m&aacute;i t&oacute;c v&agrave;ng tươi hay một nụ hoa m&agrave;u xanh biển nhỉ? Kh&ocirc;ng cần phải đắn đo đ&acirc;u, bạn cứ thoải m&aacute;i x&oacute;a đi căng thẳng, x&oacute;a đi những gam m&agrave;u u &aacute;m, tự tay m&igrave;nh điểm t&ocirc; những m&agrave;u sắc tươi s&aacute;ng lấp l&aacute;nh, đầy ắp hy vọng theo &yacute; th&iacute;ch, tận hưởng những ph&uacute;t thư gi&atilde;n thật b&igrave;nh y&ecirc;n kh&ocirc;ng muộn phiền th&ocirc;i n&agrave;o.</p>\n', 2020, 120, 96),
(52, 14, 'Skybooks', 'Nhà Xuất Bản Hà Nội', 'Hiên', 'Bìa mềm', 'Không có', '<p>Xin ch&agrave;o t&igrave;nh y&ecirc;u l&agrave; tổng hợp những c&acirc;u chuyện nhỏ về t&igrave;nh y&ecirc;u giản dị như vậy, từng trang s&aacute;ch Hi&ecirc;n viết giống như một tri kỷ, c&ugrave;ng bạn trải l&ograve;ng ch&acirc;n thật v&agrave; t&acirc;m sự tất cả c&aacute;c trạng th&aacute;i cảm x&uacute;c của một chữ &ldquo;Y&ecirc;u&rdquo;.</p>\n', '<p>&ldquo;Rau trong rừng<br />\nnh&agrave; tr&ecirc;n đồi<br />\nngười trong mộng<br />\nan trong l&ograve;ng.&rdquo;<br />\n<br />\nXin ch&agrave;o t&igrave;nh y&ecirc;u l&agrave; tổng hợp những c&acirc;u chuyện nhỏ về t&igrave;nh y&ecirc;u giản dị như vậy, từng trang s&aacute;ch Hi&ecirc;n viết giống như một tri kỷ, c&ugrave;ng bạn trải l&ograve;ng ch&acirc;n thật v&agrave; t&acirc;m sự tất cả c&aacute;c trạng th&aacute;i cảm x&uacute;c của một chữ &ldquo;Y&ecirc;u&rdquo;.</p>\n\n<p>Trong &quot;Xin ch&agrave;o t&igrave;nh y&ecirc;u&quot;, t&igrave;nh y&ecirc;u hiện hữu trong những c&acirc;u thơ, những d&ograve;ng tản văn ngọt ng&agrave;o, l&agrave; những mơ mộng về ng&ocirc;i nh&agrave; hai người b&igrave;nh an.</p>\n\n<p>T&igrave;nh y&ecirc;u l&agrave; lời nhắn đầy t&igrave;nh cảm của ch&agrave;ng trai, c&ocirc; g&aacute;i gửi cho người m&igrave;nh thương, đ&ocirc;i khi chứa cả những lời tr&aacute;ch m&oacute;c, giận hờn.<br />\nNhưng t&igrave;nh y&ecirc;u cũng c&oacute; cả những khi rất đau l&ograve;ng, Hi&ecirc;n viết l&ecirc;n những lời để ngỏ, n&oacute;i hộ nỗi l&ograve;ng khi ai đ&oacute; phải chịu đựng nỗi đau chia tay, hay những khi trời trở gi&oacute;, l&ograve;ng nhớ về người cũ.<br />\n<br />\n&ldquo;Thế nhưng m&agrave; buồn thế<br />\nTh&igrave; y&ecirc;u để l&agrave;m g&igrave;<br />\nY&ecirc;u th&igrave; phải hạnh ph&uacute;c<br />\nCan cớ g&igrave; phải đau?&rdquo;<br />\n<br />\nNhẹ nh&agrave;ng, l&atilde;ng mạn, nhưng cũng rất day dứt. Cuốn s&aacute;ch n&agrave;y, h&atilde;y đọc n&oacute; v&agrave;o những ng&agrave;y bạn thấy l&ograve;ng m&igrave;nh ch&ecirc;nh v&ecirc;nh, kh&ocirc;ng t&igrave;m nổi đ&iacute;ch đến, cũng chẳng đủ can đảm để quay đầu. C&oacute; lẽ n&oacute; kh&ocirc;ng thể chữa l&agrave;nh ngay lập tức tr&aacute;i tim đang tổn thương của bạn v&agrave;o những ng&agrave;y gi&ocirc;ng gi&oacute; như vậy, nhưng tin rằng bạn sẽ t&igrave;m thấy được sự đồng cảm ở từng c&acirc;u chữ, rồi dần dần t&igrave;m được cảm gi&aacute;c an l&ograve;ng.<br />\n<br />\nBới đọc &ldquo;Xin ch&agrave;o t&igrave;nh y&ecirc;u&rdquo; của Hi&ecirc;n, ch&uacute;ng ta dễ d&agrave;ng bắt gặp, đắm ch&igrave;m với những d&ograve;ng thơ văn đầy cảm x&uacute;c, phảng phất dư vị nồng nhiệt của t&igrave;nh y&ecirc;u, của tuổi trẻ. Nhưng c&agrave;ng trưởng th&agrave;nh, ch&uacute;ng ta c&agrave;ng phải học c&aacute;ch đối mặt với những mối t&igrave;nh chưa trọn vẹn. Học được c&aacute;ch y&ecirc;u, học được c&aacute;ch mạnh mẽ ra đi khi ai đ&oacute; đ&atilde; thay l&ograve;ng v&agrave; can đảm nhận ra rằng: &ldquo;Cảm ơn đời đ&atilde; cho m&igrave;nh gặp nhau&rdquo;.</p>\n\n<p>T&igrave;nh y&ecirc;u l&agrave; thế, hạnh ph&uacute;c hay buồn đau, cũng mong bạn can đảm bước tiếp, bỏ lại những c&acirc;u chuyện đ&atilde; cũ ở ph&iacute;a sau, học c&aacute;ch mở l&ograve;ng để đ&oacute;n nhận một t&igrave;nh y&ecirc;u tốt đẹp hơn!</p>\n\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</p>\n', 2020, 203, 176),
(53, 15, 'Skybooks', 'NXB Phụ Nữ Việt Nam', 'Ở Đây Zui Nè', 'Bìa mềm', 'Không có', '<p>Cuộc đời ngày ngày nói yêu mình.</p>\n\n<p>Xong cuộc đời lại đủ thứ phức tạp v&agrave; bất c&ocirc;ng với m&igrave;nh.<br />\nVậy là cuộc đời ghét mình r&ocirc;̀i!</p>\n\n<p>Th&ocirc;i n&agrave;o!</p>\n\n<p>Thả lỏng và tận hưởng sự vui vẻ đi. Vì chẳng phải cuộc đời đang ghét bạn đâu, mà chính bạn bạn đang loay hoay với những mệt nhọc ở trên đời. Ví dụ như nay đã là deadline mà b&ocirc;̃ng bị rớt mạng, sáng nay đi làm quên đem theo ví, hay đ&ocirc;̀ng nghiệp ở công ty nói x&acirc;́u mình,...</p>\n', '<p>Cuộc đời ngày ngày nói yêu mình.</p>\n\n<p>Xong cuộc đời lại đủ thứ phức tạp v&agrave; bất c&ocirc;ng với m&igrave;nh.<br />\nVậy là cuộc đời ghét mình r&ocirc;̀i!</p>\n\n<p>Th&ocirc;i n&agrave;o!</p>\n\n<p>Thả lỏng và tận hưởng sự vui vẻ đi. Vì chẳng phải cuộc đời đang ghét bạn đâu, mà chính bạn bạn đang loay hoay với những mệt nhọc ở trên đời. Ví dụ như nay đã là deadline mà b&ocirc;̃ng bị rớt mạng, sáng nay đi làm quên đem theo ví, hay đ&ocirc;̀ng nghiệp ở công ty nói x&acirc;́u mình,...</p>\n\n<p>N&ecirc;́u k&ecirc;̉ ra thì sẽ có ti tỉ thứ không theo ý mình m&ocirc;̃i ngày. Không nói đ&ecirc;́n những chuyện bực tức khác, những n&ocirc;̃i bu&ocirc;̀n chúng ta không làm chủ được, những đi&ecirc;̀u vô tình đ&ecirc;́n khi&ecirc;́n ta quạu cọ và x&acirc;́u xí. Rồi khi ch&uacute;ng ta cứ để ch&uacute;ng trong l&ograve;ng sự phiền muộn kh&ocirc;ng biết k&eacute;o d&agrave;i đến bao giờ mới kết th&uacute;c.</p>\n\n<p>Cho n&ecirc;n, thay v&igrave; để t&acirc;m đến những chuyện khiến m&igrave;nh kh&ocirc;ng vui, h&atilde;y lựa chọn vui vẻ, bạn sẽ thấy cuộc đời trở n&ecirc;n tuyệt vời hơn rất nhiều. V&agrave; để c&acirc;n bằng những cảm x&uacute;c ấy &ldquo;Vui vẻ không quạu nha&rdquo; chính là một m&oacute;n qu&agrave; để lan tỏa v&agrave; nh&acirc;n l&ecirc;n niềm vui, một cu&ocirc;́n sách mà ai cũng c&acirc;̀n phải có đ&ecirc;̉ thêm yêu những đi&ecirc;̀u nhỏ xíu đáng yêu xung quanh m&igrave;nh.</p>\n\n<p>Vui vẻ kh&ocirc;ng quạu nha - Cu&ocirc;́n sách kh&ocirc;ng chỉ mặn m&agrave; đúng như tên gọi của nó m&agrave; c&ograve;n bắt kịp c&aacute;c xu hướng được quan t&acirc;m từ fanpage nổi tiếng &ldquo;Ở đ&acirc;y zui n&egrave;&rdquo; sẽ giúp bạn có những tràng cười vui vẻ không ngớt, c&oacute; c&aacute;i nh&igrave;n khoan dung cởi mở hơn, nhìn nhận những xui xẻo v&acirc;́p phải b&ocirc;̃ng trở nên nhỏ b&eacute; dưới g&oacute;c độ d&iacute; dỏm v&agrave; hài hước.</p>\n\n<p>Vui vẻ kh&ocirc;ng quạu nha - xin được gửi đ&ecirc;́n những bạn trẻ đang dễ giận dữ, cau c&oacute; ngoài th&ecirc;́ giới kia, những ai đang bu&ocirc;̀n phi&ecirc;̀n v&ecirc;̀ rắc rối nào đó, &ldquo;trái tim&rdquo; nhỏ bé này còn phải dành cho ni&ecirc;̀m vui, đừng đ&ecirc;̉ bực bội, dỗi hờn từ những đi&ecirc;̀u không đáng chi&ecirc;́m h&ecirc;́t ch&ocirc;̃.</p>\n\n<p>Và hãy hét to với Th&ecirc;́ giới rằng &ldquo;Mình là một người tràn đ&acirc;̀y năng lượng,&rdquo; luôn sẵn s&agrave;ng để hạnh ph&uacute;c hơn.</p>\n', 2020, 300, 280),
(54, 16, 'Nhã Nam', 'NXB Hội Nhà Văn', 'Paulo Coelho', 'Bìa mềm', 'Lê Chu Cầu', '<p><em>Tất cả những trải nghiệm trong chuyến phi&ecirc;u du theo đuổi vận mệnh của m&igrave;nh đ&atilde; gi&uacute;p Santiago thấu hiểu được &yacute; nghĩa s&acirc;u xa nhất của hạnh ph&uacute;c, h&ograve;a hợp với vũ trụ v&agrave; con người</em>.&nbsp;</p>\n', '<p><em>Tất cả những trải nghiệm trong chuyến phi&ecirc;u du theo đuổi vận mệnh của m&igrave;nh đ&atilde; gi&uacute;p Santiago thấu hiểu được &yacute; nghĩa s&acirc;u xa nhất của hạnh ph&uacute;c, h&ograve;a hợp với vũ trụ v&agrave; con người</em>.&nbsp;</p>\n\n<p>Tiểu thuyết&nbsp;<em>Nh&agrave; giả kim&nbsp;</em>của Paulo Coelho như một c&acirc;u chuyện cổ t&iacute;ch giản dị, nh&acirc;n &aacute;i, gi&agrave;u chất thơ, thấm đẫm những minh triết huyền b&iacute; của phương Đ&ocirc;ng. Trong lần xuất bản đầu ti&ecirc;n tại Brazil v&agrave;o năm 1988, s&aacute;ch chỉ b&aacute;n được 900 bản. Nhưng, với số phận đặc biệt của cuốn s&aacute;ch d&agrave;nh cho to&agrave;n nh&acirc;n loại, vượt ra ngo&agrave;i bi&ecirc;n giới quốc gia,&nbsp;<em>Nh&agrave; giả kim&nbsp;</em>đ&atilde; l&agrave;m rung động h&agrave;ng triệu t&acirc;m hồn, trở th&agrave;nh một trong những cuốn s&aacute;ch b&aacute;n chạy nhất mọi thời đại, v&agrave; c&oacute; thể l&agrave;m thay đổi cuộc đời người đọc.</p>\n\n<p>&ldquo;Nhưng nh&agrave; luyện kim đan kh&ocirc;ng quan t&acirc;m mấy đến những điều ấy. &Ocirc;ng đ&atilde; từng thấy nhiều người đến rồi đi, trong khi ốc đảo v&agrave; sa mạc vẫn l&agrave; ốc đảo v&agrave; sa mạc. &Ocirc;ng đ&atilde; thấy vua ch&uacute;a v&agrave; kẻ ăn xin đi qua biển c&aacute;t n&agrave;y, c&aacute;i biển c&aacute;t thường xuy&ecirc;n thay h&igrave;nh đổi dạng v&igrave; gi&oacute; thổi nhưng vẫn m&atilde;i m&atilde;i l&agrave; biển c&aacute;t m&agrave; &ocirc;ng đ&atilde; biết từ thuở nhỏ. Tuy vậy, tự đ&aacute;y l&ograve;ng m&igrave;nh, &ocirc;ng kh&ocirc;ng thể kh&ocirc;ng cảm thấy vui trước hạnh ph&uacute;c của mỗi người lữ kh&aacute;ch, sau bao ng&agrave;y chỉ c&oacute; c&aacute;t v&agrave;ng với trời xanh nay được thấy ch&agrave; l&agrave; xanh tươi hiện ra trước mắt. &lsquo;C&oacute; thể Thượng đế tạo ra sa mạc chỉ để cho con người biết qu&yacute; trọng c&acirc;y ch&agrave; l&agrave;,&rsquo; &ocirc;ng nghĩ.&rdquo;</p>\n\n<p>- Tr&iacute;ch&nbsp;<em>Nh&agrave; giả kim</em></p>\n\n<p><strong>Nhận định</strong></p>\n\n<p>&ldquo;Sau Garcia M&aacute;rquez, đ&acirc;y l&agrave; nh&agrave; văn Mỹ Latinh được đọc nhiều nhất thế giới.&rdquo;&nbsp;<em>- The Economist</em>, London, Anh</p>\n\n<p>&nbsp;</p>\n\n<p>&ldquo;Santiago c&oacute; khả năng cảm nhận bằng tr&aacute;i tim như&nbsp;<em>Ho&agrave;ng tử b&eacute;</em>&nbsp;của Saint-Exup&eacute;ry.&rdquo;&nbsp;<em>- Frankfurter Allgemeine Zeitung, Đức</em></p>\n', 2020, 220, 227);
INSERT INTO `chitietsanpham` (`id`, `masp`, `nhacungcap`, `nhaxuatban`, `tacgia`, `hinhthuc`, `nguoidich`, `mota`, `noidung`, `namxuatban`, `trongluong`, `sotrang`) VALUES
(55, 17, 'AZ Việt Nam', 'NXB Văn Học', 'Trí', 'Bìa mềm', 'Không có', '<p>C&oacute; Một Người Từng L&agrave; Tất Cả</p>\n\n<p>Năm cũ sắp qua đi, năm mới lại sắp đến, khoảng thời gian, hẳn trong l&ograve;ng mỗi ch&uacute;ng ta sẽ c&oacute; rất nhiều những điều ngổn ngang v&agrave; hối tiếc. V&agrave; c&oacute; lẽ, hối tiếc nhất l&agrave; khi ch&uacute;ng ta cảm nhận thời gian tr&ocirc;i qua thật nhanh nhưng bản th&acirc;n đ&atilde; bỏ lỡ qu&aacute; nhiều điều trong qu&aacute; khứ. Bỏ lỡ nhất vẫn l&agrave; một người n&agrave;o đ&oacute; từng đến, từng y&ecirc;u thương, từng thề hẹn, từng l&agrave; một phần m&aacute;u thịt, từng l&agrave; một phần th&oacute;i quen của cuộc đời m&igrave;nh. Để rồi m&igrave;nh xem người ta l&agrave;-tất-cả nhưng họ lại xem m&igrave;nh kh&ocirc;ng-l&agrave;-g&igrave;-cả.</p>\n', '<p>C&oacute; Một Người Từng L&agrave; Tất Cả</p>\n\n<p>Năm cũ sắp qua đi, năm mới lại sắp đến, khoảng thời gian, hẳn trong l&ograve;ng mỗi ch&uacute;ng ta sẽ c&oacute; rất nhiều những điều ngổn ngang v&agrave; hối tiếc. V&agrave; c&oacute; lẽ, hối tiếc nhất l&agrave; khi ch&uacute;ng ta cảm nhận thời gian tr&ocirc;i qua thật nhanh nhưng bản th&acirc;n đ&atilde; bỏ lỡ qu&aacute; nhiều điều trong qu&aacute; khứ. Bỏ lỡ nhất vẫn l&agrave; một người n&agrave;o đ&oacute; từng đến, từng y&ecirc;u thương, từng thề hẹn, từng l&agrave; một phần m&aacute;u thịt, từng l&agrave; một phần th&oacute;i quen của cuộc đời m&igrave;nh. Để rồi m&igrave;nh xem người ta l&agrave;-tất-cả nhưng họ lại xem m&igrave;nh kh&ocirc;ng-l&agrave;-g&igrave;-cả.</p>\n\n<p>Cuốn s&aacute;ch &ldquo;C&Oacute; MỘT NGƯỜI TỪNG L&Agrave; TẤT CẢ&rdquo; đ&atilde; được Tr&iacute; ấp ủ trong một khoảng thời gian rất d&agrave;i. Trong quyển s&aacute;ch lần n&agrave;y chất chứa rất nhiều t&acirc;m tư t&igrave;nh cảm m&agrave; tin chắc rằng khi bạn mở ra bất kỳ trang s&aacute;ch n&agrave;o, bạn cũng sẽ thấy m&igrave;nh đ&acirc;u đ&oacute;. Đ&acirc;u đ&oacute; của những ng&agrave;y trẻ dại. Đ&acirc;u đ&oacute; của những lần y&ecirc;u đau. Đ&acirc;u c&oacute; của những điều hối tiếc. Đ&acirc;u đ&oacute; của sự vỗ về tr&aacute;i tim b&eacute; nhỏ để gi&uacute;p bạn c&oacute; th&ecirc;m niềm tin bước tiếp.</p>\n\n<p>Bạn biết kh&ocirc;ng, điều đẹp nhất tr&ecirc;n cuộc đời n&agrave;y l&agrave; được y&ecirc;u, được dốc hết vốn liếng y&ecirc;u đương cho một người, v&agrave; được đau cho ch&iacute;nh t&igrave;nh y&ecirc;u ấy. Phải đau th&igrave; biết m&igrave;nh y&ecirc;u một người đến nhường n&agrave;o. Nếu t&igrave;nh y&ecirc;u kh&ocirc;ng đi qua trắc trở th&igrave; sẽ kh&ocirc;ng bao giờ đong đếm được cảm x&uacute;c. V&agrave; C&Oacute; MỘT NGƯỜI TỪNG L&Agrave; TẤT CẢ sẽ dẫn lối bạn v&agrave;o những năm th&aacute;ng như thế. Bởi trong đời mỗi ch&uacute;ng ta, &iacute;t nhất cũng đ&atilde; từng y&ecirc;u v&agrave;i người. Nhưng trong số v&agrave;i người ấy, duy chỉ c&oacute; một người y&ecirc;u đậm s&acirc;u nhất. Đ&oacute; c&oacute; thể l&agrave; t&igrave;nh đầu hoặc những mối t&igrave;nh sau t&igrave;nh đầu. Đ&oacute; cũng c&oacute; thể l&agrave; một người ch&uacute;ng ta sẽ c&ugrave;ng họ đi đến cuối đất c&ugrave;ng trời tr&ecirc;n con đường nh&acirc;n gian. Nhưng cũng c&oacute; thể chỉ l&agrave; một người nắm tay nhau đi một đoạn đời ch&oacute;ng v&aacute;nh m&agrave; th&ocirc;i. Hay duy&ecirc;n phận bạc đến nỗi chỉ l&agrave; những người dưng lướt qua đời nhau. Nhưng l&agrave; ai, l&agrave; g&igrave;, d&agrave;i l&acirc;u hay ngắn ngủi giờ đ&atilde; kh&ocirc;ng-c&ograve;n-quan-trọng nữa rồi. Quan trọng l&agrave; ch&uacute;ng ta đ&atilde; từng khờ dại cho họ một đặc quyền trong l&ograve;ng m&igrave;nh, để m&igrave;nh xem họ l&agrave; tất cả, l&agrave; hơi thở trong lồng ngực, l&agrave; tế b&agrave;o tr&ecirc;n từng thớ thịt. Một ng&agrave;y họ quay đi, m&igrave;nh chợt nhận ra rằng m&igrave;nh qu&aacute; ngốc nghếch v&igrave; đ&atilde; để &ldquo;C&oacute;-một-người-từng-l&agrave;-tất-cả!&rdquo;.</p>\n\n<p><em>&nbsp;</em></p>\n\n<p><em>&ldquo;V&agrave;o một ng&agrave;y đẹp trời ch&uacute;ng ta đ&atilde; n&oacute;i lời tạm biệt. Kh&ocirc;ng ai tr&aacute;ch ai, kh&ocirc;ng ai hờn dỗi. Ch&uacute;ng ta uống cạn v&agrave;i chai rồi mỉm cười tiễn nhau th&ecirc;m một đoạn về nh&agrave;. Ho&aacute; ra, cảm gi&aacute;c chia tay kh&ocirc;ng hề đ&aacute;ng sợ! V&igrave; ch&uacute;ng ta đ&atilde; hiểu m&igrave;nh bắt đầu rất &ecirc;m đẹp n&ecirc;n kết th&uacute;c cũng phải &ecirc;m đềm.&rdquo;</em></p>\n\n<p><em>&nbsp;</em></p>\n\n<p><em>&ldquo;Năm ấy, r&otilde; r&agrave;ng l&agrave; th&iacute;ch nhau, r&otilde; r&agrave;ng l&agrave; chỉ cần tiến th&ecirc;m một bước nữa l&agrave; c&oacute; thể c&oacute; được một cuộc t&igrave;nh khắc cốt ghi t&acirc;m. Vậy m&agrave;, mỗi đứa lại chọn c&aacute;ch dừng lại v&igrave; nghĩ rằng đối phương chưa thực sự thương m&igrave;nh. Từ bỏ của tuổi trẻ, vĩnh viễn l&agrave; hối tiếc sau n&agrave;y!&rdquo;</em></p>\n\n<p><em>&nbsp;</em></p>\n\n<p><em>&ldquo;Hồi đ&oacute; tụi m&igrave;nh hay n&oacute;i, lễ thất tịch sẽ ăn ch&egrave; đậu đỏ. Anh một b&aacute;t. Em một b&aacute;t. M&atilde;i m&atilde;i kh&ocirc;ng rời. Vĩnh viễn kh&ocirc;ng xa. Sau n&agrave;y nghĩ lại ho&aacute; ra buồn cười. Dẫu c&oacute; ăn mười b&aacute;t ch&egrave; đậu đỏ đi nữa, m&agrave; l&ograve;ng người đổi thay hay kh&ocirc;ng c&oacute; duy&ecirc;n nợ, cũng c&oacute; b&ecirc;n nhau được đ&acirc;u. Sau n&agrave;y mới biết b&aacute;t ch&egrave; đậu đỏ năm xưa m&atilde;i l&agrave; một c&acirc;u chuyện n&oacute;i dối.&rdquo;</em></p>\n\n<p><em>&nbsp;</em></p>\n\n<p><em>&ldquo;c&oacute; một người từng l&agrave; tất cả</em></p>\n\n<p><em>nhưng b&acirc;y giờ đ&atilde; trở th&agrave;nh người lạ</em></p>\n\n<p><em>anh với người ta, c&ograve;n em... th&igrave; c&aacute;ch xa.</em></p>\n\n<p><em>&nbsp;</em></p>\n\n<p><em>chuyện ch&uacute;ng m&igrave;nh vừa mới h&ocirc;m qua</em></p>\n\n<p><em>bỗng h&ocirc;m nay hai người hai lối rẽ</em></p>\n\n<p><em>chả nhẽ y&ecirc;u như tr&ograve; chơi c&uacute;t bắt</em></p>\n\n<p><em>anh thắng em rồi,</em></p>\n\n<p><em>em đau thắt ruột gan.&rdquo;</em></p>\n\n<p><em>&nbsp;</em></p>\n\n<p>Đ&acirc;y sẽ l&agrave; một quyển nhật k&yacute; nhỏ, mỗi ng&agrave;y bạn h&aacute;o hức mở ra từng trang một, đọc từng con chữ v&agrave; cảm nhận thật r&otilde; những đoạn phim cuộc đời đang tua lại trong t&acirc;m tr&iacute;. &nbsp;<em>Mong rằng C&Oacute; MỘT NGƯỜI TỪNG L&Agrave; TẤT CẢ sẽ gi&uacute;p bạn đi qua M&ugrave;a Đ&ocirc;ng năm nay thật an y&ecirc;n!</em></p>\n', 2020, 250, 232),
(56, 18, 'ZGROUP', 'NXB Hà Nội', 'Bạch Lạc Mai', 'Bìa cứng', 'Lục Bích', '<p>Non nước cỏ c&acirc;y, tr&agrave; trong rượu nhạt, đ&igrave;nh đ&agrave;i lầu g&aacute;c, v&agrave;ng ngọc ch&acirc;u b&aacute;u, đều c&oacute; t&igrave;nh &yacute;, chi tiết, c&oacute; cơ duy&ecirc;n diệu kỳ kh&oacute; m&agrave; diễn tả nổi. Biết l&agrave;m sao khi sự vật sự việc c&oacute; đẹp hơn đi chăng nữa, cũng cần người c&oacute; l&ograve;ng thưởng thức, thương tiếc n&oacute;. Nếu kh&ocirc;ng chỉ l&agrave; một sự tồn tại giản đơn, ng&agrave;n năm cũng như một ng&agrave;y m&agrave; th&ocirc;i.</p>\n', '<p>iền v&agrave;i c&acirc;u tiểu lệnh, gi&atilde;i b&agrave;y dăm ba t&acirc;m sự, như thế, chẳng phụ nh&acirc;n gian như gấm th&ecirc;u.</p>\n\n<p><strong>Đ&ocirc;i n&eacute;t t&aacute;c giả</strong></p>\n\n<p>Bạch Lạc Mai t&ecirc;n thật l&agrave; Tư Tr&iacute; Tuệ, sống ở Giang Nam, đơn giản tự chủ, t&acirc;m như lan thảo, văn chương thanh đạm.<br />\n&nbsp;</p>\n\n<p><strong>Cẩm Phong đ&atilde; xuất bản:</strong><br />\n<em>- Năm th&aacute;ng tĩnh lặng, kiếp n&agrave;y b&igrave;nh y&ecirc;n (t&aacute;i bản 2018)<br />\n- Gặp lại chốn hồng trần s&acirc;u nhất (t&aacute;i bản 2018)<br />\n- Duy&ecirc;n (t&aacute;i bản 2018)<br />\n- Nếu em an l&agrave;nh, đ&oacute; l&agrave; ng&agrave;y nắng (xuất bản 2018)<br />\n- Ngoảnh lại đ&atilde; một đời (xuất bản 2019)<br />\n- Bởi v&igrave; thấu hiểu cho n&ecirc;n từ bi (xuất bản 2020)</em><br />\n&nbsp;</p>\n\n<p><strong>Cẩm Phong sắp xuất bản:</strong><br />\n<em>- Anh đi ngang qua thời khắc khuynh th&agrave;nh của em (tạm dịch)<br />\n- T&ocirc;i d&ugrave;ng cả thanh xu&acirc;n để t&igrave;m em (tạm dịch)<br />\n- Vị thời gian (tạm dịch)</em></p>\n', 2020, 450, 376),
(57, 19, 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', 'Nguyên Phong', 'Bìa mềm', 'Không có', '<p><em>&ldquo;Dấu ch&acirc;n tr&ecirc;n c&aacute;t&rdquo;</em>&nbsp;l&agrave; t&aacute;c phẩm được dịch giả Nguy&ecirc;n Phong ph&oacute;ng t&aacute;c kể về x&atilde; hội Ai Cập thế kỷ thứ XIV trước CN, qua lời k&ecirc;̉ của nh&acirc;n vật ch&iacute;nh - &nbsp;Sinuhe.</p>\n', '<p><em>&ldquo;Dấu ch&acirc;n tr&ecirc;n c&aacute;t&rdquo;</em>&nbsp;l&agrave; t&aacute;c phẩm được dịch giả Nguy&ecirc;n Phong ph&oacute;ng t&aacute;c kể về x&atilde; hội Ai Cập thế kỷ thứ XIV trước CN, qua lời k&ecirc;̉ của nh&acirc;n vật ch&iacute;nh - &nbsp;Sinuhe.</p>\n\n<p>Ng&agrave;y nay, người ta biết đến triều đại c&aacute;c vua ch&uacute;a Ai Cập thời cổ qua s&aacute;ch vở của người Hy Lạp. Sở dĩ c&aacute;c sử gia Hy Lạp biết được c&aacute;c chi tiết n&agrave;y v&igrave; họ đ&atilde; học hỏi từ người Ai Cập bị đ&agrave;y biệt xứ t&ecirc;n l&agrave; Sinuhe. Đ&acirc;y l&agrave; một nh&acirc;n vật lạ l&ugrave;ng, đ&atilde; c&oacute; c&ocirc;ng mang văn minh Ai Cập truyền v&agrave;o Hy Lạp khi quốc gia n&agrave;y c&ograve;n ở t&igrave;nh trạng k&eacute;m mở mang so với Ai Cập l&uacute;c đ&oacute;.</p>\n\n<p>C&aacute;c sử gia ng&agrave;y nay đ&atilde; đưa ra nhiều giả thuyết về nh&acirc;n vật Sinuhe n&agrave;y. C&oacute; người cho rằng &ocirc;ng l&agrave; một l&aacute;i bu&ocirc;n đến Hy Lạp lập nghiệp, nhưng l&agrave;m sao l&aacute;i bu&ocirc;n lại mở trường dạy học v&agrave; để lại nhiều t&agrave;i liệu qu&yacute; gi&aacute; như thế được? Từ ng&agrave;n xưa,&nbsp; chỉ ri&ecirc;ng giai cấp vua ch&uacute;a l&agrave; gi&aacute;o sĩ mới được hưởng quy chế gi&aacute;o dục to&agrave;n vẹn như vậy m&agrave; th&ocirc;i.</p>\n\n<p>Do đ&oacute;, một số người cho rằng &ocirc;ng thuộc giai cấp gi&aacute;o sĩ, nhưng việc một gi&aacute;o sĩ Ai Cập đến mở trường dạy học tại Hy Lạp cũng l&agrave; điều kh&oacute; chấp nhận. Mặc d&ugrave; khi đ&oacute; văn minh Hy Lạp chưa tiến bộ như Ai Cập nhưng gi&aacute;o xứ n&agrave;y ph&aacute;t triển rất mạnh, hiển nhi&ecirc;n c&aacute;c gi&aacute;o sĩ Hy Lạp kh&ocirc;ng thể chấp nhận cho một gi&aacute;o sĩ ngoại quốc đến mở trường dạy học tại th&aacute;nh địa Olympia của họ được.</p>\n\n<p>Nếu thế th&igrave; phải chăng Sinuhe thuộc giai cấp ho&agrave;ng tộc? Điều n&agrave;y x&eacute;t ra cũng kh&ocirc;ng c&oacute; l&yacute; v&igrave; một người thuộc giai cấp ho&agrave;ng tộc kh&ocirc;ng thể bị đ&agrave;y biệt xứ. Luật ph&aacute;p Ai Cập chủ trương xử tử những kẻ trong ho&agrave;ng tộc nếu họ vi phạm một tội trọng n&agrave;o đ&oacute; chứ kh&ocirc;ng c&oacute; lệ bị đ&agrave;y biệt xứ, v&igrave; c&aacute;c vua Pharaoh rất sợ những người trong bọn họ chi&ecirc;u binh m&atilde;i m&atilde; l&agrave;m phản.</p>\n\n<p>&nbsp;Việc một người Ai Cập, th&acirc;n thế mơ hồ, bị đ&agrave;y biệt xứ, đến mở trường dạy học tại Athens, trung t&acirc;m văn h&oacute;a của Hy Lạp, vẫn l&agrave; một b&iacute; mật đến nay c&aacute;c nh&agrave; khảo cổ chưa t&igrave;m ra được c&acirc;u trả lời.</p>\n\n<p>Mặc d&ugrave; l&agrave; tiểu thuyết lịch sử về x&atilde; hội Ai Cập cổ đại, song nhiều vấn đề được n&ecirc;u ra trong t&aacute;c phẩm vẫn c&oacute; &yacute; nghĩa thời sự trong thế giới ng&agrave;y nay. Đ&oacute; l&agrave; l&yacute; do khiến cho t&aacute;c phẩm kh&ocirc;ng chỉ giữ được sự cuốn h&uacute;t đối với bạn đọc Việt Nam v&agrave; rất nhiều nơi&nbsp; tr&ecirc;n thế giới trong hơn nửa thế kỷ qua.</p>\n\n<p>Bạn sẽ kh&ocirc;ng thể rời mắt khỏi những d&ograve;ng văn viết với &yacute; tứ s&acirc;u sắc của một người con xa qu&ecirc; hương: &quot;Người Ai Cập c&oacute; th&agrave;nh ngữ: &quot;Kẻ n&agrave;o đ&atilde; uống nước s&ocirc;ng Nile th&igrave; kh&ocirc;ng thể n&agrave;o uống nước ở đ&acirc;u được nữa&quot;. Quả thế tuy sống tại Hy Lạp nhưng kh&ocirc;ng bao giờ t&ocirc;i c&oacute; thể qu&ecirc;n được Ai Cập qu&ecirc; hương th&acirc;n y&ecirc;u của t&ocirc;i.</p>\n\n<p>Dường như những miền n&agrave;o x&acirc;y dựng tr&ecirc;n mặt c&aacute;t, chỉ huy ho&agrave;ng trong một thời gian rất ngắn rồi t&agrave;n lụi, nhưng mấy ai chịu để &yacute; đến điều ấy. Cũng như những vết dấu ch&acirc;n tr&ecirc;n c&aacute;t chỉ tồn tại một tho&aacute;ng gi&acirc;y rồi phai mờ, huyền thoại về một người Ai Cập qua Hy Lạp mở trường dạy học, đ&agrave;o tạo nhiều thế hệ học tr&ograve; xuất sắc chỉ c&ograve;n l&agrave; một c&acirc;u chuyện mơ hồ trong cuộc sống ồn &agrave;o, n&aacute;o nhiệt ng&agrave;y nay.</p>\n\n<p><strong><em>BOX</em></strong></p>\n\n<p>Dịch giả Nguy&ecirc;n Phong t&ecirc;n thật l&agrave; Vũ Văn Du, sinh năm 1950 tại H&agrave; Nội. &Ocirc;ng rời Việt Nam du học ở Hoa Kỳ từ năm 1968 v&agrave; tốt nghiệp cao học ở hai ng&agrave;nh Sinh vật học v&agrave; Điện to&aacute;n. Ngo&agrave;i c&ocirc;ng việc ch&iacute;nh l&agrave; một kỹ sư cao cấp tại Boeing trong hơn 20 năm, &ocirc;ng vẫn tiếp tục nghi&ecirc;n cứu trong vai tr&ograve; nh&agrave; khoa học tại Đại học Carnergie Mellon v&agrave; Đại học Seattle. &Ocirc;ng c&ograve;n giảng dạy tại một số trường đại học quốc tế tại Trung Hoa, H&agrave;n Quốc, Nhật Bản... về lĩnh vực c&ocirc;ng nghệ phần mềm.</p>\n\n<p>Song song với vai tr&ograve; một nh&agrave; khoa học, Nguy&ecirc;n Phong c&ograve;n l&agrave; dịch giả nổi tiếng của loạt s&aacute;ch về văn h&oacute;a v&agrave; t&acirc;m linh phương Đ&ocirc;ng, chuyển thể từ nhiều t&aacute;c phẩm của c&aacute;c học giả phương T&acirc;y sau qu&aacute; tr&igrave;nh t&igrave;m hiểu v&agrave; kh&aacute;m ph&aacute; c&aacute;c gi&aacute; trị tinh thần từ phương Đ&ocirc;ng. Trong số đ&oacute;, c&oacute; thể kể:&nbsp;<em>H&agrave;nh Tr&igrave;nh về phương Đ&ocirc;ng, Ngọc s&aacute;ng trong hoa sen, B&ecirc;n rặng Tuyết sơn,&nbsp; Hoa sen tr&ecirc;n tuyết, Huyền thuật v&agrave; đạo sĩ T&acirc;y Tạng, Minh Triết trong đời sống, Đường m&acirc;y qua xứ tuyết&hellip;</em></p>\n\n<p>Đam m&ecirc; thiền học, nghi&ecirc;n cứu s&acirc;u sắc c&aacute;c vấn đề t&acirc;m linh dưới g&oacute;c nh&igrave;n của khoa học, những t&aacute;c phẩm của &ocirc;ng, phần lớn l&agrave; ph&oacute;ng t&aacute;c, gi&uacute;p người đọc tiếp cận c&aacute;c t&aacute;c phẩm gốc thuận lợi hơn, l&yacute; giải được những vấn đề c&ograve;n nhiều ẩn số bằng c&aacute;i nh&igrave;n minh triết.</p>\n', 2020, 450, 432),
(58, 20, 'Huy Hoang Bookstore', 'NXB Văn học', 'Jeffrey Archer', 'Bìa cứng', 'Nguyễn Việt Hải', '<p>&ldquo;Hai số phận&rdquo; kh&ocirc;ng chỉ đơn thuần l&agrave; một cuốn tiểu thuyết, đ&acirc;y c&oacute; thể xem l&agrave; &quot;th&aacute;nh kinh&quot; cho những người đọc v&agrave; suy ngẫm, những ai kh&ocirc;ng dễ d&atilde;i, kh&ocirc;ng chấp nhận lối m&ograve;n.</p>\n', '<p>&ldquo;Hai số phận&rdquo; kh&ocirc;ng chỉ đơn thuần l&agrave; một cuốn tiểu thuyết, đ&acirc;y c&oacute; thể xem l&agrave; &quot;th&aacute;nh kinh&quot; cho những người đọc v&agrave; suy ngẫm, những ai kh&ocirc;ng dễ d&atilde;i, kh&ocirc;ng chấp nhận lối m&ograve;n.<br />\n&ldquo;Hai số phận&rdquo; l&agrave;m rung động mọi tr&aacute;i tim quả cảm, n&oacute; c&oacute; thể l&agrave;m thay đổi cả cuộc đời bạn. Đọc cuốn s&aacute;ch n&agrave;y, bạn sẽ bị chi phối bởi c&aacute; t&iacute;nh của hai nh&acirc;n vật ch&iacute;nh, hoặc bạn l&agrave; Kane, hoặc sẽ l&agrave; Abel, kh&ocirc;ng thể n&agrave;o nhầm lẫn. V&agrave; điều đ&oacute; sẽ khiến bạn thấy được ch&iacute;nh m&igrave;nh.<br />\nKhi bạn y&ecirc;u th&iacute;ch t&aacute;c phẩm n&agrave;y, người ta sẽ nh&igrave;n ra c&aacute; t&iacute;nh v&agrave; t&acirc;m hồn th&uacute; vị của bạn!<br />\n&ldquo;Nếu c&oacute; giải thưởng Nobel về khả năng kể chuyện, giải thưởng đ&oacute; chắc chắn sẽ thuộc về Archer.&rdquo;<br />\n&nbsp;- Daily Telegraph-<br />\n(&ldquo;Hai số phận&rdquo; (Kane &amp; Abel) l&agrave; c&acirc;u chuyện về hai người đ&agrave;n &ocirc;ng đi t&igrave;m vinh quang. William Kane l&agrave; con một triệu ph&uacute; nổi tiếng tr&ecirc;n đất Mỹ, lớn l&ecirc;n trong nhung lụa của thế giới thượng lưu. Wladek Koskiewicz l&agrave; đứa trẻ kh&ocirc;ng r&otilde; xuất th&acirc;n, được gia đ&igrave;nh người bẫy th&uacute; nhặt về nu&ocirc;i. Một người được ấn định để trở th&agrave;nh chủ ng&acirc;n h&agrave;ng khi lớn l&ecirc;n, người kia nhập cư v&agrave;o Mỹ c&ugrave;ng đo&agrave;n người ngh&egrave;o khổ.&nbsp;<br />\nTrải qua hơn s&aacute;u mươi năm với biết bao biến động, hai con người gi&agrave;u ho&agrave;i b&atilde;o miệt m&agrave;i x&acirc;y dựng vận mệnh của m&igrave;nh . &ldquo;Hai số phận&rdquo; n&oacute;i về nỗi kh&aacute;t khao ch&aacute;y bỏng, những nghĩa cử, những mối th&acirc;m th&ugrave;, từng bước đường phi&ecirc;u lưu, hiện thực thế giới v&agrave; những g&oacute;c khuất... m&ecirc; hoặc người đọc bằng ng&ocirc;n từ c&ocirc; đọng, hai mạch truyện song song được x&acirc;y dựng tinh tế từ những chi tiết nhỏ nhất.)&nbsp;</p>\n', 2018, 800, 768),
(59, 21, 'Random House', 'Random House Books for Young Readers', 'Mary Pope Osborne', 'Bìa mềm', 'Không có', '<p>Jack and Annie are ready for their next fantasy adventure in the bestselling middle-grade series&mdash;the Magic Tree House!<br />\n<br />\nIt&#39;s icicle city . . .<br />\n<br />\nwhen the Magic Tree House whisks Jack and Annie to the frozen Arctic. Luckily, a seal hunter on a dogsled lends them warm clothes. Unluckily, they get stuck on cracking ice. Will the giant polar bear save them? Or will Jack and Annie become frozen dinners?</p>\n', '<p>Jack and Annie are ready for their next fantasy adventure in the bestselling middle-grade series&mdash;the Magic Tree House!<br />\n<br />\nIt&#39;s icicle city . . .<br />\n<br />\nwhen the Magic Tree House whisks Jack and Annie to the frozen Arctic. Luckily, a seal hunter on a dogsled lends them warm clothes. Unluckily, they get stuck on cracking ice. Will the giant polar bear save them? Or will Jack and Annie become frozen dinners?<br />\n<br />\nVisit the Magic Tree House website!<br />\nMagicTreeHouse.com</p>\n', 1998, 73, 96),
(60, 22, '1980 Books', 'NXB Phụ Nữ', 'Jessica Higgins', 'Bìa mềm', 'Phạm Lan Anh', '<p>Kh&ocirc;ng thiếu những lời khuy&ecirc;n về việc l&agrave;m thế n&agrave;o để đạt hiệu quả giao tiếp trong c&ocirc;ng việc. Vấn đề l&agrave; bạn c&oacute; biết phương ph&aacute;p n&agrave;o thực sự được chứng minh l&agrave; c&oacute; hiệu quả v&agrave; bạn n&ecirc;n h&agrave;nh động như n&agrave;o để c&oacute; kết quả tốt nhất.</p>\n', '<p>Kh&ocirc;ng thiếu những lời khuy&ecirc;n về việc l&agrave;m thế n&agrave;o để đạt hiệu quả giao tiếp trong c&ocirc;ng việc. Vấn đề l&agrave; bạn c&oacute; biết phương ph&aacute;p n&agrave;o thực sự được chứng minh l&agrave; c&oacute; hiệu quả v&agrave; bạn n&ecirc;n h&agrave;nh động như n&agrave;o để c&oacute; kết quả tốt nhất.</p>\n\n<p>Ho&agrave;n to&agrave;n xuất ph&aacute;t từ thực tiễn&nbsp;<strong><em>&ldquo;Nghệ thuật giao tiếp nơi c&ocirc;ng sở&rdquo;&nbsp;</em></strong>sẽ gi&uacute;p bạn:</p>\n\n<p>Thuyết tr&igrave;nh trước đ&aacute;m đ&ocirc;ng</p>\n\n<p>Đ&agrave;m ph&aacute;n chuy&ecirc;n&nbsp; nghiệp</p>\n\n<p>Tạo dựng mối quan hệ tốt đẹp với đồng nghiệp</p>\n\n<p>X&acirc;y dựng tinh thần l&agrave;m việc nh&oacute;m hiệu quả</p>\n\n<p>L&agrave;m chủ ng&ocirc;n ngữ cơ thể</p>\n\n<p>Cuốn s&aacute;ch n&agrave;y truyền tải th&ocirc;ng điệp r&otilde; r&agrave;ng về việc n&ecirc;n l&agrave;m v&agrave; kh&ocirc;ng n&ecirc;n l&agrave;m. Bất cứ ai muốn l&agrave;m việc tốt hơn, bất kể cấp bậc hay tuổi t&aacute;c, đều n&ecirc;n c&oacute; cuốn s&aacute;ch n&agrave;y trong tay.</p>\n', 2019, 280, 252),
(61, 23, '1980 Books', 'Đại Học Kinh Tế Quốc Dân', 'Martin Krengel', 'Bìa mềm', 'Ngân Hà', '<p>Đại Học Hay Học &ldquo;Đại&rdquo;</p>\n\n<p>Học đại học thế n&agrave;o cho hiệu quả? Rất nhiều t&acirc;n sinh vi&ecirc;n nghĩ họ chỉ cần &aacute;p dụng y nguy&ecirc;n c&aacute;ch học từ thời phổ th&ocirc;ng l&agrave; đ&atilde; đủ. Nhưng khối lượng kiến thức ở cấp đại học ho&agrave;n to&agrave;n vượt trội so với những g&igrave; bạn từng trải qua. Học đại học kh&ocirc;ng chỉ l&agrave; &ocirc;m s&aacute;ch vở đối ph&oacute; với c&aacute;c b&agrave;i kiểm tra, m&agrave; bạn c&ograve;n phải d&agrave;nh nhiều thời gian để tham gia c&aacute;c hoạt động ngo&agrave;i giờ học (tham gia c&acirc;u lạc bộ, c&aacute;c hoạt động ngoại kh&oacute;a, dự c&aacute;c buổi hội thảo, huấn luyện...). Qu&aacute; nhiều điều bạn muốn l&agrave;m, nhưng quĩ thời gian chỉ c&oacute; hạn. Để giữ cho bản th&acirc;n kh&ocirc;ng bị rơi v&agrave;o trạng th&aacute;i mệt mỏi, giảm &yacute; ch&iacute;, ch&uacute;ng ta phải l&agrave;m g&igrave;?</p>\n', '<p>Đại Học Hay Học &ldquo;Đại&rdquo;</p>\n\n<p>Học đại học thế n&agrave;o cho hiệu quả? Rất nhiều t&acirc;n sinh vi&ecirc;n nghĩ họ chỉ cần &aacute;p dụng y nguy&ecirc;n c&aacute;ch học từ thời phổ th&ocirc;ng l&agrave; đ&atilde; đủ. Nhưng khối lượng kiến thức ở cấp đại học ho&agrave;n to&agrave;n vượt trội so với những g&igrave; bạn từng trải qua. Học đại học kh&ocirc;ng chỉ l&agrave; &ocirc;m s&aacute;ch vở đối ph&oacute; với c&aacute;c b&agrave;i kiểm tra, m&agrave; bạn c&ograve;n phải d&agrave;nh nhiều thời gian để tham gia c&aacute;c hoạt động ngo&agrave;i giờ học (tham gia c&acirc;u lạc bộ, c&aacute;c hoạt động ngoại kh&oacute;a, dự c&aacute;c buổi hội thảo, huấn luyện...). Qu&aacute; nhiều điều bạn muốn l&agrave;m, nhưng quĩ thời gian chỉ c&oacute; hạn. Để giữ cho bản th&acirc;n kh&ocirc;ng bị rơi v&agrave;o trạng th&aacute;i mệt mỏi, giảm &yacute; ch&iacute;, ch&uacute;ng ta phải l&agrave;m g&igrave;?</p>\n\n<p>T&aacute;c giả, chuy&ecirc;n gia gi&aacute;o dục người Đức Martin Krengel quan niệm: &quot;H&atilde;y học tập v&agrave; nghi&ecirc;n cứu hết m&igrave;nh nhưng vẫn kh&ocirc;ng qu&ecirc;n những cuộc vui!&quot;. &Ocirc;ng viết cuốn&nbsp;<strong>&ldquo;Đại học hay học &lsquo;đại&rsquo;&rdquo;</strong>, đ&uacute;c kết lại những trải nghiệm thực tế ch&acirc;n thực, để gợi &yacute; cho c&aacute;c bạn c&aacute;ch học tập hiệu quả v&agrave; đầy hứng th&uacute;. Xuy&ecirc;n suốt 10 chương s&aacute;ch, bạn sẽ nhận diện được c&aacute;c vấn đề quan trọng, x&aacute;c định r&otilde; những điều cần l&agrave;m, v&agrave; bắt tay v&agrave;o h&agrave;nh động. Đặc biệt, những b&iacute; k&iacute;p trong s&aacute;ch kh&ocirc;ng giới hạn trong lĩnh vực học tập, m&agrave; c&ograve;n l&agrave; &quot;kỹ năng sinh tồn&quot; để bạn thay đổi nếp sống, quản l&yacute; thời gian kh&ocirc;n kh&eacute;o hơn v&agrave; sắp xếp c&aacute;c hoạt động ngoại kh&oacute;a thật ph&ugrave; hợp. N&oacute;i t&oacute;m lại, đời sống đại học của bạn sẽ trở n&ecirc;n tươi mới v&agrave; đầy ắp niềm vui, kh&ocirc;ng c&oacute; chỗ cho sự nh&agrave;m ch&aacute;n v&agrave; những &aacute;p lực kh&ocirc;ng cần c&oacute; - với sự trợ gi&uacute;p thiết thực đến từ cuốn s&aacute;ch của Martin Krengel.</p>\n\n<p>H&atilde;y để cuốn cẩm nang&nbsp;<strong>&ldquo;Đại học hay học &lsquo;đại&rsquo;&rdquo;</strong>&nbsp;biến qu&atilde;ng thời gian tr&ecirc;n giảng đường đại học th&agrave;nh những kỷ niệm tuyệt vời kh&ocirc;ng thể qu&ecirc;n của bạn!&nbsp;</p>\n', 2017, 500, 460),
(62, 24, '1980 Books', 'NXB Thế Giới', 'Nhiều Tác Giả', 'Bìa mềm', 'Không có', '<p>Ho&agrave;ng Tử B&eacute; - Tập 8: H&agrave;nh Tinh R&ugrave;a Khổng Lồ</p>\n\n<p>Điểm dừng ch&acirc;n tiếp theo trong chuyến phi&ecirc;u lưu của Ho&agrave;ng Tử B&eacute; l&agrave; h&agrave;nh tinh của R&ugrave;a khổng lồ. Nơi đ&acirc;y đang gặp rắc rối lớn khi những cư d&acirc;n kh&ocirc;ng thể gửi thư đến người th&acirc;n của họ, li&ecirc;n lạc với b&ecirc;n ngo&agrave;i ho&agrave;n to&agrave;n bị cắt đứt.</p>\n', '<p>Ho&agrave;ng Tử B&eacute; - Tập 8: H&agrave;nh Tinh R&ugrave;a Khổng Lồ</p>\n\n<p>Điểm dừng ch&acirc;n tiếp theo trong chuyến phi&ecirc;u lưu của Ho&agrave;ng Tử B&eacute; l&agrave; h&agrave;nh tinh của R&ugrave;a khổng lồ. Nơi đ&acirc;y đang gặp rắc rối lớn khi những cư d&acirc;n kh&ocirc;ng thể gửi thư đến người th&acirc;n của họ, li&ecirc;n lạc với b&ecirc;n ngo&agrave;i ho&agrave;n to&agrave;n bị cắt đứt.</p>\n\n<p>Nguy&ecirc;n nh&acirc;n của tai ương n&agrave;y l&agrave; sự biến mất của những ch&uacute; R&ugrave;a khổng lồ - sinh vật duy nhất c&oacute; thể di chuyển tr&ecirc;n h&agrave;nh tinh với nhiệm vụ đưa thư. Ho&agrave;ng Tử B&eacute; c&ugrave;ng C&aacute;o lại bắt đầu cuộc h&agrave;nh tr&igrave;nh t&igrave;m kiếm những ch&uacute; R&ugrave;a v&agrave; kh&aacute;m ra c&acirc;u chuyện đằng sau n&oacute;.</p>\n', 2017, 200, 56),
(63, 26, '1980 Books', 'NXB Lao Động', 'Suzuki Hiroki', 'Bìa mềm', 'Nhật Linh', '<p><strong><em>&ldquo;Chiến lược của kẻ thắng&rdquo;</em></strong>&nbsp;tổng hợp 31 b&agrave;i học qu&acirc;n sự, đ&uacute;c kết từ lịch sử Đ&ocirc;ng t&acirc;y kim cổ trải d&agrave;i suốt ba ng&agrave;n năm. Những anh h&ugrave;ng mạnh nhất, lỗi lạc nhất lịch sử đều để lại cho hậu thế những b&agrave;i học để đời, từ Hannibal, Caesar, Gia C&aacute;t Lượng đến Napol&eacute;on, Oda Nobunaga, Toyotomi Hideyoshi, Tokugawa leyasu&hellip; Tất cả c&aacute;c kỹ năng tư duy, h&agrave;nh động của những vị tướng xuất sắc ấy đ&atilde; l&agrave;m thay đổi cả một giai đoạn lịch sử.</p>\n', '<p><strong><em>&ldquo;Chiến lược của kẻ thắng&rdquo;</em></strong>&nbsp;tổng hợp 31 b&agrave;i học qu&acirc;n sự, đ&uacute;c kết từ lịch sử Đ&ocirc;ng t&acirc;y kim cổ trải d&agrave;i suốt ba ng&agrave;n năm. Những anh h&ugrave;ng mạnh nhất, lỗi lạc nhất lịch sử đều để lại cho hậu thế những b&agrave;i học để đời, từ Hannibal, Caesar, Gia C&aacute;t Lượng đến Napol&eacute;on, Oda Nobunaga, Toyotomi Hideyoshi, Tokugawa leyasu&hellip; Tất cả c&aacute;c kỹ năng tư duy, h&agrave;nh động của những vị tướng xuất sắc ấy đ&atilde; l&agrave;m thay đổi cả một giai đoạn lịch sử.</p>\n\n<p>Kh&ocirc;ng chỉ thay đổi trường đoạn lịch sử, những chiến thắng, s&aacute;ch lược ấy c&ograve;n t&aacute;c động tới chiến lược quản trị kinh doanh ng&agrave;y nay. Chủ nghĩa to&agrave;n cầu đang len lỏi v&agrave;o từng doanh nghiệp, Microft, Google, Amazon, Toyota, Uniqlo, 7-Eleven&hellip; Họ kh&ocirc;ng đứng b&ecirc;n ngo&agrave;i cuộc cạnh tranh thương mại. Họ cần t&igrave;m cho m&igrave;nh ch&igrave;a kh&oacute;a để th&agrave;nh c&ocirc;ng v&agrave; đứng vững trong d&ograve;ng chảy xu thế đ&oacute;.</p>\n', 2019, 400, 376),
(64, 27, 'Thái Hà', 'NXB Công Thương', 'William J Rothwell, Robert K Prescott, Maria W Taylor', 'Bìa mềm', 'Không có', '<p>Cuốn s&aacute;ch n&agrave;y được định h&igrave;nh năm 2004. Khi đ&oacute;, một trong những t&aacute;c giả của cuốn s&aacute;ch - William Rothwell - được mời tới một nh&agrave; m&aacute;y c&ocirc;ng nghiệp lớn ở Trung Quốc v&agrave; được ph&oacute; tổng gi&aacute;m đốc phụ tr&aacute;ch nguồn nh&acirc;n lực (HR(1)) của tổ chức đ&oacute; đề nghị gi&uacute;p nh&agrave; m&aacute;y đ&aacute;p ứng đ&ograve;i hỏi đ&aacute;ng ngạc nhi&ecirc;n của l&atilde;nh đạo tập đo&agrave;n l&agrave; cắt giảm 40% c&aacute;n bộ HR của nh&agrave; m&aacute;y trong khoảng thời gian chưa đầy một th&aacute;ng.&nbsp;</p>\n', '<p>Cuốn s&aacute;ch n&agrave;y được định h&igrave;nh năm 2004. Khi đ&oacute;, một trong những t&aacute;c giả của cuốn s&aacute;ch - William Rothwell - được mời tới một nh&agrave; m&aacute;y c&ocirc;ng nghiệp lớn ở Trung Quốc v&agrave; được ph&oacute; tổng gi&aacute;m đốc phụ tr&aacute;ch nguồn nh&acirc;n lực (HR(1)) của tổ chức đ&oacute; đề nghị gi&uacute;p nh&agrave; m&aacute;y đ&aacute;p ứng đ&ograve;i hỏi đ&aacute;ng ngạc nhi&ecirc;n của l&atilde;nh đạo tập đo&agrave;n l&agrave; cắt giảm 40% c&aacute;n bộ HR của nh&agrave; m&aacute;y trong khoảng thời gian chưa đầy một th&aacute;ng. Khi ng&agrave;i ph&oacute; tổng gi&aacute;m đốc phụ tr&aacute;ch HR hỏi l&atilde;nh đạo tập đo&agrave;n l&agrave;m sao họ c&oacute; thể tiếp tục duy tr&igrave; hoạt động như hiện tại với số nh&acirc;n vi&ecirc;n bị cắt giảm đ&aacute;ng kể như vậy, c&aacute;c l&atilde;nh đạo tập đo&agrave;n đồng &yacute; rằng điều đ&oacute; l&agrave; kh&ocirc;ng thể. Nh&agrave; m&aacute;y phải &ldquo;tự t&aacute;i thiết&rdquo; bằng việc h&igrave;nh th&agrave;nh sứ mệnh, mục ti&ecirc;u v&agrave; mục đ&iacute;ch mới để củng cố nỗ lực hoạt động. Từ kinh nghiệm đ&oacute; &ndash; v&agrave; kể từ đ&oacute;, những trường hợp kh&aacute;c tương tự như vậy &ndash; Rothwell &yacute; thức s&acirc;u sắc về sự chuyển h&oacute;a HR diễn ra trong c&aacute;c c&ocirc;ng ty lớn. Nhưng c&aacute;c nỗ lực n&agrave;y thường kh&ocirc;ng được lập kế hoạch tốt v&agrave; c&aacute;c l&atilde;nh đạo kh&ocirc;ng &yacute; thức đầy đủ về nhiều quyết định chiến lược khả thi đối với c&ocirc;ng t&aacute;c HR v&agrave; tổ chức m&agrave; họ c&oacute; thể đưa ra. V&igrave; vậy, cuốn s&aacute;ch n&agrave;y đưa ra những lời khuy&ecirc;n, s&aacute;ng kiến v&agrave; đề xuất thực tế nhằm n&acirc;ng cao hiệu quả của c&ocirc;ng t&aacute;c chuyển h&oacute;a HR.</p>\n\n<p><strong>Phải chăng HR đ&atilde; hết thời?</strong></p>\n\n<p>&ldquo;HR đ&atilde; chết, HR sống m&atilde;i.&rdquo; Shari Caudron đ&atilde; viết như vậy. &ldquo;Trong kỷ nguy&ecirc;n của nguồn nh&acirc;n lực n&agrave;y, HR đang c&oacute; những thay đổi chấn động. Xu hướng thu&ecirc; ngo&agrave;i (outsourcing) đang thịnh h&agrave;nh. Nếu HR dậm ch&acirc;n tại chỗ, n&oacute; sẽ diệt vong. Nhưng nếu n&oacute; thay đổi, vai tr&ograve; của n&oacute; sẽ lớn hơn bao giờ hết.&rdquo; Caudron tiếp tục m&ocirc; tả hai mặt của sự thay đổi s&acirc;u sắc n&agrave;y bằng việc khẳng định rằng: &ldquo;lĩnh vực n&agrave;y đang trong qu&aacute; tr&igrave;nh chuyển h&oacute;a to&agrave;n diện v&agrave; h&igrave;nh h&agrave;i cuối c&ugrave;ng của n&oacute; vẫn chưa được x&aacute;c định.&rdquo; Mặc d&ugrave; b&agrave;i viết n&agrave;y được c&ocirc;ng bố lần đầu ti&ecirc;n v&agrave;o năm 2003 nhưng n&oacute; vẫn tiếp tục đ&oacute;ng vai tr&ograve; l&agrave; lời k&ecirc;u gọi h&ugrave;ng hồn c&aacute;c bộ phận HR phục vụ tốt hơn nữa tổ chức của m&igrave;nh. Chức năng HR c&oacute; lịch sử phong ph&uacute; về qu&aacute; tr&igrave;nh tự chuyển h&oacute;a. Từ chỗ được gọi l&agrave; quan hệ chủ - thợ đến nh&acirc;n sự đến quản l&yacute; nguồn nh&acirc;n lực, chức năng đ&atilde; ph&aacute;t triển v&agrave; tự điều chỉnh nhằm hỗ trợ c&aacute;c tổ chức trong việc quản l&yacute; con người. Cuốn s&aacute;ch n&agrave;y đề cập đến qu&aacute; tr&igrave;nh chuyển h&oacute;a li&ecirc;n tục nhằm x&aacute;c định r&otilde; v&agrave; vạch ra chiến lược th&agrave;nh c&ocirc;ng trong lĩnh vực HR. Nhiều nh&agrave; nghi&ecirc;n cứu kh&aacute;c cũng c&oacute; mong muốn như vậy.</p>\n\n<p>Những tranh c&atilde;i li&ecirc;n tục về vai tr&ograve; v&agrave; mối quan hệ của HR với tổ chức vẫn đang diễn ra. Keith Hammonds cho rằng: &ldquo;H&atilde;y đối mặt với điều n&agrave;y: Sau khoảng 20 năm h&ugrave;ng hồn n&oacute;i về việc trở th&agrave;nh &ldquo;đối t&aacute;c chiến lược&rdquo; với &ldquo;một ghế trong b&agrave;n&rdquo; nơi những quyết định kinh doanh quan trọng được đưa ra, phần lớn c&aacute;c c&aacute;n bộ nguồn nh&acirc;n lực kh&ocirc;ng l&agrave;m được điều họ n&oacute;i. C&aacute;c c&aacute;n bộ HR v&igrave; những l&yacute; do thực tế kh&ocirc;ng phải l&agrave; c&aacute;n bộ chiến lược hay l&atilde;nh đạo.&rdquo; Hammonds đưa ra bốn l&yacute; do tại sao chức năng HR hiện kh&ocirc;ng đảm nhận được vai tr&ograve; l&atilde;nh đạo v&agrave; tạo ra gi&aacute; trị cho tổ chức. Thứ nhất, Hammonds cho rằng: &ldquo;C&aacute;c c&aacute;n bộ HR kh&ocirc;ng phải l&agrave; cột cờ trong b&oacute; đũa.&rdquo; &Ocirc;ng giải th&iacute;ch rằng phần lớn c&aacute;c nh&agrave; quản l&yacute; đảm nhận tr&aacute;ch nhiệm HR sau khi được đ&aacute;nh gi&aacute; l&agrave; ngang h&agrave;ng với c&aacute;c nh&agrave; quản l&yacute; kh&aacute;c trong c&aacute;c bộ phận kh&aacute;c của tổ chức. Thứ hai, &ocirc;ng n&oacute;i rằng: &ldquo;HR chạy theo năng suất thay v&igrave; gi&aacute; trị.&rdquo; N&oacute;i như vậy kh&ocirc;ng c&oacute; nghĩa rằng năng suất lao động l&agrave; điều xấu. Tr&ecirc;n thực tế, n&oacute; l&agrave; mối quan t&acirc;m ch&iacute;nh khi tổ chức vận h&agrave;nh trong một thị trường ng&agrave;y c&agrave;ng cạnh tranh gay gắt. Nhưng b&ecirc;n cạnh việc kiểm so&aacute;t chi ph&iacute;, bộ phận HR phải th&uacute;c đẩy c&aacute;c hoạt động của n&oacute; th&agrave;nh t&aacute;c động tạo ra gi&aacute; trị cho tổ chức th&ocirc;ng qua con người. Thứ ba, Hammonds khẳng định: &ldquo;HR kh&ocirc;ng l&agrave;m việc cho bạn&rdquo; m&agrave; l&agrave;m việc cho ch&iacute;nh n&oacute; v&agrave; c&aacute;c hoạt động ti&ecirc;u chuẩn của n&oacute; hướng đến tất cả mọi người v&agrave; tạo ra c&aacute;ch thức ứng xử thống nhất đối với c&aacute;c vấn đề ph&aacute;t sinh từ người lao động. Thứ tư, &ldquo;ph&ograve;ng yếu thế th&igrave; kh&ocirc;ng c&oacute; HR (v&agrave; ngược lại).&rdquo; Trong qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển li&ecirc;n tục của m&igrave;nh, HR được coi l&agrave; th&agrave;nh phần xấu nhưng kh&ocirc;ng thể kh&ocirc;ng c&oacute;. Thậm ch&iacute;, một người thậm ch&iacute; c&ograve;n n&oacute;i HR l&agrave; th&agrave;nh phần xấu kh&ocirc;ng đ&aacute;ng c&oacute;. Sự h&igrave;nh th&agrave;nh của chức năng n&agrave;y đ&aacute;ng được thảo luận ở đ&acirc;y</p>\n\n<p>Một trong những nh&acirc;n vật lịch sử quan trọng trong cuộc thảo luận n&agrave;y l&agrave; Peter Drucker. Sự ra đi của &ocirc;ng năm 2005 ở tuổi 95 thực sự đ&aacute;nh dấu sự kết th&uacute;c của một kỷ nguy&ecirc;n. &Ocirc;ng c&oacute; ảnh hưởng s&acirc;u sắc đối với ng&agrave;nh quản l&yacute; hiện đại. Một trong những minh chứng về ảnh hưởng của &ocirc;ng đối với HR l&agrave; một chương trong cuốn s&aacute;ch kinh điển The Practice of Management (Hoạt động quản l&yacute;) xuất bản năm 1954 c&oacute; ti&ecirc;u đề &ldquo;Phải chăng quản l&yacute; nh&acirc;n sự đ&atilde; hết thời?&rdquo;. Drucker xuất sắc ở chỗ l&agrave; một trong những người đầu ti&ecirc;n đưa ra một vấn đề li&ecirc;n tục t&aacute;i xuất hiện trong v&ograve;ng 50 năm qua. Vấn đề đ&oacute; xoay quanh vai tr&ograve; thực v&agrave; vai tr&ograve; mong muốn của quản l&yacute; nguồn nh&acirc;n lực (HRM(1)), từng được gọi l&agrave; quản trị nh&acirc;n sự. Drucker nhận x&eacute;t trong cuốn s&aacute;ch xuất bản năm 1954 rằng c&aacute;c c&aacute;n bộ HR thường xuy&ecirc;n ph&agrave;n n&agrave;n họ kh&ocirc;ng nhận được sự k&iacute;nh trọng, kh&ocirc;ng c&oacute; vị thế m&agrave; họ đ&aacute;ng được hưởng v&agrave; kh&ocirc;ng được coi l&agrave; th&agrave;nh vi&ecirc;n ch&iacute;nh thức của bộ phận quản l&yacute; cấp cao.</p>\n\n<p>Vấn đề m&agrave; Drucker đ&atilde; đề cập đến nay đ&atilde; ph&aacute;t triển th&agrave;nh tư duy về chuyển h&oacute;a HR. Chuyển h&oacute;a HR c&oacute; nhiều &yacute; nghĩa kh&aacute;c nhau &ndash; v&agrave; l&agrave; một thuật ngữ đang trong qu&aacute; tr&igrave;nh được định nghĩa. Nhưng n&oacute; thường được hiểu l&agrave;: &ldquo;Một nỗ lực triệt để nhằm thay đổi tư duy, x&aacute;c định v&agrave; định vị lại vai tr&ograve; của HR trong tổ chức.&rdquo; Mặc d&ugrave; hướng tư duy lại c&oacute; thể ho&agrave;n to&agrave;n kh&aacute;c nhau tuỳ thuộc v&agrave;o nhu cầu của từng tổ chức m&agrave; trong đ&oacute; HR được chuyển h&oacute;a nhưng một trong những c&aacute;ch chung c&aacute;c tổ chức thực hiện l&agrave; giảm đ&aacute;ng kể số c&aacute;n bộ HR v&agrave; sau đ&oacute; buộc bộ phận n&agrave;y phải thiết lập lại hệ thống điều h&agrave;nh. Việc thiết lập lại đồng nghĩa với việc đưa ra một sứ mệnh v&agrave; danh s&aacute;ch c&aacute;c nhiệm vụ, mục ti&ecirc;u mới c&oacute; thể đạt v&agrave; đo lường được. Thất bại trong việc chuyển h&oacute;a HR c&oacute; thể dẫn đến việc thu&ecirc; ngo&agrave;i to&agrave;n bộ nh&acirc;n sự trong tổ chức hoặc ở mức tối thiểu l&agrave; việc mất năng lực tạo ra bất kỳ t&aacute;c động t&iacute;ch cực n&agrave;o đối với c&aacute;c kết quả của tổ chức. Ngo&agrave;i việc thực hiện chức năng truyền thống của HR, c&aacute;c c&aacute;n bộ HR hiện nay chia sẻ tr&aacute;ch nhiệm n&agrave;y. Mục ti&ecirc;u của chuyển h&oacute;a HR l&agrave; định vị lại HR nhằm gi&uacute;p n&oacute; ph&aacute;t huy được tiềm năng to lớn trong bối cảnh nguồn nh&acirc;n lực ng&agrave;y c&agrave;ng c&oacute; &yacute; nghĩa quan trọng trong tổ chức.</p>\n\n<p>Nhiều nghi&ecirc;n cứu đ&atilde; được thực hiện trong những năm qua nhằm chi tiết h&oacute;a c&aacute;c năng lực m&agrave; bộ phận HR cần c&oacute;. Những nghi&ecirc;n cứu n&agrave;y chỉ ra sự cần thiết của HR nhằm ph&aacute;t triển c&aacute;c lĩnh vực chuy&ecirc;n m&ocirc;n từ kiến thức kinh doanh, kỹ năng tư vấn đến s&aacute;ng kiến về nguồn lực con người. Những nghi&ecirc;n cứu đ&oacute; cũng đ&atilde; đạt được một số tiến bộ nhưng vẫn c&ograve;n nhiều việc phải l&agrave;m nếu c&aacute;c nh&agrave; thực h&agrave;nh HR muốn c&oacute; vai tr&ograve; l&atilde;nh đạo thực sự trong tổ chức. Những nh&agrave; thực h&agrave;nh HR sẽ c&oacute; một tương lai đầy hứa hẹn nếu họ sẵn s&agrave;ng l&agrave;m điều đ&oacute;.</p>\n\n<p>Nhưng chức năng v&agrave; ảnh hưởng của HR đối với một tổ chức kh&ocirc;ng thể bị sa v&agrave;o lối m&ograve;n n&agrave;y. John Boudreau nhận định rằng bộ phận HR &ldquo;l&agrave; một tổ chức độc đ&aacute;o trong c&ocirc;ng ty. N&oacute; ph&aacute;t hiện ra nhiều điều về hoạt động kinh doanh th&ocirc;ng qua lăng k&iacute;nh con người v&agrave; t&agrave;i năng. Đ&oacute; l&agrave; một cơ hội tạo ra lợi thế cạnh tranh.&rdquo; Đối với quan điểm mới n&agrave;y, c&aacute;c t&aacute;c giả tin rằng bộ phận HR c&oacute; khả năng tạo ra gi&aacute; trị gia tăng bằng c&aacute;ch tự chuyển h&oacute;a th&agrave;nh sự li&ecirc;n kết độc đ&aacute;o c&aacute;c chiến lược, ch&iacute;nh s&aacute;ch v&agrave; dịch vụ giao dịch theo y&ecirc;u cầu của tổ chức. Sự chuyển h&oacute;a đ&oacute; đ&ograve;i hỏi một quan điểm l&atilde;nh đạo mới. Nhưng chuyển h&oacute;a phải tạo ra sự bền vững v&agrave; định vị lại HR một c&aacute;ch chiến lược hơn thay v&igrave; duy tr&igrave; quan điểm v&acirc;ng lời, đề ph&ograve;ng rủi ro v&agrave; quan li&ecirc;u về HR.</p>\n\n<p>Ch&iacute;nh v&igrave; vậy, cuốn s&aacute;ch n&agrave;y d&agrave;nh cho những người muốn ph&aacute;t triển, tư duy v&agrave; c&oacute; c&aacute;ch l&agrave;m mới trong lĩnh vực HR. S&aacute;ch l&agrave; hướng dẫn nhằm vạch ra v&agrave; triển khai một chương tr&igrave;nh h&agrave;nh động mới để tạo ra sự kết nối giữa bộ phận HR v&agrave; th&agrave;nh c&ocirc;ng của tổ chức. N&oacute; hướng dẫn cung cấp th&ocirc;ng tin quan trọng về c&aacute;c xu hướng v&agrave; vấn đề th&uacute;c đẩy chuyển h&oacute;a HR, &yacute; nghĩa của sự chuyển h&oacute;a đ&oacute;, những việc cần l&agrave;m để thực hiện, c&aacute;ch thức thiết lập v&agrave; quản l&yacute; đường lối cho sự thay đổi đ&oacute; v&agrave; c&aacute;ch đ&aacute;nh gi&aacute; th&agrave;nh c&ocirc;ng của n&oacute;.</p>\n\n<p>Cuốn s&aacute;ch cung cấp cho c&aacute;c l&atilde;nh đạo HR lời khuy&ecirc;n về những quyết định chiến lược m&agrave; họ c&oacute; thể đưa ra. Ch&uacute;ng ta sẽ n&oacute;i về những quyết định chiến lược n&agrave;y trong hai bối cảnh: thứ nhất l&agrave; bối cảnh hoạt động căn bản của tổ chức hoặc những th&aacute;ch thức về hoạt động, chuyển h&oacute;a hoạt động. Thứ hai l&agrave; bối cảnh HR cần th&iacute;ch ứng để đạt được sự chuyển h&oacute;a nguồn nh&acirc;n lực.</p>\n\n<p>Cuốn s&aacute;ch n&agrave;y đưa ra một số trường hợp nghi&ecirc;n cứu nhằm minh hoạ c&aacute;ch thức c&aacute;c c&aacute;n bộ HR xử l&yacute; những xu hướng ảnh hưởng đến tổ chức của họ. Trong mỗi trường hợp, ch&uacute;ng ta sẽ t&igrave;m hiểu bối cảnh cho cả việc chuyển h&oacute;a hoạt động kinh doanh v&agrave; chuyển h&oacute;a nguồn nh&acirc;n lực.</p>\n\n<p><strong>Sự cần thiết của cuốn s&aacute;ch n&agrave;y</strong></p>\n\n<p>C&oacute; nhiều l&yacute; do quan trọng để c&aacute;c tổ chức quan t&acirc;m đến việc chuyển h&oacute;a nguồn nh&acirc;n lực. Mỗi l&yacute; do đều li&ecirc;n hệ trực tiếp với sự cần thiết của cuốn s&aacute;ch n&agrave;y. Trước hết, chuyển h&oacute;a nguồn nh&acirc;n lực c&oacute; thể l&agrave; trung t&acirc;m của sự thay đổi to&agrave;n bộ tổ chức. X&eacute;t cho c&ugrave;ng, bản chất của con người v&agrave; c&aacute;ch thức họ được quản l&yacute; ảnh hưởng đến th&agrave;nh c&ocirc;ng của tổ chức. Điều n&agrave;y đặc biệt đ&uacute;ng trong thời kỳ kinh tế tri thức chi phối v&agrave; ng&agrave;y c&agrave;ng trở th&agrave;nh nền tảng cho việc x&aacute;c định hướng đi cho tổ chức v&agrave; việc mở rộng sang c&aacute;c thị trường mới. Thứ hai, chuyển h&oacute;a nguồn nh&acirc;n lực c&oacute; thể l&agrave; c&ocirc;ng cụ để c&aacute;c l&atilde;nh đạo n&acirc;ng cao v&agrave; duy tr&igrave; khả năng cạnh tranh của tổ chức giữa những điều kiện b&ecirc;n ngo&agrave;i thay đổi ch&oacute;ng mặt. Thứ ba, chuyển h&oacute;a nguồn nh&acirc;n lực c&oacute; thể l&agrave; điểm mấu chốt trong nỗ lực phối hợp c&aacute;c s&aacute;ng kiến về HR v&agrave; c&ocirc;ng t&aacute;c HR c&oacute; vai tr&ograve; quan trọng trong c&aacute;c mục ti&ecirc;u chiến lược của tổ chức.</p>\n\n<p><strong>Mục đ&iacute;ch của cuốn s&aacute;ch</strong></p>\n\n<p>Đối tượng độc giả m&agrave; cuốn s&aacute;ch hướng tới l&agrave; c&aacute;c nh&agrave; l&atilde;nh đạo HR, d&ugrave; vậy nhiều người kh&aacute;c &ndash; bao gồm c&aacute;c gi&aacute;m đốc, c&aacute;c nh&agrave; quản l&yacute; v&agrave; nh&acirc;n vi&ecirc;n c&oacute; kinh nghiệm &ndash; đều c&oacute; thể ứng dụng từ n&oacute;. Cuốn s&aacute;ch n&agrave;y l&agrave; hướng dẫn h&agrave;nh động m&ocirc; tả c&aacute;ch thức chuyển h&oacute;a nguồn nh&acirc;n lực. N&oacute; cũng c&oacute; thể được sử dụng để cải thiện hiệu quả của c&ocirc;ng t&aacute;c HR trong việc th&uacute;c đẩy c&aacute;c mục ti&ecirc;u kinh doanh v&agrave; c&aacute;ch thức c&aacute;c lĩnh vực chuy&ecirc;n m&ocirc;n về nguồn nh&acirc;n lực hỗ trợ lẫn nhau</p>\n\n<p><strong>Sơ lược về nội dung</strong></p>\n\n<p>Cuốn s&aacute;ch gồm 8 chương chia th&agrave;nh 3 phần. Phần 1 c&oacute; ti&ecirc;u đề &ldquo;Chuyển h&oacute;a nguồn nh&acirc;n lực.&rdquo; Chương 1 đặt nền tảng cho cuốn s&aacute;ch bằng việc định nghĩa chuyển h&oacute;a nguồn nh&acirc;n lực, l&agrave;m r&otilde; sự cần thiết của n&oacute; v&agrave; tr&igrave;nh b&agrave;y c&aacute;c hướng thực hiện chuyển h&oacute;a nguồn nh&acirc;n lực. Chương 2 xem x&eacute;t c&aacute;c xu hướng c&oacute; t&aacute;c động đến nguồn nh&acirc;n lực, c&ocirc;ng sở v&agrave; HR trong 10 năm qua c&ugrave;ng c&aacute;ch thức ch&uacute;ng t&aacute;c động đến sự cần thiết phải chuyển h&oacute;a nguồn nh&acirc;n lực v&agrave; sự cần thiết phải x&aacute;c lập c&aacute;c vai tr&ograve;, phương ph&aacute;p mới cho những c&aacute;n bộ HR. Phần 2 c&oacute; ti&ecirc;u đề &ldquo;Tập trung v&agrave;o c&aacute;c xu hướng tương lai&rdquo;. Chương 3 đề cập đến c&aacute;c xu hướng tương lai ở c&ocirc;ng sở v&agrave; phương thức ch&uacute;ng t&aacute;c động đến hoạt động HR. Chương 4 xem x&eacute;t chi tiết hơn Chương 2 về c&aacute;c xu hướng tương lai trong nguồn nh&acirc;n lực n&oacute;i chung v&agrave; phương thức ch&uacute;ng t&aacute;c động đến hoạt động HR. Chương 5 t&igrave;m hiểu c&aacute;ch thức chuyển h&oacute;a HR bằng c&aacute;ch tập trung v&agrave;o c&aacute;c xu hướng tương lai.</p>\n\n<p>Phần 3 c&oacute; ti&ecirc;u đề &ldquo;Thực thi vai tr&ograve; mới của l&atilde;nh đạo HR.&rdquo; Chương 6 giải th&iacute;ch định nghĩa l&atilde;nh đạo HR, t&igrave;m hiểu c&aacute;c tư duy trước đ&acirc;y, hiện nay v&agrave; tương lai về vai tr&ograve; của HR trong tổ chức. Chương 7 tr&igrave;nh b&agrave;y c&aacute;ch thức x&acirc;y dựng c&ocirc;ng t&aacute;c l&atilde;nh đạo HR trong tổ chức. Chương 8 đưa ra những lời khuy&ecirc;n về c&aacute;ch thức thực hiện c&ocirc;ng t&aacute;c l&atilde;nh đạo HR nhằm đối ph&oacute; với xu hướng thu&ecirc; nguồn lực b&ecirc;n ngo&agrave;i.</p>\n\n<p><strong>Th&ocirc;ng tin t&aacute;c giả:</strong></p>\n\n<p><strong>TS. William J. Rothwell</strong>&nbsp;l&agrave; chuy&ecirc;n gia cao cấp về Nguồn nh&acirc;n lực, Chủ tịch C&ocirc;ng ty Rothwell &amp; Associates, Gi&aacute;o sư đ&agrave;o tạo v&agrave; ph&aacute;t triển lực lượng lao động tại Đại học bang Pennsylvania.</p>\n\n<p><strong>TS. Robert K. Prescott</strong>&nbsp;l&agrave; chuy&ecirc;n gia cao cấp về Nguồn nh&acirc;n lực, nh&agrave; nghi&ecirc;n cứu, nh&agrave; gi&aacute;o dục, nh&agrave; tư vấn v&agrave; l&agrave; Ph&oacute; gi&aacute;o sư ng&agrave;nh Quản l&yacute; nguồn nh&acirc;n lực tại Khoa Kinh tế Sau đại học Crummer, Đại học Rollins.</p>\n\n<p><strong>Maria W. Taylor</strong>&nbsp;l&agrave; Gi&aacute;m đốc giải ph&aacute;p học tập của C&ocirc;ng ty Dịch vụ Chuy&ecirc;n nghiệp Raytheon &ndash; một c&ocirc;ng ty h&agrave;ng đầu tr&ecirc;n thế giới về dịch vụ học tập v&agrave; thu&ecirc; ngo&agrave;i</p>\n', 2019, 300, 283),
(65, 28, 'Viện Quản lý PACE', 'NXB Tổng Hợp TPHCM', 'David L Rogers', 'Bìa cứng', 'Phạm Anh Tuấn', '<p>&ldquo;Mọi người đều n&oacute;i về những biến đổi trong thời đại số, nhưng đ&acirc;y mới l&agrave; cơ hội để bạn thực sự triển khai hiệu quả qu&aacute; tr&igrave;nh n&agrave;y. Rogers cung cấp một lộ tr&igrave;nh m&agrave; mọi cấp điều h&agrave;nh cần tham khảo. Nếu bạn kh&ocirc;ng tham gia v&agrave;o qu&aacute; tr&igrave;nh chuyển đổi trong doanh nghiệp, n&oacute; vẫn sẽ diễn ra! H&atilde;y đọc cuốn s&aacute;ch n&agrave;y v&agrave; sẵn s&agrave;ng cho việc cải tổ doanh nghiệp trong thời đại số&rdquo;.</p>\n', '<p>&ldquo;Mọi người đều n&oacute;i về những biến đổi trong thời đại số, nhưng đ&acirc;y mới l&agrave; cơ hội để bạn thực sự triển khai hiệu quả qu&aacute; tr&igrave;nh n&agrave;y. Rogers cung cấp một lộ tr&igrave;nh m&agrave; mọi cấp điều h&agrave;nh cần tham khảo. Nếu bạn kh&ocirc;ng tham gia v&agrave;o qu&aacute; tr&igrave;nh chuyển đổi trong doanh nghiệp, n&oacute; vẫn sẽ diễn ra! H&atilde;y đọc cuốn s&aacute;ch n&agrave;y v&agrave; sẵn s&agrave;ng cho việc cải tổ doanh nghiệp trong thời đại số&rdquo;.</p>\n\n<p><strong>Sree Sreenivasan</strong></p>\n\n<p>Gi&aacute;m đốc kỹ thuật số, Metropolitan Museum of Art</p>\n\n<p>Cuộc c&aacute;ch mạng kỹ thuật số đ&atilde; thực sự l&agrave;m đảo lộn c&aacute;c chiến lược kinh doanh truyền thống. C&aacute;c nguy&ecirc;n tắc trong kinh doanh đ&atilde; thay đổi. Trong mỗi ng&agrave;nh c&ocirc;ng nghiệp, ch&uacute;ng ta đang chứng kiến sự lan rộng của c&aacute;c c&ocirc;ng nghệ số mới, sự nổi l&ecirc;n của những nguy cơ g&acirc;y gi&aacute;n đoạn, thay thế, cũng như sự biến đổi về m&ocirc; h&igrave;nh v&agrave; quy tr&igrave;nh kinh doanh.</p>\n\n<p>Cải tổ doanh nghiệp trong thời đại số kh&ocirc;ng phải l&agrave; vấn đề về c&ocirc;ng nghệ - m&agrave; đ&oacute; l&agrave; vấn đề về chiến lược v&agrave; c&aacute;ch tư duy mới. Chuyển đổi trong thời đại kỹ thuật số đ&ograve;i hỏi doanh nghiệp phải n&acirc;ng cao nhận thức về chiến lược chứ kh&ocirc;ng phải n&acirc;ng cao chất lượng hạ tầng c&ocirc;ng nghệ th&ocirc;ng tin. Sự thật n&agrave;y được phản &aacute;nh ở vai tr&ograve; thay đổi của l&atilde;nh đạo c&ocirc;ng nghệ trong kinh doanh.</p>\n\n<p>Dựa tr&ecirc;n một thập kỷ nghi&ecirc;n cứu v&agrave; giảng dạy tại Trường kinh doanh Columbia, cũng như kinh nghiệm tư vấn cho nhiều doanh nghiệp tr&ecirc;n thế giới, Cải tổ doanh nghiệp trong thời đại số tr&igrave;nh b&agrave;y c&aacute;ch thức m&agrave; c&aacute;c doanh nghiệp c&oacute; xuất ph&aacute;t điểm từ giai đoạn trước thời đại số c&oacute; thể chuyển đổi, t&aacute;i tạo c&aacute;c kế hoạch v&agrave; chiến lược của họ, từ đ&oacute; tận dụng được những cơ hội mới trong thế giới kỹ thuật số ng&agrave;y nay.</p>\n\n<p>Rất nhiều cuốn s&aacute;ch đưa ra lời khuy&ecirc;n cho c&aacute;c c&ocirc;ng ty khởi nghiệp trong thời đại số, nhưng đ&acirc;y l&agrave; cuốn s&aacute;ch đầu ti&ecirc;n chỉ ra c&aacute;ch tiếp cận để chuyển đổi một doanh nghiệp th&agrave;nh lập từ trước thời kỳ ph&aacute;t triển của Internet, gi&uacute;p họ tiếp tục th&agrave;nh c&ocirc;ng trong thời đại số. C&oacute; thể xem đ&acirc;y l&agrave; một cẩm nang kh&ocirc;ng thể bỏ qua của c&aacute;c nh&agrave; l&atilde;nh đạo đang c&oacute; mong muốn đưa tổ chức của m&igrave;nh tiến l&ecirc;n một giai đoạn tăng trưởng c&oacute; lợi nhuận tiếp theo.</p>\n', 2019, 420, 404);
INSERT INTO `chitietsanpham` (`id`, `masp`, `nhacungcap`, `nhaxuatban`, `tacgia`, `hinhthuc`, `nguoidich`, `mota`, `noidung`, `namxuatban`, `trongluong`, `sotrang`) VALUES
(66, 29, '1980 Books', 'NXB Công Thương', 'Jonathan P Allen', 'Bìa mềm', 'Khai Nguyên', '<p>Cuốn s&aacute;ch &ldquo;Kinh doanh online&rdquo; của Johnathan P. Allen c&ograve;n được &ldquo;v&iacute; von&rdquo; l&agrave; cuốn s&aacute;ch gi&aacute;o khoa bổ &iacute;ch cung cấp c&aacute;c kiến thức cơ bản cũng như những hướng dẫn thực tế cho c&aacute;c sinh vi&ecirc;n&nbsp; việc sử dụng c&aacute;c nền tảng c&ocirc;ng nghệ để bắt tay v&agrave;o khởi nghiệp.</p>\n', '<p>Cuốn s&aacute;ch &ldquo;Kinh doanh online&rdquo; của Johnathan P. Allen c&ograve;n được &ldquo;v&iacute; von&rdquo; l&agrave; cuốn s&aacute;ch gi&aacute;o khoa bổ &iacute;ch cung cấp c&aacute;c kiến thức cơ bản cũng như những hướng dẫn thực tế cho c&aacute;c sinh vi&ecirc;n&nbsp; việc sử dụng c&aacute;c nền tảng c&ocirc;ng nghệ để bắt tay v&agrave;o khởi nghiệp.</p>\n\n<p>Gi&aacute;o sư Clyde Eir&iacute;kur Hull,Viện C&ocirc;ng nghệRochester, Mỹ sau khi đọc xong cuốn s&aacute;ch đ&atilde; phải thốt l&ecirc;n rằng:</p>\n\n<p>&ldquo;Cuối c&ugrave;ng, đ&atilde; c&oacute; một cuốn s&aacute;ch gi&aacute;o khoa gi&uacute;p ch&uacute;ng ta c&oacute; thể sử dụng để dạy sinh vi&ecirc;n khởi nghiệp kỹ thuật số!&rdquo;</p>\n\n<p>Ch&iacute;nh t&aacute;c giả cũng nhận định về vai tr&ograve; của c&ocirc;ng nghệ số đối với khởi nghiệp:</p>\n\n<p>&ldquo; Ch&uacute;ng t&ocirc;i tin rằng tự l&agrave;m chủ kinh doanh kỹ thuật số c&oacute; thể gi&uacute;p việc tạo ra c&aacute;c dự &aacute;n kinh doanh mạo hiểm mới c&oacute; nội h&agrave;m rộng v&agrave; d&acirc;n chủ hơn. Nhưng nếu chỉ ri&ecirc;ng c&ocirc;ng nghệ số sẽ kh&ocirc;ng thể khiến cho c&aacute;c thay đổi n&agrave;y xảy ra một c&aacute;ch mặc nhi&ecirc;n. Những nh&agrave; khởi nghiệp cần phải l&agrave;m việc chăm chỉ v&agrave; c&oacute; tầm nh&igrave;n để nhận ra tiềm năng của c&ocirc;ng nghệ nhằm cải thiện mọi thứ. T&ocirc;i mong rằng quyển s&aacute;ch n&agrave;y sẽ hữu &iacute;ch với c&aacute;c độc giả.&rdquo;</p>\n\n<p>Ng&agrave;y nay, sự ph&aacute;t triển như vũ b&atilde;o của c&ocirc;ng nghệ đ&atilde; tạo ra một kỷ nguy&ecirc;n khởi nghiệp mới khi c&aacute;c doanh nh&acirc;n t&igrave;m thấy c&aacute;c c&ocirc;ng cụ kỹ thuật số cho ph&eacute;p khai th&aacute;c c&aacute;c cơ hội thương mại tr&ecirc;n to&agrave;n thế giới. Đ&acirc;y l&agrave; cuốn s&aacute;ch thiết thực cung cấp cho sinh vi&ecirc;n c&aacute;c hướng dẫn của nhiều chuy&ecirc;n gia h&agrave;ng đầu về việc sử dụng c&aacute;c nền tảng c&ocirc;ng nghệ để &ldquo;dấn th&acirc;n&rdquo; v&agrave;o c&aacute;c dự &aacute;n mới.</p>\n\n<p>Với c&aacute;ch tiếp cận đ&atilde; gi&agrave;nh được giải thưởng, Johnathan P. Allen hướng dẫn độc giả trong suốt qu&aacute; tr&igrave;nh khởi nghiệp tinh gọn, thực hiện phương ph&aacute;p &quot;kỹ thuật số đầu ti&ecirc;n&quot; để khởi nghiệp. Cuốn s&aacute;ch gi&uacute;p người đọc mở rộng vốn hiểu biết n&acirc;ng cao về c&aacute;c m&ocirc; h&igrave;nh kinh doanh kỹ thuật số kh&aacute;c nhau, c&aacute;c kỹ năng ph&acirc;n t&iacute;ch cho c&aacute;c dự &aacute;n kỹ thuật số v&agrave; sự tự tin để chuyển từ nguy&ecirc;n mẫu sang sản phẩm. Johnathan P. Allen dường như đang khai mở cả thế giới số &ldquo;phức tạp&rdquo; th&agrave;nh những phần đơn giản, dễ hiểu nhất gi&uacute;p c&aacute;c nh&agrave; khởi nghiệp tiếp cận c&ocirc;ng nghệ số đ&uacute;ng hướng v&agrave; thực h&agrave;nh c&aacute;c thử nghiệm một c&aacute;ch thuần thục, g&oacute;p phần l&agrave;m tăng khả năng th&agrave;nh c&ocirc;ng của &yacute; tưởng kinh doanh.</p>\n\n<p>&ldquo;Tinh thần khởi nghiệp v&agrave; cải tiến thực sự c&oacute; nội h&agrave;m lớn hơn trong kỷ nguy&ecirc;n số mới, cần phải c&oacute; nhiều người tin rằng họ cũng c&oacute; thể trở th&agrave;nh những nh&agrave; khởi nghiệp số mới. Tương lai của c&ocirc;ng nghệ số phải d&agrave;nh cho nhiều người, kh&ocirc;ng phải cho số &iacute;t. Ch&uacute;c may mắn!&rdquo;</p>\n\n<p>&nbsp;Điều đặc biệt ở cuốn s&aacute;ch c&ograve;n bao gồm phần b&agrave;i tập khuyến kh&iacute;ch người đọc thảo luận, thực h&agrave;nh nhanh&nbsp; ch&oacute;ng k&eacute;o theo việc học hỏi nhanh ch&oacute;ng.</p>\n', 2019, 300, 480),
(67, 30, 'NXB Trẻ', 'NXB Trẻ', 'Robert T Kiyosaki', 'Bìa mềm', 'Không có', '<p>Người gi&agrave;u kh&ocirc;ng l&agrave;m việc v&igrave; tiền. Họ bắt tiền l&agrave;m việc cho họ. Chấp nhận thất bại l&agrave; bước đầu của th&agrave;nh c&ocirc;ng? Quyền lực của sự lựa chọn! Những b&agrave;i học kh&ocirc;ng c&oacute; trong nh&agrave; trường. H&atilde;y bắt đầu từ h&ocirc;m nay &ldquo;để kh&ocirc;ng c&oacute; tiền vẫn tạo ra tiền&rdquo;&hellip;.</p>\n', '<p>Người gi&agrave;u kh&ocirc;ng l&agrave;m việc v&igrave; tiền. Họ bắt tiền l&agrave;m việc cho họ. Chấp nhận thất bại l&agrave; bước đầu của th&agrave;nh c&ocirc;ng? Quyền lực của sự lựa chọn! Những b&agrave;i học kh&ocirc;ng c&oacute; trong nh&agrave; trường. H&atilde;y bắt đầu từ h&ocirc;m nay &ldquo;để kh&ocirc;ng c&oacute; tiền vẫn tạo ra tiền&rdquo;&hellip;.</p>\n', 2018, 200, 195),
(68, 31, 'NXB Trẻ', 'NXB Trẻ', 'Robin Sharma', 'Bìa mềm', 'Nguyễn Minh Thiên Kim', '<p>Nh&agrave; L&atilde;nh Đạo Kh&ocirc;ng Chức Danh</p>\n\n<p>Suốt hơn 15 năm, Robin Sharma đ&atilde; thầm lặng chia sẻ với những c&ocirc;ng ty trong danh s&aacute;ch Fortune 500 v&agrave; nhiều người si&ecirc;u gi&agrave;u kh&aacute;c một c&ocirc;ng thức th&agrave;nh c&ocirc;ng đ&atilde; gi&uacute;p &ocirc;ng trở th&agrave;nh một trong những nh&agrave; cố vấn l&atilde;nh đạo được theo đuổi nhiều nhất thế giới. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n Sharma c&ocirc;ng bố c&ocirc;ng thức độc quyền n&agrave;y với bạn, để bạn c&oacute; thể đạt được những g&igrave; tốt nhất, đồng thời gi&uacute;p tổ chức của bạn c&oacute; thể c&oacute; những bước đột ph&aacute; đến một cấp độ th&agrave;nh c&ocirc;ng mới trong thời đại thi&ecirc;n biến vạn h&oacute;a như hiện nay.</p>\n', '<p>Nh&agrave; L&atilde;nh Đạo Kh&ocirc;ng Chức Danh</p>\n\n<p>Suốt hơn 15 năm, Robin Sharma đ&atilde; thầm lặng chia sẻ với những c&ocirc;ng ty trong danh s&aacute;ch Fortune 500 v&agrave; nhiều người si&ecirc;u gi&agrave;u kh&aacute;c một c&ocirc;ng thức th&agrave;nh c&ocirc;ng đ&atilde; gi&uacute;p &ocirc;ng trở th&agrave;nh một trong những nh&agrave; cố vấn l&atilde;nh đạo được theo đuổi nhiều nhất thế giới. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n Sharma c&ocirc;ng bố c&ocirc;ng thức độc quyền n&agrave;y với bạn, để bạn c&oacute; thể đạt được những g&igrave; tốt nhất, đồng thời gi&uacute;p tổ chức của bạn c&oacute; thể c&oacute; những bước đột ph&aacute; đến một cấp độ th&agrave;nh c&ocirc;ng mới trong thời đại thi&ecirc;n biến vạn h&oacute;a như hiện nay.</p>\n\n<p>Trong cuốn s&aacute;ch&nbsp;Nh&agrave; L&atilde;nh Đạo Kh&ocirc;ng Chức Danh, bạn sẽ học được:</p>\n\n<ul>\n	<li>L&agrave;m thế n&agrave;o để l&agrave;m việc v&agrave; tạo ảnh hưởng với mọi người như một si&ecirc;u sao, bất chấp bạn đang ở vị tr&iacute; n&agrave;o</li>\n	<li>Một phương ph&aacute;p để nhận biết v&agrave; nắm bắt cơ hội v&agrave;o những thời điểm thay đổi</li>\n	<li>Những b&iacute; mật thật sự của sự đổi mới</li>\n	<li>Một chiến lược tức thời để x&acirc;y dựng đội nh&oacute;m tuyệt vời v&agrave; trở th&agrave;nh một nh&agrave; cung cấp ngoạn mục của kh&aacute;ch h&agrave;ng</li>\n	<li>Những thủ thuật cứng rắn gi&uacute;p trở n&ecirc;n mạnh mẽ cả về thể chất lẫn tinh thần để c&oacute; thể đi đầu trong lĩnh vực của bạn</li>\n	<li>Những phương thức thực tế để đ&aacute;nh bại sự căng thẳng, x&acirc;y dựng một &yacute; ch&iacute; bất bại, giải ph&oacute;ng năng lượng, v&agrave; c&acirc;n bằng cuộc sống c&aacute; nh&acirc;n</li>\n</ul>\n\n<p>Bất kể bạn l&agrave;m g&igrave; trong tổ chức v&agrave; cuộc sống hiện tại của bạn như thế n&agrave;o, một thực tế quan trọng duy nhất l&agrave; bạn c&oacute; khả năng thể hiện năng lực l&atilde;nh đạo. Cho d&ugrave; sự nghiệp hiện tại của bạn đang ở đ&acirc;u, bạn vẫn lu&ocirc;n cần phải thể hiện những khả năng tột đỉnh của m&igrave;nh. Cuốn s&aacute;ch n&agrave;y sẽ hướng dẫn bạn l&agrave;m thế n&agrave;o để khai th&aacute;c tối đa khả năng đ&oacute;, cũng như thay đổi cuộc sống v&agrave; thế giới xung quanh bạn.</p>\n', 2017, 280, 270),
(69, 32, 'NXB Trẻ', 'NXB Trẻ', 'Robin Sharma, Phạm Anh Tuấn', 'Bìa mềm', 'Không có', '<p>Điều Vĩ Đại Đời Thường (Đổi B&agrave;i Mới)</p>\n\n<p>H&agrave;nh tr&igrave;nh trở n&ecirc;n vĩ đại khởi đầu từ những điều thật giản dị v&agrave; gần gũi, cũng giống như h&agrave;nh tr&igrave;nh vạn dặm khởi đầu từ một bước ch&acirc;n giản đơn. 101 c&acirc;u chuyện v&agrave; &yacute; tưởng của t&aacute;c giả trong quyển s&aacute;ch n&agrave;y ch&iacute;nh l&agrave; 101 b&agrave;i học minh chứng cho triết l&yacute; đơn sơ ấy.<br />\nKh&ocirc;ng c&oacute; cuộc sống n&agrave;o l&agrave; ho&agrave;n hảo, tất cả ch&uacute;ng ta phải đối mặt với nhiều thử th&aacute;ch, từ đơn giản đến nghi&ecirc;m trọng&hellip; Nhưng ch&uacute;ng ta đều c&oacute; quyền lựa chọn vượt l&ecirc;n mọi nghịch cảnh để vươn tới vị tr&iacute; cao nhất v&agrave; tốt nhất của cuộc sống.</p>\n', '<p>Điều Vĩ Đại Đời Thường (Đổi B&agrave;i Mới)</p>\n\n<p>H&agrave;nh tr&igrave;nh trở n&ecirc;n vĩ đại khởi đầu từ những điều thật giản dị v&agrave; gần gũi, cũng giống như h&agrave;nh tr&igrave;nh vạn dặm khởi đầu từ một bước ch&acirc;n giản đơn. 101 c&acirc;u chuyện v&agrave; &yacute; tưởng của t&aacute;c giả trong quyển s&aacute;ch n&agrave;y ch&iacute;nh l&agrave; 101 b&agrave;i học minh chứng cho triết l&yacute; đơn sơ ấy.<br />\nKh&ocirc;ng c&oacute; cuộc sống n&agrave;o l&agrave; ho&agrave;n hảo, tất cả ch&uacute;ng ta phải đối mặt với nhiều thử th&aacute;ch, từ đơn giản đến nghi&ecirc;m trọng&hellip; Nhưng ch&uacute;ng ta đều c&oacute; quyền lựa chọn vượt l&ecirc;n mọi nghịch cảnh để vươn tới vị tr&iacute; cao nhất v&agrave; tốt nhất của cuộc sống.</p>\n', 2013, 240, 276),
(70, 33, 'Simon And Schuster', 'Simon And Schuster', 'Dale Carnegie , Arthur R Pell', 'Bìa mềm', 'Không có', '<p>Available for the first time ever in trade paperback, Dale Carnegie&#39;s enduring classic, the inspirational personal development guide that shows how to achieve lifelong success. One of the top-selling books of all time, &quot;How to Win Friends &amp; Influence People&quot; has sold more than 15 million copies in all its editions.</p>\n', '<p>Available for the first time ever in trade paperback, Dale Carnegie&#39;s enduring classic, the inspirational personal development guide that shows how to achieve lifelong success. One of the top-selling books of all time, &quot;How to Win Friends &amp; Influence People&quot; has sold more than 15 million copies in all its editions.</p>\n', 1998, 295, 260),
(71, 34, 'NXB Hà Nội', 'NXB Hà Nội', 'Ngoa', 'Bìa mềm', 'Không có', '<p>Được biết đến l&agrave; fanpage g&acirc;y cười quốc d&acirc;n với gần&nbsp;<strong>1,800,000 lượt th&iacute;ch</strong>, Ngoa đ&atilde; mang tới tiếng cười kh&ocirc;ng ngớt v&agrave; c&aacute;i nh&igrave;n kh&ocirc;ng hề giả tr&acirc;n về cuộc đời cho giới trẻ.</p>\n', '<p>Được biết đến l&agrave; fanpage g&acirc;y cười quốc d&acirc;n với gần&nbsp;<strong>1,800,000 lượt th&iacute;ch</strong>, Ngoa đ&atilde; mang tới tiếng cười kh&ocirc;ng ngớt v&agrave; c&aacute;i nh&igrave;n kh&ocirc;ng hề giả tr&acirc;n về cuộc đời cho giới trẻ.</p>\n\n<p>Được y&ecirc;u mến từ đầu năm 2016, cho tới b&acirc;y giờ, Ngoa c&ugrave;ng phong c&aacute;ch độc đ&aacute;o thẳng-thật-kh&ocirc;ng biết sợ vẫn l&agrave; nơi thu h&uacute;t sự quan t&acirc;m lớn từ cộng đồng mạng.</p>\n\n<p><strong>Giới thiệu nội dung s&aacute;ch:</strong></p>\n\n<p>Thanh xu&acirc;n kh&ocirc;ng ai nhắc bạn rằng:</p>\n\n<p>Kh&ocirc;ng ai muốn nghe bạn ba hoa về thu nhập ngo&agrave;i nh&acirc;n vi&ecirc;n b&aacute;n bảo hiểm.</p>\n\n<p>Kh&ocirc;ng ai chướng mắt với ảnh check-in du lịch của bạn hơn &ocirc;ng sếp ngồi nh&agrave;.</p>\n\n<p>Thanh xu&acirc;n kh&ocirc;ng ai nhắc bạn những thứ bạn n&ecirc;n l&agrave;m, kh&ocirc;ng ai nhắc bạn những thứ bạn n&ecirc;n tin, kh&ocirc;ng ai nhắc bạn chuyện quan trọng nhất để kh&ocirc;ng l&atilde;ng ph&iacute; tuổi trẻ l&agrave; đừng sống nhạt nho&agrave;.</p>\n\n<p>H&atilde;y sống mặn m&agrave; v&agrave; tận hưởng sự vui vẻ đi!</p>\n\n<p>Sống thật vui vẻ với một ng&agrave;y trời mưa tầm t&atilde;.</p>\n\n<p>Sống thật vui vẻ với người họ h&agrave;ng giả tr&acirc;n.</p>\n\n<p>Sống thật vui vẻ với người bạn nợ nần dai như đỉa.</p>\n\n<p>Sống thật vui vẻ với deadline d&iacute; đầu c&ugrave;ng người sếp kh&oacute; t&iacute;nh&hellip;</p>\n\n<p>&ldquo;Đừng nhạt nữa&rdquo; - Cu&ocirc;́n sách với sự nh&uacute;ng tay từ NGOA, t&aacute;c giả của định nghĩa &ldquo;Tuesday&rdquo; (người thứ ba) g&acirc;y b&atilde;o cộng đồng mạng, sẽ mang đến cho bạn những tiếng cười không ngớt cũng như nguồn năng lượng t&iacute;ch cực gi&uacute;p bạn đối mặt với độ phũ của cuộc đời.</p>\n\n<p>Bởi v&igrave; canh nhạt th&igrave; th&ecirc;m muối.</p>\n\n<p>Đời nhạt qu&aacute; th&igrave; đọc s&aacute;ch của NGOA.</p>\n', 2021, 300, 264),
(72, 35, 'NXB Trẻ', 'NXB Trẻ', 'Cù Mai Công', 'Bìa mềm', 'Không có', '<p>S&agrave;i G&ograve;n Một Thuở &quot;D&acirc;n &Ocirc;ng Tạ Đ&oacute;!&quot;</p>\n\n<p>L&agrave; một c&acirc;y b&uacute;t gi&agrave;u kinh nghiệm. một người con lớn l&ecirc;n ở khu &Ocirc;ng Tạ S&agrave;i G&ograve;n v&agrave;o đ&uacute;ng giai đoạn lịch sử nhiều biến động, t&aacute;c giả C&ugrave; Mai C&ocirc;ng đ&atilde; d&agrave;nh nhiều t&acirc;m huyết cho t&aacute;c phẩm &quot;S&agrave;i G&ograve;n một thuở &#39; &quot;d&acirc;n &Ocirc;ng Tạ đ&oacute;!&quot;</p>\n', '<p>S&agrave;i G&ograve;n Một Thuở &quot;D&acirc;n &Ocirc;ng Tạ Đ&oacute;!&quot;</p>\n\n<p>L&agrave; một c&acirc;y b&uacute;t gi&agrave;u kinh nghiệm. một người con lớn l&ecirc;n ở khu &Ocirc;ng Tạ S&agrave;i G&ograve;n v&agrave;o đ&uacute;ng giai đoạn lịch sử nhiều biến động, t&aacute;c giả C&ugrave; Mai C&ocirc;ng đ&atilde; d&agrave;nh nhiều t&acirc;m huyết cho t&aacute;c phẩm &quot;S&agrave;i G&ograve;n một thuở &#39; &quot;d&acirc;n &Ocirc;ng Tạ đ&oacute;!&quot;</p>\n\n<p>S&agrave;i G&ograve;n l&agrave; vậy, h&agrave;o sảng đ&oacute;n những người d&acirc;n &nbsp;tứ xứ tụ về, rồi ch&iacute;nh những con người lại khi sống gần nhau, lại l&agrave;m n&ecirc;n bản sắc cho một địa danh. N&oacute;i &quot;d&acirc;n &Ocirc;ng Tạ đ&oacute;!&quot; l&agrave; người ta nhớ tới khu chợ Bắc với những m&oacute;n đặc sản gi&ograve; chả, mắm t&ocirc;m, b&uacute;n chả..., nhớ bước ch&acirc;n v&agrave;o Nam của người d&acirc;n Bắc 54 c&ugrave;ng kh&aacute;t vọng sống v&agrave; vươn l&ecirc;n mạnh mẽ, nhớ đại đồn Ch&iacute; H&ograve;a, những trại l&iacute;nh thời trước 75, những khu nghĩa địa với bao tin đồn hư hư thực thực, đặc biệt, đ&acirc;y l&agrave; nơi quy tụ những văn nghệ sĩ nổi tiếng trong nhiều lĩnh vực, từ trước đến nay. &quot;Những người mu&ocirc;n năm ấy&quot; c&oacute; khi nay kh&ocirc;ng c&ograve;n, hoặc đ&atilde; đi mu&ocirc;n phương, nhưng một thời họ l&agrave; &quot;d&acirc;n &Ocirc;ng Tạ&quot;, g&oacute;p phần tạo n&eacute;t ri&ecirc;ng cho v&ugrave;ng đất n&agrave;y.</p>\n\n<p>&Ocirc;ng Tạ l&agrave; một phần của S&agrave;i G&ograve;n một thuở. C&oacute; nhiều người trẻ S&agrave;i G&ograve;n h&ocirc;m nay vẫn c&ograve;n nghe &quot;ng&atilde; ba &ocirc;ng Tạ&quot;, thế nhưng &quot;&Ocirc;ng Tạ&quot; thực ra l&agrave; ai, v&igrave; sao được lấy t&ecirc;n đặt t&ecirc;n ng&atilde; ba, t&ecirc;n chợ... th&igrave; nhiều người kh&ocirc;ng r&otilde;. Lịch sử v&agrave; x&atilde; hội một thời được ghi lại tỉ mỉ v&agrave; đầy th&uacute; vị qua ng&ograve;i b&uacute;t của t&aacute;c giả vốn l&agrave; nh&agrave; văn - nh&agrave; b&aacute;o từng trải. Anh nhớ tỉ mỉ đến từng ng&otilde; nhỏ, qu&aacute;n cafe, những nh&acirc;n vật một thời phong v&acirc;n... với t&igrave;nh y&ecirc;u mến v&agrave; tr&acirc;n trọng.&nbsp;<br />\nCuốn s&aacute;ch đ&atilde; g&oacute;p một mảng m&agrave;u độc đ&aacute;o trong những t&aacute;c phẩm về S&agrave;i G&ograve;n.</p>\n', 2021, 200, 174),
(73, 36, 'Penguin Books', 'ARROW BOOKS LTD', 'Harper Lee', 'Bìa mềm', 'Không có', '<p>&#39;Shoot all the bluejays you want, if you can hit &#39;em, but remember it&#39;s a sin to kill a mockingbird&#39;. A lawyer&#39;s advice to his children as he defends the real mockingbird of Harper Lee&#39;s classic novel - a black man charged with the rape of a white girl.&nbsp;</p>\n', '<p>&#39;Shoot all the bluejays you want, if you can hit &#39;em, but remember it&#39;s a sin to kill a mockingbird&#39;. A lawyer&#39;s advice to his children as he defends the real mockingbird of Harper Lee&#39;s classic novel - a black man charged with the rape of a white girl. Through the young eyes of Scout and Jem Finch, Harper Lee explores with exuberant humour the irrationality of adult attitudes to race and class in the Deep South of the thirties. The conscience of a town steeped in prejudice, violence and hypocrisy is pricked by the stamina of one man&#39;s struggle for justice. But the weight of history will only tolerate so much. &quot;To Kill a Mockingbird&quot; is a coming-of-age story, an anti-racist novel, a historical drama of the Great Depression and a sublime example of the Southern writing tradition.</p>\n', 2010, 280, 320),
(74, 37, 'Penguin Books', 'Penguin Putnam Inc', 'Miguel de Cervantes Saavedra,Walter Starkie', 'Bìa mềm', 'Không có', '<p>Don Quixote, a lanky scarecrow of a man with his withered face and lantern jaw, dons his rusty armour and mounts his ramshackle steed, Rozinante. With lance couched he still rides through our lives, followed by his potbellied squire Sancho Panza.</p>\n', '<p>Don Quixote, a lanky scarecrow of a man with his withered face and lantern jaw, dons his rusty armour and mounts his ramshackle steed, Rozinante. With lance couched he still rides through our lives, followed by his potbellied squire Sancho Panza. With these words, Walter Starkie launches the introduction to his highly esteemed translation and abridgment of Cervantes s great classic a book that has enchanted generations of readers throughout the world. Brimming with humor, rich in idealism and earthy common sense, vivid in its characterizations of men and women from every walk of life nobles, priests, impassioned damsels, simple country girls, rogues, and romantics &quot;Don Quixote,&quot; in this zestful translation, will win many new friends.&quot;</p>\n', 2013, 260, 560),
(75, 38, 'Random House', 'Bantam Doubleday Dell Publishing Group Inc', 'Jane Austen', 'Bìa mềm', 'Không có', '<p>Austen&#39;s comedy of manners--one of the most popular novels of all time--features splendidly civilized sparring between the proud Mr. Darcy and the prejudiced Elizabeth Bennet as they play out their spirited courtship in a series of 18-century drawing-room intrigues. Reissue.</p>\n', '<p>Austen&#39;s comedy of manners--one of the most popular novels of all time--features splendidly civilized sparring between the proud Mr. Darcy and the prejudiced Elizabeth Bennet as they play out their spirited courtship in a series of 18-century drawing-room intrigues. Reissue.</p>\n', 1983, 180, 352),
(76, 39, 'Penguin Books', 'Penguin Classics', 'Jane Austen', 'Bìa mềm', 'Không có', '<p>Part of Penguin&#39;s beautiful hardback Clothbound Classics series, designed by the award-winning Coralie Bickford-Smith, these delectable and collectible editions are bound in high-quality colourful, tactile cloth with foil stamped into the design.&nbsp;</p>\n', '<p>Part of Penguin&#39;s beautiful hardback Clothbound Classics series, designed by the award-winning Coralie Bickford-Smith, these delectable and collectible editions are bound in high-quality colourful, tactile cloth with foil stamped into the design. Beautiful, clever, rich - and single - Emma Woodhouse is perfectly content with her life and sees no need for either love or marriage. Nothing, however, delights her more than interfering in the romantic lives of others. But when she ignores the warnings of her good friend Mr Knightley and attempts to arrange a suitable match for her prot&eacute;g&eacute;e Harriet Smith, her carefully laid plans soon unravel and have consequences that she never expected. With its imperfect but charming heroine and its witty and subtle exploration of relationships, Emma is often seen as Jane Austen&#39;s most flawless work.</p>\n', 2010, 596, 512),
(77, 40, 'Penguin Books', 'Penguin Books', 'William Golding', 'Bìa mềm', 'Không có', '<p><strong>Before&nbsp;<em>The Hunger Games</em>&nbsp;there was&nbsp;<em>Lord of the Flies</em><br />\n<br />\nLord of the Flies</strong>&nbsp;remains as provocative today as when it was first published in 1954, igniting passionate debate with its startling, brutal portrait of human nature. Though critically acclaimed, it was largely ignored upon its initial publication. Yet soon it became a cult favorite among both students and literary critics who compared it to J.D. Salinger&#39;s&nbsp;<strong>The Catcher in the Rye</strong>&nbsp;in its influence on modern thought and literature.<br />\n&nbsp;</p>\n', '<p><strong>Before&nbsp;<em>The Hunger Games</em>&nbsp;there was&nbsp;<em>Lord of the Flies</em><br />\n<br />\nLord of the Flies</strong>&nbsp;remains as provocative today as when it was first published in 1954, igniting passionate debate with its startling, brutal portrait of human nature. Though critically acclaimed, it was largely ignored upon its initial publication. Yet soon it became a cult favorite among both students and literary critics who compared it to J.D. Salinger&#39;s&nbsp;<strong>The Catcher in the Rye</strong>&nbsp;in its influence on modern thought and literature.<br />\n<br />\nWilliam Golding&#39;s compelling story about a group of very ordinary small boys marooned on a coral island has become a modern classic. At first it seems as though it is all going to be great fun; but the fun before long becomes furious and life on the island turns into a nightmare of panic and death. As ordinary standards of behaviour collapse, the whole world the boys know collapses with them&mdash;the world of cricket and homework and adventure stories&mdash;and another world is revealed beneath, primitive and terrible.Labeled a parable, an allegory, a myth, a morality tale, a parody, a political treatise, even a vision of the apocalypse,&nbsp;<strong>Lord of the Flies</strong>&nbsp;has established itself as a true classic.</p>\n\n<p><br />\n&quot;<strong><em>Lord of the Flies</em></strong>&nbsp;is one of my favorite books. That was a big influence on me as a teenager, I still read it every couple of years.&quot;&nbsp;<br />\n<strong>&mdash;Suzanne Collins, author of&nbsp;<em>The Hunger Games</em></strong><br />\n<br />\n&quot;As exciting, relevant, and thought-provoking now as it was when Golding published it in 1954.&quot;<br />\n<strong>&mdash;<em>Stephen King</em></strong></p>\n', 2003, 136, 208),
(78, 41, 'Penguin Books', 'Viking', 'Vladimir Nabokov', 'Bìa mềm', 'Không có', '<p>&#39;Lolita, light of my life, fire of my loins. My sin, my soul. Lo-lee-ta: the tip of my tongue taking a trip of three steps down the palate to tap, at three, on the teeth. Lo. Lee. Ta&#39;. Humbert Humbert is a middle-aged, frustrated college professor. In love with his landlady&#39;s twelve-year-old daughter Lolita, he&#39;ll do anything to possess her.</p>\n', '<p>&#39;Lolita, light of my life, fire of my loins. My sin, my soul. Lo-lee-ta: the tip of my tongue taking a trip of three steps down the palate to tap, at three, on the teeth. Lo. Lee. Ta&#39;. Humbert Humbert is a middle-aged, frustrated college professor. In love with his landlady&#39;s twelve-year-old daughter Lolita, he&#39;ll do anything to possess her.</p>\n\n<p>Unable and unwilling to stop himself, he is prepared to commit any crime to get what he wants. Is he in love or insane? A silver-tongued poet or a pervert? A tortured soul or a monster? Or is he all of these?</p>\n', 2011, 360, 368),
(79, 42, 'ZenBooks', 'NXB Đà Nẵng', 'Mai Lan Hương', 'Bìa mềm', 'Không có', '<p>Động Từ Bất Quy Tắc &amp; Ngữ Ph&aacute;p Tiếng Anh Căn Bản</p>\n\n<p>Việc học một thứ ng&ocirc;n ngữ kh&aacute;c kh&ocirc;ng phải tiếng mẹ đẻ, bao giờ cũng đem lại cho bạn nhiều kh&oacute; khăn, v&igrave; thế &nbsp;để học tốt tiếng Anh hay bất k&igrave; thứ tiếng n&agrave;o kh&aacute;c đ&ograve;i hỏi bạn phải c&oacute; sự ki&ecirc;n tr&igrave; v&agrave; t&agrave;i liệu học tập tốt.</p>\n', '<p>Động Từ Bất Quy Tắc &amp; Ngữ Ph&aacute;p Tiếng Anh Căn Bản</p>\n\n<p>Việc học một thứ ng&ocirc;n ngữ kh&aacute;c kh&ocirc;ng phải tiếng mẹ đẻ, bao giờ cũng đem lại cho bạn nhiều kh&oacute; khăn, v&igrave; thế &nbsp;để học tốt tiếng Anh hay bất k&igrave; thứ tiếng n&agrave;o kh&aacute;c đ&ograve;i hỏi bạn phải c&oacute; sự ki&ecirc;n tr&igrave; v&agrave; t&agrave;i liệu học tập tốt.<br />\n<br />\nĐộng Từ Bất Quy Tắc &amp; Ngữ Ph&aacute;p Tiếng Anh Căn Bản&nbsp;l&agrave; t&agrave;i liệu học Tiếng Anh căn bản d&agrave;nh cho người mới bắt đầu học tiếng Anh.<br />\n<br />\nCuốn s&aacute;ch gồm hai phần:<br />\n<br />\nPhần I: &nbsp;Động từ bất quy tắc gồm bảng động từ bất quy tắc được chia theo 4: dạng hiện tại, qu&aacute; khứ, qu&aacute; khứ ph&acirc;n từ, hiện tại ph&acirc;n từ c&oacute; k&egrave;m nghĩa của từ.<br />\n<br />\nPhần II: Những kiến thức Anh văn cơ bản giới thiệu kiến thức về c&aacute;c thể c&acirc;u.</p>\n', 2016, 100, 39),
(80, 43, 'ZenBooks', 'NXB Đà Nẵng', 'Mai Lan Hương , Hà Thanh Uyên', 'Bìa mềm', 'Không có', '<p>C&aacute;c Lỗi Thường Gặp Trong Tiếng Anh</p>\n\n<p>Nội dung cuốn s&aacute;ch được tr&igrave;nh b&agrave;y đồng nhất. Trước ti&ecirc;n, lỗi thường gặp được đề cập v&agrave; tr&igrave;nh b&agrave;y trong c&acirc;u để c&aacute;c bạn c&oacute; thể nhận biết trước khi n&oacute; được sửa lại cho đ&uacute;ng; sau đ&oacute; c&acirc;u sai được thay bằng c&acirc;u đ&uacute;ng ngay b&ecirc;n dưới; cuối c&ugrave;ng l&agrave; lời giảng giải hoặc c&aacute;ch d&ugrave;ng cụ thể. Phần lưu &yacute; được th&ecirc;m v&agrave;o khi cần mở rộng hoặc nhấn mạnh điểm n&agrave;o đ&oacute;.</p>\n', '<p>C&aacute;c Lỗi Thường Gặp Trong Tiếng Anh</p>\n\n<p>Nội dung cuốn s&aacute;ch được tr&igrave;nh b&agrave;y đồng nhất. Trước ti&ecirc;n, lỗi thường gặp được đề cập v&agrave; tr&igrave;nh b&agrave;y trong c&acirc;u để c&aacute;c bạn c&oacute; thể nhận biết trước khi n&oacute; được sửa lại cho đ&uacute;ng; sau đ&oacute; c&acirc;u sai được thay bằng c&acirc;u đ&uacute;ng ngay b&ecirc;n dưới; cuối c&ugrave;ng l&agrave; lời giảng giải hoặc c&aacute;ch d&ugrave;ng cụ thể. Phần lưu &yacute; được th&ecirc;m v&agrave;o khi cần mở rộng hoặc nhấn mạnh điểm n&agrave;o đ&oacute;.</p>\n\n<p>Cuốn s&aacute;ch n&agrave;y c&oacute; thể kh&ocirc;ng bao qu&aacute;t hết tất cả c&aacute;c lỗi thường gặp trong Tiếng Anh, nhưng c&aacute;c v&iacute; dụ trong s&aacute;ch l&agrave; những lỗi thường gặp nhất.</p>\n', 2017, 300, 235),
(81, 44, 'ZenBooks', 'NXB Đà Nẵng', 'Mai Lan Hương , Hà Thanh Uyên', 'Bìa mềm', 'Không có', '<p>S&aacute;ch Giải Th&iacute;ch Ngữ Ph&aacute;p Tiếng Anh Với B&agrave;i Tập V&agrave; Đ&aacute;p &Aacute;n được bi&ecirc;n soạn th&agrave;nh 9 chương, đề cập đến những vấn đề ngữ ph&aacute;p từ cơ bản đến n&acirc;ng cao.</p>\n', '<p>S&aacute;ch Giải Th&iacute;ch Ngữ Ph&aacute;p Tiếng Anh Với B&agrave;i Tập V&agrave; Đ&aacute;p &Aacute;n được bi&ecirc;n soạn th&agrave;nh 9 chương, đề cập đến những vấn đề ngữ ph&aacute;p từ cơ bản đến n&acirc;ng cao.</p>\n\n<p>Nội dung c&aacute;c chương được bi&ecirc;n soạn dựa tr&ecirc;n ngữ ph&aacute;p tiiếng Anh hiện đại, giải th&iacute;ch cặn kẽ, r&otilde; r&agrave;ng c&aacute;c c&aacute;ch d&ugrave;ng v&agrave; quy luật m&agrave; người học cần nắm vững: đồng thời chỉ ra những quy luật kh&aacute;c nhau giữa tiếng anh của người Anh (British English) v&agrave; tiếng Anh của người Mỹ (American English).</p>\n\n<p>Cuối mỗi phần ngữ ph&aacute;p đều c&oacute; b&agrave;i tập đa dạng để gi&uacute;p người học nắm vững phần l&yacute; thuyết. Hơn nữa, hiện nay h&igrave;nh thức thi trắc nghiệm đang được &aacute;p dụng trong c&aacute;c kỳ thi, n&ecirc;n trong s&aacute;ch n&agrave;y ch&uacute;ng t&ocirc;i bi&ecirc;n soạn một số phần b&agrave;i tập trắc nghiệm để đ&aacute;p ứng nhu cầu của người học trong t&igrave;nh h&igrave;nh mới.</p>\n\n<p>Hy vọng Giải Th&iacute;ch Ngữ Ph&aacute;p Tiếng Anh sẽ l&agrave; một quyển s&aacute;ch thiết thực, đ&aacute;p ứng y&ecirc;u cầu học, &ocirc;n tập v&agrave; n&acirc;ng cao tr&igrave;nh độ ngữ ph&aacute;p cho người học v&agrave; l&agrave; quyển s&aacute;ch tham khảo bổ &iacute;ch d&agrave;nh cho gi&aacute;o vi&ecirc;n.</p>\n', 2020, 550, 560),
(82, 45, 'MCBooks', 'NXB Đại Học Quốc Gia Hà Nội', 'The Windy', 'Bìa mềm', 'Không có', '<p>Nh&agrave; ng&ocirc;n ngữ học nổi tiếng D.A.Wilkins đ&atilde; n&oacute;i rằng &ldquo;Without grammar, very little can be conveyed; without vocabulary, nothing can be conveyed.&rdquo; (Kh&ocirc;ng c&oacute; ngữ ph&aacute;p, rất &iacute;t th&ocirc;ng tin c&oacute; thể được truyền đạt; Kh&ocirc;ng c&oacute; từ vựng, kh&ocirc;ng một th&ocirc;ng tin n&agrave;o c&oacute; thể được truyền đạt cả). V&igrave; thế, trong việc học một ngoại ngữ, th&igrave; từ vựng c&oacute; thể xem như c&aacute;c tế b&agrave;o nhỏ h&igrave;nh th&agrave;nh n&ecirc;n khả năng sử dụng ngoại ngữ của người học.</p>\n', '<p>Nh&agrave; ng&ocirc;n ngữ học nổi tiếng D.A.Wilkins đ&atilde; n&oacute;i rằng &ldquo;Without grammar, very little can be conveyed; without vocabulary, nothing can be conveyed.&rdquo; (Kh&ocirc;ng c&oacute; ngữ ph&aacute;p, rất &iacute;t th&ocirc;ng tin c&oacute; thể được truyền đạt; Kh&ocirc;ng c&oacute; từ vựng, kh&ocirc;ng một th&ocirc;ng tin n&agrave;o c&oacute; thể được truyền đạt cả). V&igrave; thế, trong việc học một ngoại ngữ, th&igrave; từ vựng c&oacute; thể xem như c&aacute;c tế b&agrave;o nhỏ h&igrave;nh th&agrave;nh n&ecirc;n khả năng sử dụng ngoại ngữ của người học.</p>\n\n<p>C&aacute;c bạn đang băn khoăn chưa biết n&ecirc;n học từ vựng như thế n&agrave;o để c&oacute; thể ghi nhớ được nhiều từ v&agrave; l&acirc;u nhất. Dưới đ&acirc;y l&agrave; những phương ph&aacute;p học m&agrave; c&aacute;c bạn c&oacute; thể &aacute;p dụng để học từ vựng hiệu quả nhất.</p>\n\n<p>1. Học từ vựng theo chủ đề<br />\nThay v&igrave; học ri&ecirc;ng lẻ từng từ rời rạc, nếu c&aacute;c bạn biết nh&oacute;m ch&uacute;ng v&agrave;o c&ugrave;ng một chủ đề th&igrave; đảm bảo việc học thuộc sẽ trở n&ecirc;n đơn giản hơn rất nhiều lu&ocirc;n. C&oacute; vốn từ vựng theo chủ đề, khi l&agrave;m b&agrave;i thi n&oacute;i hoặc viết theo một chủ đề n&agrave;o đ&oacute; bạn sẽ ph&aacute;t huy được tối đa vốn từ vựng. Khiến b&agrave;i n&oacute;i v&agrave; b&agrave;i viết của bạn tr&ocirc;i chảy v&agrave; nhiều &yacute; tưởng. V&igrave; vậy, việc học từ theo chủ đề cũng giống như một mũi t&ecirc;n tr&uacute;ng hai đ&iacute;ch vậy, vừa gi&uacute;p ch&uacute;ng ta biết th&ecirc;m nhiều từ mới, vừa c&oacute; nhiều từ vựng v&agrave; &yacute; hay để triển khai cho b&agrave;i thi được tốt hơn.</p>\n\n<p>2. Sử dụng h&igrave;nh ảnh, &acirc;m thanh hay c&acirc;u chuyện để ghi nhớ từ vựng nhanh<br />\nThực tế cho thấy, c&aacute;ch m&agrave; nhiều người thường hay học từ vựng đ&oacute; l&agrave; viết từ vựng đ&oacute; k&egrave;m nghĩa ra giấy thật nhiều lần để ghi nhớ. Tuy nhi&ecirc;n, c&aacute;ch n&agrave;y c&oacute; thể hiệu quả tức thời nhưng về l&acirc;u về d&agrave;i, nếu kh&ocirc;ng sử dụng lại th&igrave; chắc chắn ta sẽ qu&ecirc;n từ. Ngược lại, nếu sử dụng những h&igrave;nh ảnh, &acirc;m thanh hay c&acirc;u chuyện ấn tượng th&igrave; c&oacute; thể khiến n&atilde;o bộ ch&uacute;ng ta nhớ rất m&atilde;i. C&aacute;ch thức học n&agrave;y cũng v&ocirc; c&ugrave;ng đơn giản, khi ch&uacute;ng ta học một từ mới, h&atilde;y cố gắng tự nghĩ &yacute; tưởng v&agrave; minh họa cho n&oacute; bằng h&igrave;nh ảnh ngộ nghĩnh, khi học một nh&oacute;m từ vựng theo chủ đề, h&atilde;y cố gắng sắp xếp ch&uacute;ng v&agrave;o th&agrave;nh một đoạn văn, kể về một c&acirc;u chuyện th&uacute; vị.</p>\n\n<p>3. Học từ vựng qua b&agrave;i h&aacute;t, phim ảnh</p>\n\n<p>Nhiều l&uacute;c do t&acirc;m l&yacute; nghĩ học tập l&agrave; một g&aacute;nh nặng, bắt buộc phải học m&agrave; khiến ch&uacute;ng ta mất đi niềm vui trong việc tiếp nhận tri thức. Nếu c&aacute;c bạn c&oacute; thể giải tỏa t&acirc;m l&yacute; đ&oacute; bằng việc nghe nhạc hay xem phim th&igrave; sẽ khiến từ mới &ldquo;v&agrave;o đầu&rdquo; dễ d&agrave;ng hơn m&agrave; kh&ocirc;ng khiến ta ch&aacute;n nản khi học. C&aacute;c bạn n&ecirc;n học từ vựng bằng những phim từ mức độ thấp đến cao. Rất nhiều bộ phim hay để học tiếng Anh như: Extra English, Friends, How I met your mother?...</p>\n\n<p>4. Lu&ocirc;n c&oacute; một cuốn sổ tay ghi từ mới<br />\nCuốn sổ n&agrave;y sẽ cực k&igrave; hữu &iacute;ch cho bạn trong việc học mọi l&uacute;c mọi nơi. Mỗi ng&agrave;y bạn hay ghi v&agrave;o quyển sổ đ&oacute; những từ mới học được bao gồm nghĩa, họ từ v&agrave; ng&agrave;y n&agrave;o cũng mở ra đọc v&agrave; học lại như vậy từ vựng đ&oacute; sẽ kh&ocirc;ng bao giờ c&oacute; thể qu&ecirc;n được.</p>\n\n<p>5. Tập sử dụng c&aacute;c từ mới v&agrave; &ocirc;n tập thường xuy&ecirc;n<br />\nSau khi học c&aacute;c từ mới, bạn n&ecirc;n tập sử dụng ch&uacute;ng h&agrave;ng</p>\n', 2019, 280, 260),
(83, 46, 'MCBooks', 'NXB Hồng Đức', 'The Windy', 'Bìa mềm', 'Không có', '<p>C&ugrave;ng với thời đại hội nhập, tiếng Anh lu&ocirc;n được coi l&agrave; một chiếc cầu nối đến với kho t&agrave;ng tri thức dồi d&agrave;o của nh&acirc;n loại, v&agrave; l&agrave; ng&ocirc;n ngữ ch&iacute;nh trong c&aacute;c t&igrave;nh huống giao tiếp h&agrave;ng ng&agrave;y v&agrave; c&ocirc;ng việc. V&igrave; vậy học tiếng Anh l&agrave; một việc cần thiết v&agrave; quan trọng. Trong tiếng Anh, ngữ ph&aacute;p giữ vai tr&ograve; quan trọng, l&agrave; nền tảng cơ bản cho việc học c&aacute;c kỹ năng kh&aacute;c.</p>\n', '<p>C&ugrave;ng với thời đại hội nhập, tiếng Anh lu&ocirc;n được coi l&agrave; một chiếc cầu nối đến với kho t&agrave;ng tri thức dồi d&agrave;o của nh&acirc;n loại, v&agrave; l&agrave; ng&ocirc;n ngữ ch&iacute;nh trong c&aacute;c t&igrave;nh huống giao tiếp h&agrave;ng ng&agrave;y v&agrave; c&ocirc;ng việc. V&igrave; vậy học tiếng Anh l&agrave; một việc cần thiết v&agrave; quan trọng. Trong tiếng Anh, ngữ ph&aacute;p giữ vai tr&ograve; quan trọng, l&agrave; nền tảng cơ bản cho việc học c&aacute;c kỹ năng kh&aacute;c.</p>\n\n<p>Khi bắt tay v&agrave;o học ngữ ph&aacute;p tiếng Anh bạn kh&ocirc;ng biết phải bắt đầu từ đ&acirc;u v&agrave; c&oacute; qu&aacute; nhiều ngữ ph&aacute;p khiến bạn hoang mang. Việc tham gia học tập tại cũng trung t&acirc;m thường đắt đỏ v&agrave; kh&ocirc;ng chắc chắn mang lại hiệu quả? V&igrave; vậy một cuốn s&aacute;ch tự học ngữ ph&aacute;p v&agrave; c&oacute; hướng dẫn giải th&iacute;ch chi tiết l&agrave; giải ph&aacute;p tuyệt vời nhất cho bạn tự học tiếng Anh tại nh&agrave;.</p>\n\n<p>Hướng dẫn sử dụng ngữ ph&aacute;p tiếng Anh được nh&oacute;m t&aacute;c giả The Windy&nbsp; c&ocirc;ng ty cổ phần s&aacute;ch MCBooks tỉ mỉ bi&ecirc;n soạn vớ những chủ điểm ngữ ph&aacute;p quan trọng nhất trong tiếng Anh, l&yacute; thuyết v&agrave; b&agrave;i tập thực h&agrave;nh b&aacute;m s&aacute;t kiến thức tr&ecirc;n lớp đặc biệt d&agrave;nh cho học sinh, sinh vi&ecirc;n muốn nắm vững ngữ ph&aacute;p tiếng Anh.</p>\n\n<p><em><strong>Ưu điểm của cuốn s&aacute;ch Hướng dẫn sử dụng ngữ ph&aacute;p tiếng Anh</strong></em></p>\n\n<p>Cuốn s&aacute;ch chọn lọc những chủ điểm ngữ ph&aacute;p tiếng Anh quan trọng v&agrave; cần thiết nhất</p>\n\n<p>Hướng dẫn sử dụng ngữ ph&aacute;p tiếng Anh bao gồm 15 chương ngữ ph&aacute;p: danh từ, động từ, t&iacute;nh từ&hellip;.đến c&aacute;c th&igrave; trong tiếng Anh, c&aacute;c loại c&acirc;u trong tiếng Anh&hellip;Đ&oacute; ch&iacute;nh l&agrave; những chủ điể ngữ ph&aacute;p quan trong v&agrave; cần thiết nhất.</p>\n\n<p>Giải th&iacute;ch &ndash; ph&acirc;n biệt &ndash; hướng dẫn sử dụng đ&uacute;ng ngữ ph&aacute;p trong những trường hợp kh&aacute;c nhau</p>\n\n<p>Ở mỗi đề mục ngữ ph&aacute;p sẽ c&oacute; giải th&iacute;ch v&agrave; hướng dẫn c&aacute;ch sử dụng chi tiết.</p>\n\n<p>Tr&igrave;nh b&agrave;y ngắn gọn &ndash; đ&uacute;ng trọng t&acirc;m &ndash; dễ hiểu &ndash; dễ nhớ</p>\n\n<p>Kh&ocirc;ng đi v&agrave;o qu&aacute; nhiều việc giải th&iacute;ch v&agrave; tr&igrave;nh b&agrave;y c&aacute;c t&igrave;nh huống như trong c&aacute;c s&aacute;ch nước ngo&agrave;i, cuốn hướng dẫn sử dụng ngữ ph&aacute;p tiếng Anh tập trung nếu c&aacute;c ngữ ph&aacute;p v&agrave; c&aacute;ch sử dụng như một cẩm nang tổng kết cho người học tra cứu v&agrave; ghi nhớ.</p>\n\n<p>Mỗi một từ mới, cấu tr&uacute;c mới đều c&oacute; h&igrave;nh ảnh minh họa k&egrave;m dịch lu&ocirc;n sang nghĩa tiếng Việt gi&uacute;p người học dễ d&agrave;ng theo d&otilde;i, ghi nhớ v&agrave; học được th&ecirc;m cả nghĩa của từ.</p>\n\n<p>Gi&uacute;p tự kiểm tra &ndash; đ&aacute;nh gi&aacute; kiến thức qua phần b&agrave;i tập thực h&agrave;nh k&egrave;m đ&aacute;p &aacute;n</p>\n\n<p>Cuối mỗi đề mục đều c&oacute; rất nhiều b&agrave;i tập ứng dụng c&aacute;c hạng mục ngữ ph&aacute;p vừa học để nhắc lại v&agrave; ghi nhớ s&acirc;u kiến thức. C&aacute;c b&agrave;i tập đ&oacute; v&ocirc; c&ugrave;ng hiệu quả trong việc tự kiểm tra đ&aacute;nh gi&aacute; kiến thứ cho người tự học ngữ ph&aacute;p tiếng Anh.</p>\n\n<p>Dễ d&agrave;ng chinh phục điểm cao trong phần thi ngữ ph&aacute;p tiếng Anh</p>\n\n<p>Với những điểm tuyệt vời như vậy, bạn học cuốn s&aacute;ch Hướng dẫn sử dụng ngữ ph&aacute;p tiếng Anh ho&agrave;n to&agrave;n c&oacute; thể tự tin chinh phục điểm cao trong c&aacute;c phần thi ngữ ph&aacute;p tiếng Anh tr&ecirc;n trường.</p>\n', 2020, 300, 288),
(84, 47, 'ZenBooks', 'NXB Đà Nẵng', 'Mai Lan Hương , Hà Thanh Uyên', 'Bìa mềm', 'Không có', '<p>Cuốn s&aacute;ch sẽ gi&uacute;p c&aacute;c bạn phần n&agrave;o giải quyết được c&aacute;c kh&oacute; khăn v&agrave; sai s&oacute;t thường gặp khi sử dụng tiếng Anh, để từ đ&oacute; ho&agrave;n thiện dần kỹ năng sử dụng tiếng Anh của m&igrave;nh. S&aacute;ch bao gồm phần l&yacute; thuyết chi tiết để c&aacute;c bạn trau dồi kiến thức v&agrave; phần b&agrave;i tập từ mức độ dễ đến kh&oacute; để c&aacute;c bạn tự luyện tập.</p>\n', '<p>Cuốn s&aacute;ch sẽ gi&uacute;p c&aacute;c bạn phần n&agrave;o giải quyết được c&aacute;c kh&oacute; khăn v&agrave; sai s&oacute;t thường gặp khi sử dụng tiếng Anh, để từ đ&oacute; ho&agrave;n thiện dần kỹ năng sử dụng tiếng Anh của m&igrave;nh. S&aacute;ch bao gồm phần l&yacute; thuyết chi tiết để c&aacute;c bạn trau dồi kiến thức v&agrave; phần b&agrave;i tập từ mức độ dễ đến kh&oacute; để c&aacute;c bạn tự luyện tập.</p>\n', 2019, 200, 196),
(85, 48, 'ZenBooks', 'NXB Đà Nẵng', 'Mai Lan Hương , Hà Thanh Uyên', 'Bìa mềm', 'Không có', '<p><strong><em>Cụm động từ&nbsp;</em></strong>l&agrave; cụm từ bao gồm một động từ kết hợp với một hoặc hai tiểu từ (trạng từ hoặc giới từ). Nghĩa của cụm động từ thường rất kh&aacute;c với nghĩa của động từ hoặc tiểu từ độc lập, chẳng hạn như&nbsp;<strong>look after</strong><em><strong>&nbsp;(</strong></em><em>chăm s&oacute;c)</em>,&nbsp;<strong>turn up</strong>&nbsp;<em>(xuất hiện)</em>,&nbsp;<strong>put off</strong><em>&nbsp;(ho&atilde;n lại)</em>,&nbsp;<strong>break out</strong>&nbsp;<em>(b&ugrave;ng nổ)</em>,&nbsp;<strong>let down</strong>&nbsp;<em>(l&agrave;m thất vọng)</em>. Do đ&oacute;, khi học cụm động từ ch&uacute;ng ta phải học nghĩa của cả cụm từ v&agrave; học c&aacute;ch ứng dụng cụm động từ theo ngữ cảnh.</p>\n', '<p><strong><em>Cụm động từ&nbsp;</em></strong>l&agrave; cụm từ bao gồm một động từ kết hợp với một hoặc hai tiểu từ (trạng từ hoặc giới từ). Nghĩa của cụm động từ thường rất kh&aacute;c với nghĩa của động từ hoặc tiểu từ độc lập, chẳng hạn như&nbsp;<strong>look after</strong><em><strong>&nbsp;(</strong></em><em>chăm s&oacute;c)</em>,&nbsp;<strong>turn up</strong>&nbsp;<em>(xuất hiện)</em>,&nbsp;<strong>put off</strong><em>&nbsp;(ho&atilde;n lại)</em>,&nbsp;<strong>break out</strong>&nbsp;<em>(b&ugrave;ng nổ)</em>,&nbsp;<strong>let down</strong>&nbsp;<em>(l&agrave;m thất vọng)</em>. Do đ&oacute;, khi học cụm động từ ch&uacute;ng ta phải học nghĩa của cả cụm từ v&agrave; học c&aacute;ch ứng dụng cụm động từ theo ngữ cảnh.</p>\n\n<p>Nh&igrave;n chung, x&eacute;t về mặt văn phạm lẫn ngữ nghĩa, cụm động từ l&agrave; phần g&acirc;y kh&ocirc;ng &iacute;t kh&oacute; khăn cho người học tiếng Anh. Nhằm gi&uacute;p người học c&oacute; th&ecirc;m t&agrave;i liệu để tham khảo, tra cứu v&agrave; học cụm động từ, nh&oacute;m t&aacute;c giải đ&atilde; bi&ecirc;n soạn cuốn s&aacute;ch&nbsp;<strong>Cụm Động Từ Tiếng Anh Th&ocirc;ng Dụng</strong>.</p>\n\n<p>S&aacute;ch&nbsp;<strong>Cụm Động Từ Tiếng Anh Th&ocirc;ng Dụng&nbsp;</strong>được bi&ecirc;n soạn theo dạng từ điển. C&aacute;c cụm từ trong s&aacute;ch được sắp xếp theo động từ ch&iacute;nh v&agrave; thứ tự chữ c&aacute;i của tiểu từ. Trong từng mục từ, trước ti&ecirc;n cụm động từ được giải th&iacute;ch bằng tiếng Anh, sau đ&oacute; l&agrave; phần tiếng Việt v&agrave; từ kết (được in đậm). C&oacute; v&iacute; dụ minh họa trong từng mục từ, nhằm gi&uacute;p người học &aacute;p dụng cụm động từ theo ngữ cảnh.</p>\n', 2021, 337, 293),
(86, 49, 'CTY TNHH GIÁO DỤC TIẾN BỘ', 'NXB Thế Giới', 'Nguyễn Văn Hiệp', 'Bìa mềm', 'Không có', '<p><strong>CUỐN S&Aacute;CH N&Agrave;Y SẼ PH&Ugrave; HỢP VỚI AI?</strong><br />\nHọc sinh cấp 3 đang muốn tăng 1-3 điểm thi THPTQG</p>\n\n<p>Sinh vi&ecirc;n cần th&ecirc;m &iacute;t nhất 200-300 điểm TOEIC để đủ điều kiện ra trường</p>\n\n<p>Người đi l&agrave;m muốn mở rộng vốn từ để đọc hiểu t&agrave;i liệu, viết email v&agrave; giao tiếp cơ bản bằng tiếng Anh</p>\n\n<p>Người trung tuổi hoặc lớn tuổi muốn học lại tiếng Anh từ đầu</p>\n\n<p>C&aacute;c bạn nhỏ hơn với sự hướng dẫn của bố mẹ</p>\n', '<p><strong>CUỐN S&Aacute;CH N&Agrave;Y SẼ PH&Ugrave; HỢP VỚI AI?</strong><br />\nHọc sinh cấp 3 đang muốn tăng 1-3 điểm thi THPTQG</p>\n\n<p>Sinh vi&ecirc;n cần th&ecirc;m &iacute;t nhất 200-300 điểm TOEIC để đủ điều kiện ra trường</p>\n\n<p>Người đi l&agrave;m muốn mở rộng vốn từ để đọc hiểu t&agrave;i liệu, viết email v&agrave; giao tiếp cơ bản bằng tiếng Anh</p>\n\n<p>Người trung tuổi hoặc lớn tuổi muốn học lại tiếng Anh từ đầu</p>\n\n<p>C&aacute;c bạn nhỏ hơn với sự hướng dẫn của bố mẹ</p>\n\n<p><strong>TẠI SAO CUỐN S&Aacute;CH TIẾNG ANH N&Agrave;Y HIỆU QUẢ?</strong></p>\n\n<p>&Acirc;M THANH TƯƠNG TỰ</p>\n\n<p>Gi&uacute;p nạp 1 từ mới chỉ trong 30s. Dựa tr&ecirc;n nguy&ecirc;n l&yacute; &ldquo;bắc cầu tạm&rdquo; từ tiếng Anh sang tiếng mẹ đẻ</p>\n\n<p>TRUYỆN CH&Ecirc;M<br />\nMỗi b&agrave;i c&oacute; 1 đoạn truyện cười gồm 80% tiếng Việt, 20% từ mới tiếng Anh. Bạn sẽ đo&aacute;n được v&agrave; nhớ ngay nghĩa của từ th&ocirc;ng qua văn cảnh.</p>\n\n<p>HỌC PH&Aacute;T &Acirc;M CHUẨN TẠI NH&Agrave;<br />\nTừng l&agrave; gi&aacute;o vi&ecirc;n dạy ph&aacute;t &acirc;m n&ecirc;n ch&uacute;ng m&igrave;nh hiểu rất r&otilde; người Việt hay bị sai ở đ&acirc;u. Do đ&oacute; trong mỗi b&agrave;i học dạy ph&aacute;t &acirc;m đều c&oacute; lưu &yacute; những lỗi sai người Việt hay mắc phải. Từng video sẽ chỉ cho bạn cụ thể khẩu h&igrave;nh của từng &acirc;m, bạn chỉ cần nhại theo l&agrave; được. Phần n&agrave;y thực sự rất chi tiết, kh&ocirc;ng sản phẩm hay kho&aacute; học ph&aacute;t &acirc;m n&agrave;o hiện tại tr&ecirc;n thị trường c&oacute; được. 1500 b&agrave;i học ph&aacute;t &acirc;m trị gi&aacute; 800.000đ n&agrave;y sẽ được tặng k&egrave;m miễn ph&iacute; khi c&aacute;c bạn đặt s&aacute;ch h&ocirc;m nay.</p>\n\n<p>T&Aacute;C GIẢ CUỐN S&Aacute;CH L&Agrave; AI?<br />\n&ldquo;Khi mới bắt đầu học tiếng Anh m&igrave;nh cũng như bạn, lu&ocirc;n ao ước tr&ecirc;n đời tồn tại B&aacute;nh mỳ gi&uacute;p tr&iacute; nhớ của Doraemon hoặc m&igrave;nh sinh ra ở c&aacute;c nước n&oacute;i tiếng Anh để đỡ phải học tiếng Anh. Qua một thời gian d&agrave;i t&igrave;m kiếm v&agrave; nghi&ecirc;n cứu, ho&aacute; ra việc học tiếng Anh ho&agrave;n to&agrave;n dễ d&agrave;ng nếu c&oacute; được phương ph&aacute;p đ&uacute;ng.</p>\n\n<p>Kỉ lục học ng&agrave;y xưa của m&igrave;nh l&agrave; 117 từ một ng&agrave;y v&agrave; chỉ qu&ecirc;n 8 từ sau 1 tuần kiểm tra lại. V&igrave; thế m&igrave;nh đ&atilde; l&agrave;m cuốn s&aacute;ch tiếng Anh n&agrave;y l&agrave; để cho những người &ldquo;kh&ocirc;ng c&oacute; năng khiếu ngoại ngữ&rdquo; cũng c&oacute; thể giỏi như d&acirc;n học chuy&ecirc;n. Tin m&igrave;nh đi. Bạn c&oacute; thể l&agrave;m được.&rdquo;</p>\n', 2021, 350, 496),
(87, 50, 'BÁO TIỀN PHONG', 'Báo Sinh Viên VN - Hoa Học Trò', 'Nhiều Tác Giả', 'Bìa mềm', 'Không có', '<p><strong>LỚP HỌC MẬT NGỮ NGOẠI TRUYỆN</strong></p>\n\n<p><strong>VŨ TRỤ SONG NGƯ - NHỮNG PHA XỬ L&Yacute; ĐI V&Agrave;O L&Ograve;NG BIỂN CỦA SONG NGƯ V&Agrave; &ldquo;ĐỒNG BỌN&rdquo;</strong></p>\n\n<p><strong>Sau ngoại truyện về Song Tử được tween hưởng ứng nhiệt liệt th&igrave; cộng đồng Song Ngư h&atilde;y phấn kh&iacute;ch đi th&ocirc;i v&igrave; cuốn ngoại truyện n&agrave;y d&agrave;nh cho c&aacute;c bạn. V&agrave; nếu thuộc cung kh&aacute;c, th&igrave; quyển s&aacute;ch n&agrave;y cũng c&oacute; rất nhiều điều để bạn kh&aacute;m ph&aacute; về ch&iacute;nh m&igrave;nh đấy, tuyệt c&uacute; chưa!</strong></p>\n\n<p><strong>Song Ngư - bạn l&agrave; ai?</strong></p>\n', '<p><strong>LỚP HỌC MẬT NGỮ NGOẠI TRUYỆN</strong></p>\n\n<p><strong>VŨ TRỤ SONG NGƯ - NHỮNG PHA XỬ L&Yacute; ĐI V&Agrave;O L&Ograve;NG BIỂN CỦA SONG NGƯ V&Agrave; &ldquo;ĐỒNG BỌN&rdquo;</strong></p>\n\n<p><strong>Sau ngoại truyện về Song Tử được tween hưởng ứng nhiệt liệt th&igrave; cộng đồng Song Ngư h&atilde;y phấn kh&iacute;ch đi th&ocirc;i v&igrave; cuốn ngoại truyện n&agrave;y d&agrave;nh cho c&aacute;c bạn. V&agrave; nếu thuộc cung kh&aacute;c, th&igrave; quyển s&aacute;ch n&agrave;y cũng c&oacute; rất nhiều điều để bạn kh&aacute;m ph&aacute; về ch&iacute;nh m&igrave;nh đấy, tuyệt c&uacute; chưa!</strong></p>\n\n<p><strong>Song Ngư - bạn l&agrave; ai?</strong></p>\n\n<p>Ch&uacute;ng ta đều biết ch&ograve;m sao Song Ngư mang biểu tượng hai ch&uacute; c&aacute; dễ thương, l&agrave; cung ho&agrave;ng đạo của những bạn c&oacute; ng&agrave;y sinh: 19/2 - 20/3. Với đặc điểm của c&aacute;, Song Ngư l&agrave; người &ldquo;bơi&rdquo; trong một thế giới kh&aacute;c, xa rời mặt đất. Họ nhạy cảm, linh hoạt v&agrave;&hellip; chạy trốn rất giỏi.</p>\n\n<p>Bản chất Song Ngư cực kỳ tốt bụng. Đ&acirc;y l&agrave; m&oacute;n qu&agrave; lớn nhất m&agrave; vũ trụ trao cho Song Ngư. Nhưng nếu kh&ocirc;ng biết sử dụng, đ&oacute; l&agrave; một g&aacute;nh nặng. Song Ngư hay nghĩ cho người kh&aacute;c hơn l&agrave; cho m&igrave;nh. Vừa mạnh mẽ vừa yếu đuối, vừa th&ocirc;ng th&aacute;i vừa trẻ con, vừa tốt bụng vừa&hellip; để bụng!</p>\n\n<p>L&agrave; một trong ba cung Nước, Song Ngư rất mong manh, dễ bị bốc hơi! B&ugrave; lại, họ c&oacute; khiếu nghệ thuật, th&iacute;ch sự l&atilde;ng mạn v&agrave; v&ocirc; c&ugrave;ng tinh tế. C&ugrave;ng với sự t&aacute;c động của một cung biến đổi, Song Ngư l&agrave; cung gi&agrave;u cảm x&uacute;c nhất, dễ thấu hiểu, tận t&acirc;m lẫn s&aacute;ng tạo nhất 12 cung ho&agrave;ng đạo.</p>\n\n<p>Sương sương vậy th&ocirc;i, bạn c&oacute; thấy chuẩn về cung Song Ngư chưa? Cuốn s&aacute;ch n&agrave;y sẽ c&ograve;n cho bạn biết nhiều hơn thế. C&ugrave;ng đi t&igrave;m&nbsp;<em>profile</em>&nbsp;thật của Song Ngư, kh&aacute;m ph&aacute; về Song Ngư qua g&oacute;c nh&igrave;n của c&aacute;c cung ho&agrave;ng đạo kh&aacute;c, v&agrave; t&igrave;m hiểu th&ecirc;m c&aacute;c cung ho&agrave;ng đạo qua lăng k&iacute;nh của Song Ngư&hellip; V&ocirc; v&agrave;n những ph&aacute;t hiện th&uacute; vị v&agrave; mới mẻ sẽ được cập nhật trong tập n&agrave;y!</p>\n\n<p><strong>Vũ trụ Song Ngư c&oacute; m&agrave;u g&igrave;?</strong></p>\n\n<p>Hẳn bạn sẽ trả lời m&agrave;u xanh của nước. Nhưng vũ trụ đ&oacute; cũng ẩn chứa v&ocirc; v&agrave;n điều kỳ th&uacute;. Cung ho&agrave;ng đạo l&agrave; c&aacute;i t&ocirc;i của c&aacute;c ch&ograve;m sao. Nhưng c&aacute;i t&ocirc;i Song Ngư đ&oacute; chỉ l&agrave; phần nổi của tảng băng. Giống như vũ trụ đầy vật chất tối, t&iacute;nh c&aacute;ch của Song Ngư c&ograve;n bị ảnh hưởng bởi những thế lực b&iacute; ẩn. Mặt Trăng tượng trưng cho thế giới cảm x&uacute;c. H&atilde;y c&ugrave;ng t&igrave;m hiểu Song Ngư kết hợp với 12 thế giới cảm x&uacute;c sẽ cho ra phi&ecirc;n bản g&igrave; nh&eacute;!</p>\n\n<p>Trong tập n&agrave;y c&ograve;n c&oacute; c&acirc;u chuyện về Xử Nữ nam s&aacute;ng t&aacute;c truyện tranh nhưng bị ch&ecirc; dở n&ecirc;n t&igrave;m đến Song Ngư nữ để học hỏi c&aacute;ch s&aacute;ng tạo. Song Ngư nữ th&igrave; muốn gi&uacute;p nhưng rất tiếc, phải từ chối. V&igrave; Song Ngư cũng&hellip; kh&ocirc;ng biết tại sao m&igrave;nh s&aacute;ng tạo được. Kh&oacute; tả lắm! Bảo B&igrave;nh nữ giải th&iacute;ch, đ&oacute; l&agrave; do Song Ngư nữ &ldquo;xuất thần&rdquo; v&agrave; cảnh b&aacute;o Xử Nữ nam, kh&ocirc;ng xuất thần th&igrave; kh&ocirc;ng thể đi xa tr&ecirc;n con đường s&aacute;ng tạo... Cuối c&ugrave;ng, c&aacute;c bạn ấy đ&atilde; t&igrave;m ra c&ocirc;ng thức s&aacute;ng tạo n&agrave;o, c&ugrave;ng theo d&otilde;i nh&eacute;!</p>\n\n<p><strong>Qu&agrave; tặng &ldquo;chất&rdquo; ph&aacute;t ngất:&nbsp;</strong>Khung ảnh để b&agrave;n Song Ngư.</p>\n', 2021, 200, 160);
INSERT INTO `chitietsanpham` (`id`, `masp`, `nhacungcap`, `nhaxuatban`, `tacgia`, `hinhthuc`, `nguoidich`, `mota`, `noidung`, `namxuatban`, `trongluong`, `sotrang`) VALUES
(88, 51, 'Nhà Xuất Bản Kim Đồng', 'NXB Kim Đồng', 'Thomas Bulfinch, Lee Kwangjin, Seo Young', 'Bìa mềm', 'Nguyễn Minh Trang', '<p>Thần thoại Hy Lạp, c&acirc;u chuyện kh&ocirc;ng chỉ d&agrave;nh cho thiếu nhi m&agrave; cả người lớn.</p>\n\n<p>Thần thoại Hy Lạp l&agrave; những c&acirc;u chuyện v&ocirc; c&ugrave;ng cảm động, k&igrave; th&uacute; về thế giới c&aacute;c vị thần c&oacute; thể h&ocirc; phong ho&aacute;n vũ; về c&aacute;c n&agrave;ng ti&ecirc;n rừng, ti&ecirc;n s&ocirc;ng, ti&ecirc;n biển xinh đẹp, y&ecirc;u kiều; về c&aacute;c dũng sĩ, anh h&ugrave;ng gan dạ, lừng lẫy tiếng tăm; về c&aacute;c nh&agrave; vua, ho&agrave;ng hậu, c&ocirc;ng ch&uacute;a, ho&agrave;ng tử số phận bi ai; về c&aacute;c lo&agrave;i qu&aacute;i vật gớm ghiếc, những t&ecirc;n khổng lồ đ&aacute;ng sợ... đ&atilde; trở th&agrave;nh nguồn cội của văn h&oacute;a phương T&acirc;y v&agrave; của cả thế giới khiến bao thế hệ say m&ecirc; t&igrave;m đọc.</p>\n', '<p>Thần thoại Hy Lạp, c&acirc;u chuyện kh&ocirc;ng chỉ d&agrave;nh cho thiếu nhi m&agrave; cả người lớn.</p>\n\n<p>Thần thoại Hy Lạp l&agrave; những c&acirc;u chuyện v&ocirc; c&ugrave;ng cảm động, k&igrave; th&uacute; về thế giới c&aacute;c vị thần c&oacute; thể h&ocirc; phong ho&aacute;n vũ; về c&aacute;c n&agrave;ng ti&ecirc;n rừng, ti&ecirc;n s&ocirc;ng, ti&ecirc;n biển xinh đẹp, y&ecirc;u kiều; về c&aacute;c dũng sĩ, anh h&ugrave;ng gan dạ, lừng lẫy tiếng tăm; về c&aacute;c nh&agrave; vua, ho&agrave;ng hậu, c&ocirc;ng ch&uacute;a, ho&agrave;ng tử số phận bi ai; về c&aacute;c lo&agrave;i qu&aacute;i vật gớm ghiếc, những t&ecirc;n khổng lồ đ&aacute;ng sợ... đ&atilde; trở th&agrave;nh nguồn cội của văn h&oacute;a phương T&acirc;y v&agrave; của cả thế giới khiến bao thế hệ say m&ecirc; t&igrave;m đọc.</p>\n\n<p><strong>Học giả Thomas Bulfinch (</strong><em>1796 -1867) t</em>ốt nghiệp Đại học Harvard (Mĩ). Thomas Bulfnch từng l&agrave;m rất nhiều việc li&ecirc;n quan tới c&aacute;c lĩnh vực như bảo t&agrave;ng, văn học, gi&aacute;o dục. &Ocirc;ng cho rằng, chưa hiểu Thần thoại Hy Lạp l&agrave; chưa biết đến văn minh ch&acirc;u &Acirc;u n&ecirc;n đ&atilde; d&agrave;nh nhiều thời gian nghi&ecirc;n cứu thần thoại, truyền thuyết, văn học d&acirc;n gian. Thomas l&agrave; t&aacute;c giả nhiều t&aacute;c phẩm, trong đ&oacute; &ldquo;<strong>Thời đại của truyền thuyết</strong>&rdquo; l&agrave; t&aacute;c phẩm thần thoại tiếng Anh nổi tiếng nhất của &ocirc;ng được rất nhiều độc giả tr&ecirc;n thế giới y&ecirc;u th&iacute;ch.<br />\nBộ truyện tranh m&agrave;u n&agrave;y được x&acirc;y dựng dựa theo c&aacute;c c&acirc;u chuyện về thần thoại Hy Lạp trong t&aacute;c phẩm &ldquo;<strong>Thời đại của truyền thuyết</strong>&rdquo; của nh&agrave; văn Thomas Bulfnch.</p>\n\n<p><strong>Họa sĩ</strong>&nbsp;<strong>Seo Young&nbsp;</strong>Sau khi ra mắt độc giả với ấn phẩm &ldquo;<strong>Cầu thủ thay thế đ&aacute;ng sợ</strong>&rdquo;, &ldquo;<strong>Truyện d&agrave;i k&igrave; anh ch&agrave;ng FA</strong>&rdquo; tr&ecirc;n tờ &ldquo;<strong>Thanh thiếu ni&ecirc;n H&agrave;n Quốc</strong>&rdquo;, họa sĩ Seo Young li&ecirc;n tục minh họa cho c&aacute;c t&aacute;c phẩm truyện tranh d&agrave;nh cho trẻ em như &ldquo;<strong>Lịch sử H&agrave;n Quốc</strong>&rdquo;, &ldquo;<strong>Tam Quốc ch&iacute;</strong>&rdquo;, &ldquo;<strong>Lịch sử thế giới</strong>&rdquo;&hellip; Năm 2000, hoạ sĩ được nhận giải thưởng với t&aacute;c phẩm &ldquo;<strong>Qu&agrave; kỉ niệm của thi&ecirc;n nhi&ecirc;n ở đ&acirc;u?</strong>&rdquo; thuộc hạng mục Truyện tranh kiến thức do Bộ Văn h&oacute;a v&agrave; Du lịch H&agrave;n Quốc tổ chức.</p>\n', 2018, 210, 200),
(89, 52, 'Công Ty TNHH Headfully', 'NXB Thanh Niên', 'Nhiều Tác Giả', 'Bìa mềm', 'Không có', '<p>&quot;THỔI BAY&quot; &Aacute;P LỰC HỌC ĐƯỜNG!!!<br />\n----------------------------------<br />\n- Tween đang lo lắng chuyện b&agrave;i vở?</p>\n\n<p>- Tween sợ điểm số cuối kỳ kh&ocirc;ng như kỳ vọng?</p>\n\n<p>- Tween hoang mang v&igrave; một &aacute;p lực n&agrave;o từ bạn b&egrave;, gia đ&igrave;nh, học h&agrave;nh?</p>\n', '<p>&quot;THỔI BAY&quot; &Aacute;P LỰC HỌC ĐƯỜNG!!!<br />\n----------------------------------<br />\n- Tween đang lo lắng chuyện b&agrave;i vở?</p>\n\n<p>- Tween sợ điểm số cuối kỳ kh&ocirc;ng như kỳ vọng?</p>\n\n<p>- Tween hoang mang v&igrave; một &aacute;p lực n&agrave;o từ bạn b&egrave;, gia đ&igrave;nh, học h&agrave;nh?</p>\n\n<p>H&Atilde;Y C&Ugrave;NG C&Aacute;C NH&Acirc;N VẬT&nbsp;LỚP HỌC MẬT NGỮ&nbsp;&ldquo;THỔI BAY&rdquo; NHỮNG PHIỀN TO&Aacute;I Đ&Oacute;!!!</p>\n\n<p>C&aacute;c tween mến y&ecirc;u ơi!!! Đừng qu&ecirc;n đặt mua cuốn s&aacute;ch kỹ năng v&ocirc; c&ugrave;ng hấp dẫn v&agrave; bổ &iacute;ch n&agrave;y về sớm cho m&igrave;nh nh&eacute;!!!</p>\n\n<p>Tủ s&aacute;ch kỹ năng Tween Book 4.0 do B&aacute;o Thiếu ni&ecirc;n Tiền Phong v&agrave; Nhi Đồng xuất bản:</p>\n\n<p>- Cung cấp những kỹ năng để gi&uacute;p cậu học vui hơn, chơi đ&atilde; hơn, biết kh&aacute;m ph&aacute; v&agrave; ho&agrave;n thiện tiềm năng của bản th&acirc;n.&nbsp;</p>\n\n<p>- Trong bộ s&aacute;ch n&agrave;y, c&aacute;c cậu sẽ kh&ocirc;ng chỉ được gặp những nh&acirc;n vật quen thuộc l&agrave; Những Người Bạn Cung Ho&agrave;ng Đạo từ bộ truyện tranh b&aacute;n chạy nhất LỚP HỌC MẬT NGỮ, m&agrave; c&ograve;n c&ugrave;ng c&aacute;c bạn ấy vừa học tiếng Anh, vừa chơi game, vừa l&agrave;m đồ chơi, vừa giải &ocirc; chữ&hellip; Chưa kể, c&aacute;c cậu c&ograve;n c&oacute; cơ hội được nhận những phần qu&agrave; tặng may mắn nữa chứ!</p>\n\n<p>- Tập 1 của Bộ s&aacute;ch kỹ năng TWEEN 4.0 mang t&ecirc;n &ldquo;THỔI BAY &Aacute;P LỰC HỌC ĐƯỜNG&rdquo; sẽ gi&uacute;p c&aacute;c tween nhận diện những &aacute;p lực v&agrave; chia sẻ c&aacute;c b&iacute; k&iacute;p để nhảy ph&oacute;c qua c&aacute;c vấn đề kh&oacute; ưa để mỗi ng&agrave;y tới trường l&agrave; một ng&agrave;y vui.</p>\n', 2021, 200, 103),
(90, 53, 'Phuc Minh Books', 'NXB Văn Học', 'Giang Anh', 'Bìa mềm', 'Không có', '<p>Thời gian gần đ&acirc;y, kĩ năng sống cho trẻ l&agrave; một trong những vẫn đề nhận được rất nhiều sự quan t&acirc;m từ bố mẹ? Nhưng dạy con như thế n&agrave;o để con c&oacute; thể hiểu v&agrave; thực hiện theo th&igrave; chẳng dễ d&agrave;ng một ch&uacute;t n&agrave;o. L&agrave;m thế n&agrave;o khi c&oacute; người lạ tới, bắt con đi? L&agrave;m sao để con kh&ocirc;ng đi theo người lạ khi được cho kẹo, đồ chơi? Khi con bỗng nhi&ecirc;n gặp đ&aacute;m ch&aacute;y nổ hay đơn giản l&agrave; khi bố mẹ bận con phải ở nh&agrave; một m&igrave;nh. Bạn b&egrave; tr&ecirc;n lớp bắt nạt con?</p>\n', '<p>Thời gian gần đ&acirc;y, kĩ năng sống cho trẻ l&agrave; một trong những vẫn đề nhận được rất nhiều sự quan t&acirc;m từ bố mẹ? Nhưng dạy con như thế n&agrave;o để con c&oacute; thể hiểu v&agrave; thực hiện theo th&igrave; chẳng dễ d&agrave;ng một ch&uacute;t n&agrave;o. L&agrave;m thế n&agrave;o khi c&oacute; người lạ tới, bắt con đi? L&agrave;m sao để con kh&ocirc;ng đi theo người lạ khi được cho kẹo, đồ chơi? Khi con bỗng nhi&ecirc;n gặp đ&aacute;m ch&aacute;y nổ hay đơn giản l&agrave; khi bố mẹ bận con phải ở nh&agrave; một m&igrave;nh. Bạn b&egrave; tr&ecirc;n lớp bắt nạt con?<br />\n<br />\nCh&uacute;ng ta những người l&agrave;m cha, l&agrave;m mẹ ngay từ b&acirc;y giờ h&atilde;y d&atilde;y trẻ biết rằng: thế giới n&agrave;y đ&ocirc;i khi c&oacute; những điều cần phải tr&aacute;nh v&agrave; việc trang bị cho con những kĩ năng tho&aacute;t hiểm l&agrave; v&ocirc; c&ugrave;ng cần thiết.<br />\n<br />\nBộ s&aacute;ch Si&ecirc;u nh&acirc;n tho&aacute;t hiểm gồm 6 cuốn:&nbsp;<strong><em>Đừng h&ograve;ng bắt nạt tớ, Tự vệ khi gặp nguy hiểm, Tuyệt chi&ecirc;u tho&aacute;t kẻ bắt c&oacute;c, Cảnh gi&aacute;c với người lạ, An to&agrave;n khi ở nh&agrave; một m&igrave;nh, Tự ứng ph&oacute; khi ch&aacute;y nổ</em></strong>&nbsp;sẽ l&agrave; trợ thủ đắc lực gi&uacute;p bố mẹ hướng dẫn c&aacute;c b&eacute; những kĩ năng tho&aacute;t hiểm cơ bản.</p>\n\n<p>Bộ s&aacute;ch n&agrave;y d&agrave;nh cho c&aacute;c b&eacute; 6 tuổi trở l&ecirc;n nhưng c&aacute;c bạn mầm non cũng c&oacute; thể đọc hiểu v&agrave; ghi nhớ nội dung trong s&aacute;ch. L&agrave; bộ s&aacute;ch kĩ năng nhưng c&aacute;c cuốn s&aacute;ch kh&ocirc;ng hề kh&ocirc; khan m&agrave; được tr&igrave;nh b&agrave;y th&uacute; vị, h&agrave;i hước như một tr&ograve; chơi trong gia đ&igrave;nh với h&igrave;nh minh họa ngộ nghĩnh, dễ thương. Bố mẹ v&agrave; c&aacute;c b&eacute; sẽ dễ d&agrave;ng thực hiện theo.</p>\n\n<p><strong>Đừng h&ograve;ng bắt nạt tớ:&nbsp;</strong>Phương l&agrave; c&ocirc; b&eacute; ngoan, ng&agrave;y n&agrave;o đi học về Phương cũng l&iacute;u lo kể đủ thứ chuyện ở trường, ở lớp cho bố mẹ nghe. Nhưng h&ocirc;m nay sao Phương buồn thế, th&igrave; ra c&ocirc; b&eacute; biết được c&oacute; hai ban trong lớp định bắt nạt m&igrave;nh. Ngay lập tức, bố chỉ cho Phương c&aacute;ch đối ph&oacute; với c&aacute;c bạn. Đầu ti&ecirc;n nếu bạn lớn hơn m&igrave;nh th&igrave; Phương phải t&igrave;m c&aacute;ch tho&aacute;t thật nhanh, chạy đi thật xa. C&ograve;n nếu bạn t&uacute;m được th&igrave; phải t&ugrave;y từng t&igrave;nh huống nh&eacute;. Nhưng điều quan trọng hơn l&agrave; Phương phải tr&ograve; chuyện với c&aacute;c bạn để giải tỏa những hiểu lầm. C&oacute; như thế th&igrave; t&igrave;nh bạn của ch&uacute;ng m&igrave;nh mới bền chặt đ&uacute;ng kh&ocirc;ng n&agrave;o?<br />\n<br />\nCh&uacute;ng ta h&atilde;y chung tay gi&uacute;p c&aacute;c b&eacute; được lớn l&ecirc;n tự lập, an to&agrave;n v&agrave; khỏe mạnh!</p>\n', 2019, 80, 24),
(91, 54, 'Phuc Minh Books', 'NXB Văn Học', 'Giang Anh', 'Bìa mềm', 'Không có', '<p>Thời gian gần đ&acirc;y, kĩ năng sống cho trẻ l&agrave; một trong những vẫn đề nhận được rất nhiều sự quan t&acirc;m từ bố mẹ? Nhưng dạy con như thế n&agrave;o để con c&oacute; thể hiểu v&agrave; thực hiện theo th&igrave; chẳng dễ d&agrave;ng một ch&uacute;t n&agrave;o. L&agrave;m thế n&agrave;o khi c&oacute; người lạ tới, bắt con đi? L&agrave;m sao để con kh&ocirc;ng đi theo người lạ khi được cho kẹo, đồ chơi? Khi con bỗng nhi&ecirc;n gặp đ&aacute;m ch&aacute;y nổ hay đơn giản l&agrave; khi bố mẹ bận con phải ở nh&agrave; một m&igrave;nh. Bạn b&egrave; tr&ecirc;n lớp bắt nạt con?</p>\n', '<p>Thời gian gần đ&acirc;y, kĩ năng sống cho trẻ l&agrave; một trong những vẫn đề nhận được rất nhiều sự quan t&acirc;m từ bố mẹ? Nhưng dạy con như thế n&agrave;o để con c&oacute; thể hiểu v&agrave; thực hiện theo th&igrave; chẳng dễ d&agrave;ng một ch&uacute;t n&agrave;o. L&agrave;m thế n&agrave;o khi c&oacute; người lạ tới, bắt con đi? L&agrave;m sao để con kh&ocirc;ng đi theo người lạ khi được cho kẹo, đồ chơi? Khi con bỗng nhi&ecirc;n gặp đ&aacute;m ch&aacute;y nổ hay đơn giản l&agrave; khi bố mẹ bận con phải ở nh&agrave; một m&igrave;nh. Bạn b&egrave; tr&ecirc;n lớp bắt nạt con?</p>\n\n<p>Ch&uacute;ng ta những người l&agrave;m cha, l&agrave;m mẹ ngay từ b&acirc;y giờ h&atilde;y d&atilde;y trẻ biết rằng: thế giới n&agrave;y đ&ocirc;i khi c&oacute; những điều cần phải tr&aacute;nh v&agrave; việc trang bị cho con những kĩ năng tho&aacute;t hiểm l&agrave; v&ocirc; c&ugrave;ng cần thiết.</p>\n\n<p>Bộ s&aacute;ch Si&ecirc;u nh&acirc;n tho&aacute;t hiểm gồm 6 cuốn:&nbsp;<strong><em>Đừng h&ograve;ng bắt nạt tớ, Tự vệ khi gặp nguy hiểm, Tuyệt chi&ecirc;u tho&aacute;t kẻ bắt c&oacute;c, Cảnh gi&aacute;c với người lạ, An to&agrave;n khi ở nh&agrave; một m&igrave;nh, Tự ứng ph&oacute; khi ch&aacute;y nổ</em></strong>&nbsp;sẽ l&agrave; trợ thủ đắc lực gi&uacute;p bố mẹ hướng dẫn c&aacute;c b&eacute; những kĩ năng tho&aacute;t hiểm cơ bản.</p>\n\n<p>Bộ s&aacute;ch n&agrave;y d&agrave;nh cho c&aacute;c b&eacute; 6 tuổi trở l&ecirc;n nhưng c&aacute;c bạn mầm non cũng c&oacute; thể đọc hiểu v&agrave; ghi nhớ nội dung trong s&aacute;ch. L&agrave; bộ s&aacute;ch kĩ năng nhưng c&aacute;c cuốn s&aacute;ch kh&ocirc;ng hề kh&ocirc; khan m&agrave; được tr&igrave;nh b&agrave;y th&uacute; vị, h&agrave;i hước như một tr&ograve; chơi trong gia đ&igrave;nh với h&igrave;nh minh họa ngộ nghĩnh, dễ thương. Bố mẹ v&agrave; c&aacute;c b&eacute; sẽ dễ d&agrave;ng thực hiện theo.<br />\n<br />\n<strong>Cảnh gi&aacute;c với người lạ:&nbsp;</strong>Trong truyện, b&eacute; Mon thường hay mở cửa cho người lạ v&agrave;o nh&agrave;. Thấy thế, Ch&uacute; H&ograve;a đ&atilde; rủ Mon chơi đ&oacute;ng kịch Gặp người lạ. B&eacute; Mon h&agrave;o hứng lắm. Sau buổi chơi đ&oacute;ng kịch c&ugrave;ng ch&uacute;, Mon hiểu ra rằng phải lu&ocirc;n giữ khoảng c&aacute;ch với người lạ, tối tiểu l&agrave; 5 bước ch&acirc;n, khi người lạ tới gần cho đồ ăn hoặc kẹo b&aacute;nh th&igrave; kh&ocirc;ng được ph&eacute;p nhận. Nếu như c&oacute; người lạ đến nh&agrave;, nhận l&agrave; họ h&agrave;ng hay người quen của bố mẹ rồi bảo mở cửa th&igrave; phải gọi điện thoại hỏi bố mẹ&hellip; Nhờ những b&agrave;i học n&agrave;y m&agrave; b&eacute; Mon trở th&agrave;nh si&ecirc;u nh&acirc;n nhanh tr&iacute; đấy.<br />\n<br />\nCh&uacute;ng ta h&atilde;y chung tay gi&uacute;p c&aacute;c b&eacute; được lớn l&ecirc;n tự lập, an to&agrave;n v&agrave; khỏe mạnh!</p>\n', 2019, 100, 24),
(92, 55, 'Nhã Nam', 'NXB Thế Giới', 'Uyên Bùi, BS Trí Đoàn', 'Bìa mềm', 'Không có', '<p>TOP 100 BEST SELLER - Để Con Được Ốm</p>\n\n<p>Để con được ốm&nbsp;c&oacute; thể coi l&agrave; một cuốn nhật k&yacute; học l&agrave;m mẹ th&ocirc;ng qua những c&acirc;u chuyện từ trải nghiệm thực tế m&agrave; chị&nbsp;Uy&ecirc;n B&ugrave;i&nbsp;đ&atilde; trải qua từ khi mang thai đến khi em b&eacute; ch&agrave;o đời v&agrave; trở th&agrave;nh một c&ocirc; b&eacute; khỏe mạnh, vui vẻ.&nbsp;</p>\n', '<p>TOP 100 BEST SELLER - Để Con Được Ốm</p>\n\n<p>Để con được ốm&nbsp;c&oacute; thể coi l&agrave; một cuốn nhật k&yacute; học l&agrave;m mẹ th&ocirc;ng qua những c&acirc;u chuyện từ trải nghiệm thực tế m&agrave; chị&nbsp;Uy&ecirc;n B&ugrave;i&nbsp;đ&atilde; trải qua từ khi mang thai đến khi em b&eacute; ch&agrave;o đời v&agrave; trở th&agrave;nh một c&ocirc; b&eacute; khỏe mạnh, vui vẻ. C&ugrave;ng với những c&acirc;u chuyện nhỏ th&uacute; vị của người mẹ l&agrave; lời khuy&ecirc;n mang t&iacute;nh chuy&ecirc;n m&ocirc;n, giải đ&aacute;p cụ thể từ b&aacute;c sỹ&nbsp;Nguyễn Tr&iacute; Đo&agrave;n, gi&uacute;p h&oacute;a giải những hiểu lầm từ kinh nghiệm d&acirc;n gian được truyền lại, cũng như l&yacute; giải một c&aacute;ch khoa học những th&ocirc;ng tin chưa đ&uacute;ng đắn đang được lưu truyền hiện nay, mang đến g&oacute;c nh&igrave;n đ&uacute;ng đắn nhất cho mỗi hiện tượng, sự việc với những kiến thức y khoa hiện đại được cập nhật li&ecirc;n tục. Cuốn s&aacute;ch sẽ gi&uacute;p c&aacute;c bậc phụ huynh trang bị một số kiến thức cơ bản trong việc chăm s&oacute;c trẻ một c&aacute;ch khoa học v&agrave; g&oacute;p phần gi&uacute;p c&aacute;c mẹ v&agrave; những-người-sẽ-l&agrave;-mẹ trở n&ecirc;n tự tin hơn trong việc chăm con, xua tan đi những lo lắng, để mỗi em b&eacute; ra đời đều được hưởng sự chăm s&oacute;c tốt nhất.</p>\n\n<p>Tr&ecirc;n thị trường c&oacute; nhiều đầu s&aacute;ch về chăm s&oacute;c trẻ, tuy nhi&ecirc;n đa phần đều l&agrave; nội dung được dịch từ c&aacute;c s&aacute;ch nước ngo&agrave;i n&ecirc;n phần n&agrave;o đ&oacute; kh&ocirc;ng ph&ugrave; hợp với c&aacute;c gia đ&igrave;nh Việt bởi sự kh&aacute;c biệt về suy nghĩ v&agrave; &yacute; thức hệ c&ugrave;ng những quan niệm sống. Đặc biệt l&agrave; c&aacute;c cuốn s&aacute;ch ấy kh&ocirc;ng bao giờ nhắc tới c&aacute;ch xử l&yacute; c&aacute;c vấn đề đời thường, những kinh nghiệm d&acirc;n gian hay phương ph&aacute;p chăm s&oacute;c trẻ từ &ldquo;kinh nghiệm c&aacute; nh&acirc;n&rdquo; của c&aacute;c mẹ tr&agrave;n lan tr&ecirc;n c&aacute;c diễn đ&agrave;n v&agrave; mạng x&atilde; hội - những điều đang tồn tại trong x&atilde; hội Việt ta hiện nay. Do đ&oacute;,Để con được ốm&nbsp;trở th&agrave;nh một t&aacute;c phẩm độc đ&aacute;o khi đi s&acirc;u v&agrave;o vấn đề chăm s&oacute;c con trẻ c&ugrave;ng những điều bất cập trong x&atilde; hội Việt, trong những gia đ&igrave;nh Việt, giữa c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh.&nbsp;Để con được ốm&nbsp;kh&ocirc;ng phải l&agrave; cuốn &ldquo;b&aacute;ch khoa to&agrave;n thư&rdquo; chữa b&aacute;ch bệnh ở trẻ em, n&oacute; đơn giản l&agrave; lời sẻ chia v&agrave; động vi&ecirc;n gửi đến c&aacute;c b&agrave; mẹ trẻ n&oacute;i ri&ecirc;ng v&agrave; c&aacute;c gia đ&igrave;nh n&oacute;i chung.</p>\n\n<p>Hi vọng, cuốn s&aacute;ch sẽ khiến c&aacute;c bậc phụ huynh cảm thấy như một luồng gi&oacute; m&aacute;t gi&uacute;p &ldquo;cởi phăng&rdquo;, &ldquo;cuốn bay&rdquo; những suy nghĩ, những định kiến kh&ocirc;ng đ&uacute;ng như những lớp &aacute;o gi&aacute;p vững chắc về hiều biết, về c&aacute;ch chăm s&oacute;c trẻ khi bệnh cũng như khi khỏe.</p>\n\n<p>Nhận x&eacute;t</p>\n\n<p>&ldquo;Khi cầm cuốn s&aacute;ch tr&ecirc;n tay, t&ocirc;i cảm thấy hết sức th&uacute; vị v&igrave; nội dung của n&oacute; đ&atilde; gạt bỏ hầu hết mọi quan niệm về chăm s&oacute;c trẻ theo &ldquo;kinh nghiệm&rdquo; truyền tai của c&aacute;c b&agrave; mẹ - nu&ocirc;i con nh&igrave;n sang nh&agrave; h&agrave;ng x&oacute;m. Đặc biệt hơn, n&oacute; được tổng kết từ thực tế chăm s&oacute;c con của một b&agrave; mẹ trẻ. Những rắc rối hết sức đời thường tưởng như nhỏ nhặt ấy đ&atilde; được giải đ&aacute;p v&agrave; ph&acirc;n t&iacute;ch một c&aacute;ch cặn kẽ bằng c&aacute;c chứng cứ khoa học v&agrave; những lời khuy&ecirc;n cũng hết sức đời thường.&rdquo;</p>\n\n<p>(Bs. Ng&ocirc; Đức H&ugrave;ng, Bệnh viện Bạch Mai)</p>\n\n<p>&quot;Từ gi&acirc;y ph&uacute;t đầu ti&ecirc;n cầm quyển s&aacute;ch tr&ecirc;n tay, t&ocirc;i biết rằng quyển s&aacute;ch n&agrave;y sẽ tạo ra sự thay đổi trong nhận thức của cha mẹ Việt về c&aacute;ch chữa trị bằng tr&aacute;i tim y&ecirc;u thương, thay v&igrave; việc chữa trị th&ocirc;ng thường th&ocirc;ng qua c&aacute;c biểu hiện bệnh l&yacute;.&quot;</p>\n\n<p>(Mrs. Jeannie Chan-Ho, Viện trưởng Viện Gi&aacute;o dục Shichida Việt Nam)</p>\n\n<p>&quot;Sức khỏe của con lu&ocirc;n l&agrave; mối quan t&acirc;m h&agrave;ng đầu của mẹ. Chỉ cần con hắt hơi th&ocirc;i l&agrave; lo lắng c&oacute; phải bệnh kh&ocirc;ng? Khi con bệnh, m&igrave;nh chăm s&oacute;c sao đ&acirc;y? Sao th&aacute;ng n&agrave;y con tăng k&yacute; &iacute;t vậy? Ph&aacute;t triển vậy c&oacute; b&igrave;nh thường kh&ocirc;ng? C&oacute; suy dinh dưỡng kh&ocirc;ng? H&ocirc;m nay con ăn c&oacute; v&agrave;i muỗng, sao đủ chất? Bao nhi&ecirc;u lo lắng, từ lớn đến nhỏ đều được B&aacute;c sĩ Tr&iacute; Đo&agrave;n giải th&iacute;ch r&otilde; r&agrave;ng, dễ hiểu v&agrave; mang t&iacute;nh khoa học. Nhiều kinh nghiệm d&acirc;n gian chưa đ&uacute;ng cũng được mổ xẻ, ph&acirc;n t&iacute;ch gi&uacute;p c&aacute;c mẹ hiểu c&aacute;i g&igrave; n&ecirc;n giữ lại, c&aacute;i g&igrave; n&ecirc;n điều chỉnh cho đ&uacute;ng. Thậm ch&iacute;, những người trong ng&agrave;nh y như t&ocirc;i cũng được cập nhật những kiến thức cũ th&agrave;nh mới. C&oacute; thể n&oacute;i, chưa quyển s&aacute;ch n&agrave;o l&agrave;m đươc điều n&agrave;y. T&ocirc;i tin Để con được ốm l&agrave; một quyển s&aacute;ch đ&aacute;ng để gối đầu giường cho c&aacute;c b&agrave; mẹ, gi&uacute;p c&aacute;c mẹ tự tin chăm s&oacute;c b&eacute; y&ecirc;u một c&aacute;ch tốt nhất!&quot;</p>\n\n<p>(Bs. Anh Thy - Chuy&ecirc;n vi&ecirc;n&nbsp;<a href=\"http://tiki.vn/tu-van/\">Tư vấn</a>&nbsp;Sữa mẹ Quốc tế tại Việt Nam)</p>\n\n<p>&quot;C&aacute;i hay của s&aacute;ch l&agrave; gi&uacute;p ch&uacute;ng ta - những học tr&ograve; bỡ ngỡ trong lớp học &ldquo;l&agrave;m cha mẹ&rdquo; - giảm bớt &aacute;p lực trong qu&aacute; tr&igrave;nh nu&ocirc;i con. Nu&ocirc;i con kh&ocirc;ng bao giờ nh&agrave;n nh&atilde;, nhưng n&ecirc;n mang đến niềm vui, thay v&igrave; sự căng thẳng v&agrave; những chuỗi ng&agrave;y d&agrave;i băn khoăn lo lắng, rồi dẫn tới bao điều kh&ocirc;ng hay kh&aacute;c.&quot;</p>\n\n<p>(Bs. Đinh Huỳnh Linh, Bệnh viện Tim mạch Quốc gia)</p>\n', 2018, 320, 291),
(93, 56, 'Nhã Nam', 'NXB Thế Giới', 'BS Vân Hương, BS LÊ DUY MINH, Uyên Bùi', 'Bìa mềm', 'Không có', '<p><strong>Để Con Được Ch&iacute;ch - Hiểu Hết Về Vắc Xin V&agrave; Miễn Dịch - K&egrave;m Chữ K&yacute; T&aacute;c Giả (Số Lượng C&oacute; Hạn)</strong></p>\n\n<p>Th&aacute;ng S&aacute;u năm 2019, C&ocirc;ng ty Cổ phần Văn h&oacute;a v&agrave; Truyền th&ocirc;ng Nh&atilde; Nam giới thiệu đến bạn đọc cuốn s&aacute;ch&nbsp;<em>Để con được ch&iacute;ch &ndash; Hiểu hết về vắc xin v&agrave; miễn dịch</em>&nbsp;của c&aacute;c t&aacute;c giả Uy&ecirc;n B&ugrave;i, Bs. Minh L&ecirc;, Bs. V&acirc;n Hương, v&agrave; Ts. Bs. Ho&agrave;ng Hồng hiệu đ&iacute;nh.</p>\n', '<p><strong>Để Con Được Ch&iacute;ch - Hiểu Hết Về Vắc Xin V&agrave; Miễn Dịch - K&egrave;m Chữ K&yacute; T&aacute;c Giả (Số Lượng C&oacute; Hạn)</strong></p>\n\n<p>Th&aacute;ng S&aacute;u năm 2019, C&ocirc;ng ty Cổ phần Văn h&oacute;a v&agrave; Truyền th&ocirc;ng Nh&atilde; Nam giới thiệu đến bạn đọc cuốn s&aacute;ch&nbsp;<em>Để con được ch&iacute;ch &ndash; Hiểu hết về vắc xin v&agrave; miễn dịch</em>&nbsp;của c&aacute;c t&aacute;c giả Uy&ecirc;n B&ugrave;i, Bs. Minh L&ecirc;, Bs. V&acirc;n Hương, v&agrave; Ts. Bs. Ho&agrave;ng Hồng hiệu đ&iacute;nh.</p>\n\n<p><strong><em>Để con được ch&iacute;ch</em></strong>&nbsp;l&agrave; cuốn s&aacute;ch thứ 2 nằm trong bộ s&aacute;ch 10 cuốn &ldquo;Để con&hellip;&rdquo; của Nh&atilde; Nam hợp t&aacute;c độc quyền với t&aacute;c giả Uy&ecirc;n B&ugrave;i với nội dung chuy&ecirc;n s&acirc;u về chăm s&oacute;c sức khoẻ v&agrave; tinh thần của trẻ từ l&uacute;c c&ograve;n trong bụng mẹ cho đến khi trưởng th&agrave;nh. Cuốn s&aacute;ch l&agrave; sự hợp t&aacute;c giữa Uy&ecirc;n B&ugrave;i với hai b&aacute;c sĩ trẻ, nhiệt huyết, BS V&acirc;n Hương v&agrave; BS Minh L&ecirc;.</p>\n\n<p>Kh&aacute;c với nỗi niềm của người mẹ trong qu&aacute; tr&igrave;nh kh&ocirc;n lớn c&ugrave;ng con, c&acirc;u chuyện của&nbsp;<strong><em>Để con được ch&iacute;ch</em></strong>&nbsp;l&agrave; c&acirc;u chuyện về h&agrave;nh tr&igrave;nh t&igrave;m hiểu về hệ miễn dịch v&agrave; c&aacute;c vấn đề c&oacute; li&ecirc;n quan đến vắc xin của Di Di, một bệnh nhi 7 tuổi v&agrave; b&aacute;c sĩ Hương L&ecirc;. Những c&acirc;u chuyện v&agrave;o mỗi đ&ecirc;m của b&aacute;c sĩ Hương L&ecirc; gi&uacute;p độc giả t&igrave;m hiểu đến gốc rễ c&aacute;c vấn đề c&oacute; li&ecirc;n quan đến dịch bệnh, đến c&aacute;ch thức hoạt động của hệ miễn dịch, giải th&iacute;ch cho l&yacute; do v&igrave; sao ch&uacute;ng ta cần đến vắc xin cũng như giải th&iacute;ch c&aacute;c hiểu lầm v&agrave; phản b&aacute;c lại c&aacute;c luận điểm của hội b&agrave;i trừ vắc xin.</p>\n\n<p>Cuốn s&aacute;ch ra đời trong thời điểm phong tr&agrave;o b&agrave;i trừ vắc xin (anti-vaccine) đ&atilde; trở th&agrave;nh 1 trong 10 vấn nạn lớn nhất về sức khoẻ của con người trong thời hiện đại do Tổ chức Y Tế thế giới (WHO) cảnh b&aacute;o. Sở dĩ c&oacute; điều n&agrave;y l&agrave; bởi hiện nay, c&aacute;c dịch bệnh đ&atilde; được kiểm so&aacute;t tốt nhờ v&agrave;o vắc xin, số lượng trẻ tử vong sau ti&ecirc;m do c&aacute;c bệnh hiểm ngh&egrave;o đ&atilde; giảm xuống ở mức thấp nhất, khiến cho nhiều người trong số ch&uacute;ng ta nghĩ rằng dịch bệnh đ&atilde; kh&ocirc;ng c&ograve;n tồn tại nữa v&agrave; kh&ocirc;ng thể nh&igrave;n thấy được những lợi &iacute;ch m&agrave;&nbsp;<em>chương tr&igrave;nh Ti&ecirc;m chủng mở rộng</em>&nbsp;mang lại cho x&atilde; hội.</p>\n\n<p>Trong khi đ&oacute;, với sự lan toả nhanh ch&oacute;ng của mạng x&atilde; hội, những th&ocirc;ng tin sai lệch v&agrave; bịa đặt về vắc xin đ&atilde; c&oacute; cơ hội để ph&aacute;t t&aacute;n rất nhanh c&ugrave;ng với những tin tức xấu về c&aacute;c biến chứng bất lợi sau ti&ecirc;m chủng đ&atilde; khiến cho rất nhiều cha mẹ hoang mang v&agrave; dẫn đến t&acirc;m l&yacute; do dự trong việc lựa chọn ti&ecirc;m vắc xin cho con. Hậu quả l&agrave; hệ miễn dịch cộng đồng bị ph&aacute; vỡ v&agrave; c&aacute;c đợt dịch bệnh nghi&ecirc;m trọng tưởng như đ&atilde; được kiểm so&aacute;t nay đ&atilde; quay trở lại. Số lượng trẻ bị mắc bệnh v&agrave; tử vong do biến chứng của bệnh cũng tăng nhanh. Do đ&oacute;, cuốn s&aacute;ch hi vọng sẽ trở th&agrave;nh một cơn mưa lớn, gi&uacute;p hạ nhiệt cho những tin tức &ldquo;n&oacute;ng&rdquo; về vắc xin, gi&uacute;p cha mẹ tự tin hơn trong h&agrave;nh tr&igrave;nh học l&agrave;m cha mẹ tốt v&agrave; chăm con khoa học.</p>\n\n<p><strong><em>Để con được ch&iacute;ch</em></strong>&nbsp;tiếp tục mang đến cho c&aacute;c bậc cha mẹ một nguồn tham khảo c&oacute; cơ sở v&agrave; đ&aacute;ng tin cậy hơn nữa về những điều dễ g&acirc;y hoang mang trong việc ti&ecirc;m chủng hiện nay như: nguồn gốc lịch sử của thủ thuật chủng ngừa, vắc xin, lịch sử của c&aacute;c dịch bệnh, c&aacute;ch thức hoạt động của hệ miễn dịch, vắc xin v&agrave; c&aacute;c quy tr&igrave;nh sản xuất, kiểm nghiệm, hiểu về Ti&ecirc;m chủng mở rộng &amp; Ti&ecirc;m chủng dịch vụ, c&aacute;c vấn đề xảy ra sau ti&ecirc;m chủng, quy tr&igrave;nh điều tra c&aacute;c biến cố bất lợi sau ti&ecirc;m chủng, c&aacute;c phản ứng phụ sau ti&ecirc;m n&ecirc;n hiểu thế n&agrave;o cho đ&uacute;ng, lịch sử về phong tr&agrave;o b&agrave;i vắc xin, phản biện c&aacute;c luận điểm b&agrave;i vắc xin&hellip; Những c&acirc;u chuyện được nh&acirc;n vật b&aacute;c sĩ Hương L&ecirc; giải th&iacute;ch cụ thể v&agrave; r&otilde; r&agrave;ng dễ hiểu cho em b&eacute; Di Di 7 tuổi ph&ugrave; hợp với mọi đối tượng độc giả, gi&uacute;p cha mẹ cảm thấy tự tin hơn, can đảm hơn, &ldquo;l&igrave; đ&ograve;n&rdquo; hơn, cứng rắn hơn để đứng về ph&iacute;a con trong mọi ho&agrave;n cảnh. Cuốn s&aacute;ch hi vọng sẽ gi&uacute;p cha mẹ v&agrave; những người chăm s&oacute;c trẻ t&igrave;m được c&aacute;ch để ho&agrave; hợp giữa những lo lắng quan t&acirc;m v&agrave; điều thực sự tốt cho con trẻ. Bởi đến cuối c&ugrave;ng, mọi lựa chọn của cha mẹ đều dẫn đến kết quả l&agrave; sức khoẻ của con v&agrave; đ&oacute; mới l&agrave; điều quan trọng nhất.</p>\n', 2019, 300, 296),
(94, 57, 'Thái Hà', 'NXB Lao Động Xã Hội', 'Gill Rapley, Tracey Murkett', 'Bìa mềm', 'Không có', '<p>Phương Ph&aacute;p Ăn Dặm B&eacute; Chỉ Huy (Baby Led-Weaning) (Tái Bản 2018)</p>\n\n<p>Nếu bạn đ&atilde; quen với h&igrave;nh ảnh c&aacute;c b&eacute; được mẹ d&ugrave;ng muỗng đ&uacute;t thức ăn nghiền nhuyễn v&agrave;o miệng, b&eacute; nh&egrave; ra v&agrave; mẹ lại v&eacute;t v&agrave;o cho đến khi n&agrave;o b&eacute; nuốt th&igrave; th&ocirc;i, th&igrave; BLW sẽ l&agrave; một h&igrave;nh ảnh ho&agrave;n to&agrave;n kh&aacute;c. Với phương ph&aacute;p n&agrave;y, sẽ kh&ocirc;ng c&oacute; chuyện đ&uacute;t muỗng hay nghiền nhuyễn, m&agrave; ba mẹ sẽ cung cấp cho b&eacute; những thức ăn c&oacute; h&igrave;nh dạng v&agrave; k&iacute;ch cỡ ph&ugrave; hợp để b&eacute; c&oacute; thể cầm lấy v&agrave; tự đ&uacute;t cho m&igrave;nh bằng c&aacute;c ng&oacute;n tay, tự chọn thức ăn, tự quyết định ăn bao nhi&ecirc;u v&agrave; ăn với tốc độ như thế n&agrave;o. Rất c&oacute; thể nhiều mẹ sẽ lo lắng b&eacute; ăn như thế nhỡ bị h&oacute;c th&igrave; sao.</p>\n', '<p>Phương Ph&aacute;p Ăn Dặm B&eacute; Chỉ Huy (Baby Led-Weaning) (Tái Bản 2018)</p>\n\n<p>Nếu bạn đ&atilde; quen với h&igrave;nh ảnh c&aacute;c b&eacute; được mẹ d&ugrave;ng muỗng đ&uacute;t thức ăn nghiền nhuyễn v&agrave;o miệng, b&eacute; nh&egrave; ra v&agrave; mẹ lại v&eacute;t v&agrave;o cho đến khi n&agrave;o b&eacute; nuốt th&igrave; th&ocirc;i, th&igrave; BLW sẽ l&agrave; một h&igrave;nh ảnh ho&agrave;n to&agrave;n kh&aacute;c. Với phương ph&aacute;p n&agrave;y, sẽ kh&ocirc;ng c&oacute; chuyện đ&uacute;t muỗng hay nghiền nhuyễn, m&agrave; ba mẹ sẽ cung cấp cho b&eacute; những thức ăn c&oacute; h&igrave;nh dạng v&agrave; k&iacute;ch cỡ ph&ugrave; hợp để b&eacute; c&oacute; thể cầm lấy v&agrave; tự đ&uacute;t cho m&igrave;nh bằng c&aacute;c ng&oacute;n tay, tự chọn thức ăn, tự quyết định ăn bao nhi&ecirc;u v&agrave; ăn với tốc độ như thế n&agrave;o. Rất c&oacute; thể nhiều mẹ sẽ lo lắng b&eacute; ăn như thế nhỡ bị h&oacute;c th&igrave; sao.</p>\n\n<p>Thực ra, b&eacute; cũng như người lớn, dễ bị h&oacute;c hay nghẹn hơn khi c&oacute; người đ&uacute;t cho, bởi về cơ bản đ&oacute; l&agrave; kiểu ăn thụ động - b&eacute; kh&ocirc;ng kiểm so&aacute;t được lượng thức ăn đưa v&agrave;o. Khi b&eacute; tự cho thức ăn v&agrave;o miệng th&igrave; b&eacute; cũng nhận thức l&agrave; m&igrave;nh đang ăn v&agrave; sẽ điều khiển lưỡi, h&agrave;m, m&ocirc;i, họng một c&aacute;ch ph&ugrave; hợp. Nếu thức ăn to qu&aacute; th&igrave; b&eacute; sẽ nh&egrave; ra. Tất nhi&ecirc;n, việc b&eacute; bị ọe v&agrave;o thời gian đầu l&agrave; ho&agrave;n to&agrave;n c&oacute; thể. Nhưng ọe chỉ l&agrave; một phản ứng của b&eacute; khi kh&ocirc;ng chấp nhận đồ ăn. Sau v&agrave;i lần ọe th&igrave; b&eacute; cũng h&igrave;nh th&agrave;nh &yacute; thức v&agrave; kỹ năng để biết ăn bao nhi&ecirc;u, ăn như thế n&agrave;o cho khỏi ọe. Thất bại l&agrave; mẹ th&agrave;nh c&ocirc;ng m&agrave;.</p>\n\n<p>Đ&atilde; &aacute;p dụng phương ph&aacute;p BLW th&igrave; mẹ phải tin tưởng b&eacute;. Tất cả những em b&eacute; khỏe mạnh đều c&oacute; thể l&agrave;m điều đ&oacute; từ 6 th&aacute;ng tuổi, khi b&eacute; đ&atilde; c&oacute; thể ngồi kh&aacute; vững v&agrave; hệ ti&ecirc;u h&oacute;a cũng như miễn dịch đ&atilde; đủ trưởng th&agrave;nh gi&uacute;p b&eacute; hấp thụ c&aacute;c thức ăn kh&aacute;c ngo&agrave;i sữa. B&eacute; kh&ocirc;ng cần được đ&uacute;t muỗng, b&eacute; chỉ cần được tạo cơ hội để tự ăn m&agrave; th&ocirc;i. BLW sẽ gi&uacute;p b&eacute; ăn uống một c&aacute;ch thoải m&aacute;i, tự lập v&agrave; r&egrave;n luyện kỹ năng ăn cho b&eacute;, cụ thể l&agrave;:- Cho ph&eacute;p b&eacute; kh&aacute;m ph&aacute; m&ugrave;i vị v&agrave; cảm gi&aacute;c về độ th&ocirc; mịn;- Khuyến kh&iacute;ch sự độc lập v&agrave; tự tin;- Gi&uacute;p b&eacute; ph&aacute;t triển c&aacute;c kỹ năng nhai v&agrave; phối hợp giữa mắt v&agrave; tay- L&agrave;m cho bữa ăn của b&eacute; bớt &aacute;p lực.</p>\n', 2018, 350, 450),
(95, 58, 'Thái Hà', 'NXB Lao Động', 'Erika Takeuchi', 'Bìa mềm', 'Không có', '<p>T&aacute;c giả của cuốn s&aacute;ch&nbsp;<strong><em>Nu&ocirc;i dạy b&eacute; trai từ 0 &ndash; 6 tuổi</em></strong>&nbsp;l&agrave; người đ&atilde; c&oacute; 20 năm nghi&ecirc;n cứu về sự trưởng th&agrave;nh của trẻ em, quan s&aacute;t 12.000 trẻ ở nhiều độ tuổi kh&aacute;c nhau, từ em b&eacute; sơ sinh cho đến sinh vi&ecirc;n đại học.</p>\n', '<p>T&aacute;c giả của cuốn s&aacute;ch&nbsp;<strong><em>Nu&ocirc;i dạy b&eacute; trai từ 0 &ndash; 6 tuổi</em></strong>&nbsp;l&agrave; người đ&atilde; c&oacute; 20 năm nghi&ecirc;n cứu về sự trưởng th&agrave;nh của trẻ em, quan s&aacute;t 12.000 trẻ ở nhiều độ tuổi kh&aacute;c nhau, từ em b&eacute; sơ sinh cho đến sinh vi&ecirc;n đại học. Từ kinh nghiệm thực tiễn v&agrave; nghi&ecirc;n cứu t&aacute;c giả đ&atilde; đ&uacute;c kết được những điều quan trọng đối với sự ph&aacute;t triển của trẻ rằng:</p>\n\n<p>Ngay từ khi sinh ra, giữa b&eacute; trai v&agrave; b&eacute; g&aacute;i đ&atilde; c&oacute; những đặc trưng kh&aacute;c nhau cả về n&atilde;o bộ cũng như hệ thần kinh vận động. Do vậy c&aacute;ch gi&aacute;o dục cũng sẽ kh&aacute;c nhau. Nếu nắm được điểm mấu chốt trong c&aacute;ch nu&ocirc;i dạy cho từng b&eacute; th&igrave; bố mẹ c&oacute; thể ph&aacute;t triển năng lực của trẻ một c&aacute;ch to&agrave;n diện.</p>\n\n<p><strong>Với b&eacute; trai</strong></p>\n\n<p><strong><em>Khả năng học hỏi của b&eacute; trai được nu&ocirc;i dưỡng bởi &ldquo;hứng th&uacute;&rdquo;.</em></strong></p>\n\n<p>Nếu l&agrave;m thui chột mầm non hứng th&uacute; n&agrave;y của b&eacute;, th&igrave; khi lớn l&ecirc;n, trước khi bắt tay thực hiện việc g&igrave; b&eacute; sẽ lu&ocirc;n n&oacute;i kiểu ti&ecirc;u cực như: &ldquo;C&oacute; l&agrave;m th&igrave; đằng n&agrave;o cũng thất bại m&agrave; th&ocirc;i&rdquo;, &ldquo;Việc đấy với m&igrave;nh l&agrave; kh&ocirc;ng thể&rdquo;. V&igrave; vậy h&atilde;y t&ocirc;n trọng những điều b&eacute; muốn l&agrave;m mặc d&ugrave; c&oacute; rất nhiều h&agrave;nh động của b&eacute; m&agrave; mẹ th&igrave; kh&ocirc;ng t&agrave;i n&agrave;o hiểu được.</p>\n\n<p><strong><em>C&aacute;c b&eacute; trai l&yacute; giải t&iacute;nh chất của sự vật th&ocirc;ng qua những trải nghiệm thất bại. H&atilde;y để b&eacute; thất bại thật nhiều.</em></strong></p>\n\n<p>C&oacute; nhiều b&eacute; trai sẽ kh&ocirc;ng nắm bắt được t&iacute;nh chất của sự vật sự việc cho đến khi b&eacute; thất bại. Trước hết, b&eacute; sẽ l&agrave;m thử, nếu thất bại b&eacute; sẽ hiểu rằng, c&aacute;i n&agrave;y l&agrave; kh&ocirc;ng được, c&aacute;i n&agrave;y l&agrave; đau n&agrave;y. Nếu ngay từ đầu b&eacute; kh&ocirc;ng l&agrave;m g&igrave; cả th&igrave; sẽ kh&ocirc;ng thất bại, nhưng nếu vậy th&igrave; khi lớn l&ecirc;n một ch&uacute;t nữa, b&eacute; sẽ kh&ocirc;ng thể lường trước được những nguy hiểm, n&ecirc;n gặp nhiều kh&oacute; khăn.</p>\n\n<p>V&agrave;o l&uacute;c 2 tuổi, 4 tuổi, 5 tuổi, c&aacute;c b&eacute; trai l&agrave;m bố mẹ thực sự rất vất vả. Trong giai đoạn từ l&uacute;c tr&ograve;n 1 tuổi đến 2 tuổi th&igrave; n&oacute;i g&igrave; cũng: &ldquo;Kh&ocirc;ng, kh&ocirc;ng&rdquo;. Đ&acirc;y l&agrave; thời k&igrave; phản kh&aacute;ng đầu ti&ecirc;n, c&aacute;i t&ocirc;i bắt đầu h&igrave;nh th&agrave;nh. Tiếp theo nữa l&agrave; từ 3 đến khoảng 4 tuổi, tinh thần tự lập đ&atilde; trở n&ecirc;n mạnh mẽ hơn. Việc g&igrave; muốn th&igrave; đ&ograve;i tự m&igrave;nh l&agrave;m cho bằng được, n&ecirc;n việc g&igrave; cũng &ldquo;Để con l&agrave;m&rdquo;, &ldquo;Để con, để con&rdquo;. Những b&agrave; mẹ kh&ocirc;ng c&oacute; thời gian thường thở d&agrave;i v&igrave; phải chờ đợi l&acirc;u. Từ 4 đến 5 tuổi th&igrave; biểu hiện &iacute;ch kỷ của b&eacute; gia tăng. Sẽ c&oacute; l&uacute;c b&eacute; ăn vạ ầm ĩ ở cửa h&agrave;ng: &ldquo;Mua đồ chơi cho con!&rdquo;.</p>\n\n<p>Thế nhưng, đ&acirc;y l&agrave; thời k&igrave; m&agrave; mẹ cần thực sự nỗ lực. Nếu mẹ c&oacute; thể xử tr&iacute; ph&ugrave; hợp với từng độ tuổi, nu&ocirc;i dưỡng t&iacute;nh hiếu k&igrave;, hướng dẫn l&agrave;m sao để b&eacute; c&oacute; tinh thần tự lập v&agrave; tr&aacute;ch nhiệm, c&oacute; thể kiểm so&aacute;t cảm x&uacute;c của m&igrave;nh, th&igrave; việc nu&ocirc;i dạy b&eacute; sau đ&oacute; sẽ trở n&ecirc;n nh&agrave;n hơn rất nhiều. Kết quả l&agrave; b&eacute; sẽ c&oacute; thể tự m&igrave;nh l&agrave;m những việc của m&igrave;nh, c&oacute; thể tự m&igrave;nh kiểm so&aacute;t được sự &iacute;ch kỷ của ch&iacute;nh bản th&acirc;n.</p>\n\n<p><strong><em>Nu&ocirc;i dạy b&eacute; trai từ 0 &ndash; 6 tuổi&nbsp;</em></strong>sẽ chỉ ra cho bố mẹ 7 bước quan trọng trong sự trưởng th&agrave;nh của c&aacute;c b&eacute;:</p>\n\n<p><strong>Bước 0 (0 tuổi):</strong>&nbsp;Trải nghiệm cảm gi&aacute;c &ldquo;vui vẻ&rdquo;</p>\n\n<p>-&gt; Nu&ocirc;i dưỡng &ldquo;t&iacute;nh hiếu kỳ&rdquo;</p>\n\n<p><strong>Bước 1 (1 tuổi):</strong>&nbsp;Trải nghiệm cảm gi&aacute;c &ldquo;th&agrave;nh c&ocirc;ng</p>\n\n<p>-&gt; Nu&ocirc;i dưỡng &ldquo;hứng th&uacute;&rdquo;</p>\n\n<p><strong>Bước 2 (2 tuổi):</strong>&nbsp;Trải nghiệm cảm gi&aacute;c &ldquo;Hiểu rồi&rdquo;</p>\n\n<p>-&gt; Nu&ocirc;i dưỡng &ldquo;khả năng tập trung&rdquo;</p>\n\n<p><strong>Bước 3 (3 tuổi):</strong>&nbsp;Trải nghiệm &ldquo;Muốn l&agrave;m thử&rdquo;</p>\n\n<p>-&gt; Nu&ocirc;i dưỡng &ldquo;t&iacute;nh tự lập&rdquo;</p>\n\n<p><strong>Bước 4 (4 tuổi):</strong>&nbsp;Trải nghiệm cảm gi&aacute;c &ldquo;Cố gắng&rdquo;</p>\n\n<p>-&gt; Nu&ocirc;i dưỡng &ldquo;t&iacute;nh kiềm chế&rdquo;</p>\n\n<p><strong>Bước 5 (5 tuổi):</strong>&nbsp;Trải nghiệm việc n&oacute;i với nhau lời &ldquo;Cảm ơn&rdquo;</p>\n\n<p>-&gt; &nbsp;Nu&ocirc;i dưỡng &ldquo;t&iacute;nh quan t&acirc;m&rdquo;</p>\n\n<p><strong>Bước 6 (6 tuổi):</strong>&nbsp;Trải nghiệm cảm gi&aacute;c &ldquo;Đ&atilde; l&agrave;m đến c&ugrave;ng&rdquo;</p>\n\n<p>-&gt; Nu&ocirc;i dưỡng &ldquo;T&iacute;nh tự tin&rdquo;</p>\n', 2021, 220, 216),
(96, 59, 'Thái Hà', 'NXB Lao Động', 'Erika Takeuchi', 'Bìa mềm', 'La Minh Nhật', '<p>T&aacute;c giả của cuốn s&aacute;ch&nbsp;<strong><em>Nu&ocirc;i dạy b&eacute; g&aacute;i từ 0 &ndash; 6 tuổi</em></strong>&nbsp;l&agrave; người đ&atilde; c&oacute; 20 năm nghi&ecirc;n cứu về sự trưởng th&agrave;nh của trẻ em, quan s&aacute;t 12.000 trẻ ở nhiều độ tuổi kh&aacute;c nhau, từ em b&eacute; sơ sinh cho đến sinh vi&ecirc;n đại học.</p>\n', '<p>T&aacute;c giả của cuốn s&aacute;ch&nbsp;<strong><em>Nu&ocirc;i dạy b&eacute; g&aacute;i từ 0 &ndash; 6 tuổi</em></strong>&nbsp;l&agrave; người đ&atilde; c&oacute; 20 năm nghi&ecirc;n cứu về sự trưởng th&agrave;nh của trẻ em, quan s&aacute;t 12.000 trẻ ở nhiều độ tuổi kh&aacute;c nhau, từ em b&eacute; sơ sinh cho đến sinh vi&ecirc;n đại học. Từ kinh nghiệm thực tiễn v&agrave; nghi&ecirc;n cứu t&aacute;c giả đ&atilde; đ&uacute;c kết được những điều quan trọng đối với sự ph&aacute;t triển của trẻ rằng:</p>\n\n<p>Ngay từ khi sinh ra, giữa b&eacute; trai v&agrave; b&eacute; g&aacute;i đ&atilde; c&oacute; những đặc trưng kh&aacute;c nhau cả về n&atilde;o bộ cũng như hệ thần kinh vận động. Do vậy c&aacute;ch gi&aacute;o dục cũng sẽ kh&aacute;c nhau. Nếu nắm được điểm mấu chốt trong c&aacute;ch nu&ocirc;i dạy cho từng b&eacute; th&igrave; bố mẹ c&oacute; thể ph&aacute;t triển năng lực của trẻ một c&aacute;ch to&agrave;n diện.</p>\n\n<p><strong>Với b&eacute; g&aacute;i</strong></p>\n\n<p><strong><em>L&agrave; người c&oacute; khả năng cảm thụ v&agrave; đồng cảm tốt.</em></strong></p>\n\n<p>Sở hữu khả năng đồng cảm tốt n&ecirc;n c&aacute;c b&eacute; g&aacute;i lu&ocirc;n cư xử h&ograve;a nh&atilde; v&agrave; th&acirc;n thiện. B&eacute; biết đi sớm nhưng giữ thăng bằng kh&ocirc;ng tốt v&agrave; dễ t&eacute; ng&atilde;. Điều n&agrave;y l&agrave; do sự ph&aacute;t triển của hệ thần kinh vận động to&agrave;n th&acirc;n Nhưng b&eacute; lại giỏi trong những tr&ograve; chơi đ&ograve;i hỏi t&iacute;nh ki&ecirc;n nhẫn như gấp giấy hay vẽ tranh. Đ&ocirc;i khi b&eacute; phản kh&aacute;ng: &ldquo;Kh&ocirc;ng, kh&ocirc;ng&rdquo; l&agrave; do t&iacute;nh kỷ luật chưa được ho&agrave;n thiện.</p>\n\n<p>B&eacute; g&aacute;i hay thể hiện sự vui mừng khi được c&ugrave;ng chơi h&ograve;a thuận với bạn, l&uacute;c cả hai c&ugrave;ng chia sẻ cho nhau. B&eacute; coi trọng qu&aacute; tr&igrave;nh thực hiện hơn l&agrave; kết quả. Hơn nữa, với b&eacute; g&aacute;i việc được mẹ &ocirc;m ấp, cưng nựng l&agrave; một điều thật tuyệt vời.</p>\n\n<p><strong><em>Điều quan trọng đối với b&eacute; g&aacute;i đ&oacute; l&agrave; được y&ecirc;u thương, thấu hiểu, sẻ chia v&agrave; giao tiếp.</em></strong></p>\n\n<p>V&igrave; muốn được giao tiếp n&ecirc;n khả năng ng&ocirc;n ngữ của b&eacute; g&aacute;i ph&aacute;t triển nhanh. B&eacute; d&ugrave;ng từ kh&eacute;o l&eacute;o, r&iacute;u r&iacute;t kể cho bố mẹ nghe những chuyện m&igrave;nh gặp trong ng&agrave;y h&ocirc;m nay hay chuyện bạn b&egrave;.</p>\n\n<p>Bố mẹ thường c&oacute; cảm gi&aacute;c như b&eacute; g&aacute;i ph&aacute;t triển sớm, trưởng th&agrave;nh một c&aacute;ch thuận lợi hơn so với b&eacute; trai. Nhưng điều đ&oacute; kh&ocirc;ng đồng nghĩa l&agrave; ta c&oacute; thể y&ecirc;n t&acirc;m với sự trưởng th&agrave;nh sớm của b&eacute;. T&acirc;m l&yacute; của b&eacute; g&aacute;i phức tạp hơn so với b&eacute; trai. Nh&igrave;n b&eacute; c&oacute; vẻ ra d&aacute;ng &ldquo;người lớn&rdquo; biết v&acirc;ng lời nhưng thực ra th&igrave; nh&otilde;ng nhẽo, v&iacute; dụ b&eacute; n&oacute;i: &ldquo;Kh&ocirc;ng th&iacute;ch đ&acirc;u!&rdquo; nhưng trong l&ograve;ng th&igrave; ngược lại, v&igrave; vậy việc nắm bắt được suy nghĩ, t&igrave;nh cảm thực sự của c&aacute;c b&eacute; g&aacute;i l&agrave; rất quan trọng.</p>\n\n<p><strong><em>Nu&ocirc;i dạy b&eacute; g&aacute;i từ 0 &ndash; 6 tuổi&nbsp;</em></strong>sẽ chỉ ra cho bố mẹ 7 bước quan trọng trong sự trưởng th&agrave;nh của c&aacute;c b&eacute;:</p>\n\n<p><strong>Bước 0 (0 tuổi):</strong>&nbsp;K&iacute;ch th&iacute;ch năm gi&aacute;c quan &rarr; Nu&ocirc;i dưỡng cảm nhận</p>\n\n<p><strong>Bước 1 (1 tuổi):</strong>&nbsp;Chia sẻ niềm vui &ldquo;Yeah! Con l&agrave;m được rồi&rdquo; &rarr; Nu&ocirc;i dưỡng t&iacute;nh tự chủ</p>\n\n<p><strong>Bước 2 (2 tuổi):</strong>&nbsp;T&ocirc;n trọng thế giới quan của trẻ &rarr; Nu&ocirc;i dưỡng khả năng tưởng tượng</p>\n\n<p><strong>Bước 3 (3 tuổi):</strong>&nbsp;Tin tưởng, c&ocirc;ng nhận &yacute; kiến của trẻ &rarr; Nu&ocirc;i dưỡng t&iacute;nh kh&ocirc;ng ỷ lại</p>\n\n<p><strong>Bước 4 (4 tuổi):</strong>&nbsp;Cho trẻ cơ hội tự m&igrave;nh suy nghĩ &rarr; Nu&ocirc;i dưỡng khả năng giải quyết vấn đề</p>\n\n<p><strong>Bước 5 (5 tuổi):</strong>&nbsp;Để trẻ trải nghiệm việc gi&uacute;p đỡ v&agrave; được gi&uacute;p đỡ &rarr; Nu&ocirc;i dưỡng khả năng tự b&agrave;y tỏ &yacute; kiến c&aacute; nh&acirc;n một c&aacute;ch r&otilde; r&agrave;ng</p>\n\n<p><strong>Bước 6 (6 tuổi):</strong>&nbsp;Để trẻ cảm nhận đầy đủ t&igrave;nh thương y&ecirc;u &rarr; Nu&ocirc;i dưỡng cảm gi&aacute;c khẳng định bản th&acirc;n</p>\n', 2020, 250, 236),
(97, 60, 'Nhà Xuất Bản Kim Đồng', 'NXB Kim Đồng', 'Nguyễn Thị Thu', 'Bìa mềm', 'Không có', '<p>Trẻ cần g&igrave; nhất ở cha mẹ trong những năm th&aacute;ng đầu đời? Trẻ cần những trải nghiệm n&agrave;o nhất trong giai đoạn ấu thơ?</p>\n\n<p>L&agrave;m thế n&agrave;o để vượt qua giai đoạn phản kh&aacute;ng v&agrave; những cơn ăn vạ của con để mỗi ng&agrave;y b&ecirc;n con thực sự l&agrave; h&agrave;nh tr&igrave;nh hạnh ph&uacute;c v&agrave; &yacute; nghĩa?</p>\n\n<p>L&agrave;m thế n&agrave;o để nu&ocirc;i dưỡng cho con những năng lực sống &ndash; kĩ năng sống cần thiết nhất sẽ theo con suốt cả cuộc đời sau n&agrave;y?</p>\n', '<p>Trẻ cần g&igrave; nhất ở cha mẹ trong những năm th&aacute;ng đầu đời? Trẻ cần những trải nghiệm n&agrave;o nhất trong giai đoạn ấu thơ?</p>\n\n<p>L&agrave;m thế n&agrave;o để vượt qua giai đoạn phản kh&aacute;ng v&agrave; những cơn ăn vạ của con để mỗi ng&agrave;y b&ecirc;n con thực sự l&agrave; h&agrave;nh tr&igrave;nh hạnh ph&uacute;c v&agrave; &yacute; nghĩa?</p>\n\n<p>L&agrave;m thế n&agrave;o để nu&ocirc;i dưỡng cho con những năng lực sống &ndash; kĩ năng sống cần thiết nhất sẽ theo con suốt cả cuộc đời sau n&agrave;y?</p>\n\n<p>Cuốn s&aacute;ch Kỉ luật mềm của tr&aacute;i tim &ndash; Mẹ Việt dạy con kiểu Nhật Bản sẽ trả lời cho những c&acirc;u hỏi tr&ecirc;n v&agrave; những c&acirc;u hỏi kh&aacute;c về nu&ocirc;i dạy con của người Nhật Bản, th&ocirc;ng qua ch&iacute;nh những trải nghiệm thực tế của t&aacute;c giả với hơn mười một năm ở Nhật vừa học tập, vừa l&agrave;m mẹ, trong đ&oacute; c&oacute; s&aacute;u năm đồng h&agrave;nh chia sẻ những c&acirc;u chuyện nu&ocirc;i dạy con với c&aacute;c cha mẹ Việt Nam.</p>\n\n<p>&ldquo;BAO DUNG - THỪA NHẬN - KHEN NGỢI - KH&Iacute;CH LỆ - TIN TƯỞNG với con c&aacute;i v&agrave; TỰ TIN với ch&iacute;nh m&igrave;nh, đ&oacute; l&agrave; những từ kho&aacute; quan trọng nhất trong c&aacute;ch ứng xử hằng ng&agrave;y với con. &ldquo;Cuộc sống hiện tại l&agrave; tấm&nbsp; gương phản chiếu t&acirc;m hồn của ch&iacute;nh ch&uacute;ng ta.&rdquo; V&igrave; thế t&ocirc;i học c&aacute;ch cảm ơn những g&igrave; đang c&oacute;, bu&ocirc;ng bớt đi những &aacute;p lực l&ecirc;n bản th&acirc;n v&agrave; con c&aacute;i để vừa c&acirc;n bằng cuộc sống, vừa chăm con m&agrave; vẫn chăm lo cho sự nghiệp. H&atilde;y tin rằng bạn sẽ lu&ocirc;n nu&ocirc;i dạy được những đứa trẻ th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c theo những c&aacute;ch rất ri&ecirc;ng của m&igrave;nh, để ch&uacute;ng tự t&igrave;m ra sứ mệnh ri&ecirc;ng của cuộc đời m&igrave;nh&rdquo; (Nguyễn Thị Thu)</p>\n\n<p>T&aacute;c giả: NGUYỄN THỊ THU - (Aki Nguyễn)</p>\n\n<p>&bull; Tốt nghiệp tiến sĩ tại Đại học Tsukuba, Nhật Bản.</p>\n\n<p>&bull; Hiện c&oacute; một con trai 4 tuổi, t&ecirc;n th&acirc;n mật l&agrave; Bon.</p>\n\n<p>&bull; L&agrave; đồng s&aacute;ng lập của Trường Mầm non Tsubaki v&agrave; l&agrave; dịch giả của nhiều đầu s&aacute;ch nu&ocirc;i dạy con của Nhật như: &ldquo;Chờ đến mẫu gi&aacute;o th&igrave; đ&atilde; muộn&rdquo;, &ldquo;Cha mẹ Nhật dạy con tự lập&rdquo;, &ldquo;Nu&ocirc;i dạy con kiểu Nhật Bản, tập 2&rdquo;, v&agrave; rất nhiều Ehon Nhật như bộ &ldquo;Chơi c&ugrave;ng Momo&rdquo;, &ldquo;Những em b&eacute; đ&aacute;ng y&ecirc;u&rdquo;, bộ &ldquo;Voi Pao&rdquo; &hellip;</p>\n', 2019, 280, 268),
(98, 61, 'Quang Van Books', 'NXB Phụ Nữ Việt Nam', 'Lý Tĩnh', 'Bìa mềm', 'Thu Trần', '<p>Giai đoạn 3-6 tuổi l&agrave; thời k&igrave; ph&aacute;t triển t&acirc;m sinh l&yacute; quan trọng của trẻ. Trong giai đoạn n&agrave;y, trẻ sẽ nảy sinh niềm y&ecirc;u th&iacute;ch v&agrave; theo đuổi v&ocirc; c&ugrave;ng cuồng nhiệt đối với một sự vật hoặc h&agrave;nh động nhất định. Đ&acirc;y cũng được coi l&agrave; thời k&igrave; v&agrave;ng để ph&aacute;t triển c&aacute;c loại năng lực của trẻ nhờ khả năng học hỏi v&ocirc; c&ugrave;ng lớn.</p>\n', '<p>Giai đoạn 3-6 tuổi l&agrave; thời k&igrave; ph&aacute;t triển t&acirc;m sinh l&yacute; quan trọng của trẻ. Trong giai đoạn n&agrave;y, trẻ sẽ nảy sinh niềm y&ecirc;u th&iacute;ch v&agrave; theo đuổi v&ocirc; c&ugrave;ng cuồng nhiệt đối với một sự vật hoặc h&agrave;nh động nhất định. Đ&acirc;y cũng được coi l&agrave; thời k&igrave; v&agrave;ng để ph&aacute;t triển c&aacute;c loại năng lực của trẻ nhờ khả năng học hỏi v&ocirc; c&ugrave;ng lớn.</p>\n\n<p>Tuy nhi&ecirc;n, khi đối mặt với những h&agrave;nh động &ldquo;phản kh&aacute;ng&rdquo; thậm ch&iacute; đ&ograve;i hỏi &ldquo;v&ocirc; l&iacute;&rdquo; của trẻ nhiều bố mẹ đ&atilde; kh&ocirc;ng c&oacute; được những phương ph&aacute;p đ&uacute;ng đắn, ph&ugrave; hợp khiến trẻ kh&ocirc;ng những kh&ocirc;ng ph&aacute;t huy được năng lực m&agrave; c&ograve;n ảnh hưởng đến t&acirc;m l&iacute; c&ograve;n non nớt của trẻ.</p>\n\n<p>Ch&iacute;nh v&igrave; vậy, cuốn s&aacute;ch &ldquo;C&ugrave;ng con bước qua thời k&igrave; nhạy cảm 3-6 tuổi&rdquo; sẽ c&ugrave;ng c&aacute;c bậc phụ huynh nghi&ecirc;n cứu c&aacute;c đặc điểm, biểu hiện trong thời k&igrave; nhạy cảm 3-6 tuổi của trẻ v&agrave; đưa ra c&aacute;c biện ph&aacute;p ứng ph&oacute; ph&ugrave; hợp nhất với từng biểu hiện.</p>\n\n<p>Bằng lối viết kể chuyện, th&ocirc;ng qua từng chia sẻ ch&acirc;n thực, gần gũi t&aacute;c giả đ&atilde; dẫn dắt c&aacute;c bậc phụ huynh từng bước bước v&agrave;o thế giới nội t&acirc;m của trẻ, gợi mở b&iacute; mật li&ecirc;n quan tới ch&uacute;ng nhằm hỗ trợ phụ huynh xử l&iacute; đ&uacute;ng đắn c&aacute;c vấn đề trong giai đoạn n&agrave;y. Từ đ&oacute; c&ugrave;ng con bước qua thời k&igrave; n&agrave;y một c&aacute;ch nhẹ nh&agrave;ng, tạo điều kiện để trẻ ph&aacute;t huy tối đa tiềm năng của m&igrave;nh.</p>\n\n<p>Trong cuốn s&aacute;ch n&agrave;y t&aacute;c giả L&yacute; Tĩnh c&ograve;n chỉ ra 8 thời k&igrave; v&agrave;ng của trẻ trong giai đoạn 3-6 tuổi để gi&uacute;p bố mẹ c&oacute; lộ tr&igrave;nh nu&ocirc;i dạy con ph&ugrave; hợp v&agrave; bồi dưỡng nh&acirc;n c&aacute;ch trẻ để trở th&agrave;nh một đứa trẻ ưu t&uacute;, tự tin v&agrave; s&aacute;ng tạo.</p>\n\n<p><strong>GIỚI THIỆU T&Aacute;C GIẢ</strong></p>\n\n<p>L&yacute; Tĩnh l&agrave; nh&agrave; nghi&ecirc;n cứu gi&aacute;o dục gia đ&igrave;nh, t&aacute;c giả nổi tiếng chủ yếu viết về chủ đề gi&aacute;o dục gia đ&igrave;nh v&agrave; tiểu thuyết thiếu nhi.</p>\n\n<p>Những t&aacute;c phẩm đ&atilde; xuất bản của b&agrave; c&oacute; thể kế đến: Thỏ Lamu, chạy mau; Cẩm nang dạy con cho c&aacute;c b&agrave; mẹ l&agrave; d&acirc;n văn ph&ograve;ng; Mạo hiểm c&ugrave;ng con trai: S&aacute;ch ma thuật nu&ocirc;i dạy con trai th&ocirc;ng minh, hiếu động; Nghệ thuật c&ugrave;ng con g&aacute;i: S&aacute;ch ma thuật nu&ocirc;i dạy con g&aacute;i thanh lịch v&agrave; đa t&agrave;i đa nghệ&hellip;</p>\n', 2021, 400, 320),
(99, 62, 'Cty Sách Giáo Dục Tại TP.HCM', 'NXB Giáo Dục Việt Nam', 'Bộ Giáo Dục Và Đào Tạo', 'Bìa mềm', 'Không có', '<p>Atlat Địa L&iacute; Việt Nam - 2021</p>\n', '<p>Atlat Địa L&iacute; Việt Nam - 2021</p>\n', 2021, 183, 31);
INSERT INTO `chitietsanpham` (`id`, `masp`, `nhacungcap`, `nhaxuatban`, `tacgia`, `hinhthuc`, `nguoidich`, `mota`, `noidung`, `namxuatban`, `trongluong`, `sotrang`) VALUES
(100, 63, 'Cty Sách & Thiết Bị Giáo Dục Miền Nam', 'NXB Giáo Dục Việt Nam', 'Nhiều Tác Giả', 'Bìa mềm', 'Không có', '<p>Với chủ trương cung cấp cho c&aacute;c em học sinh một bộ t&agrave;i liệu tham khảo quan trọng nhằm chuẩn bị cho kỳ thi Trung học phổ th&ocirc;ng năm 2021, theo định hướng nhằm đ&aacute;nh gi&aacute; ch&iacute;nh x&aacute;c kết quả học tập của học sinh sau 12 năm học ở mức độ học vấn phổ th&ocirc;ng, l&agrave;m cơ sở cho tuyển sinh gi&aacute;o dục đại học, gi&aacute;o dục nghề nghiệp theo tinh thần tự chủ tuyển sinh.</p>\n', '<p>Với chủ trương cung cấp cho c&aacute;c em học sinh một bộ t&agrave;i liệu tham khảo quan trọng nhằm chuẩn bị cho kỳ thi Trung học phổ th&ocirc;ng năm 2021, theo định hướng nhằm đ&aacute;nh gi&aacute; ch&iacute;nh x&aacute;c kết quả học tập của học sinh sau 12 năm học ở mức độ học vấn phổ th&ocirc;ng, l&agrave;m cơ sở cho tuyển sinh gi&aacute;o dục đại học, gi&aacute;o dục nghề nghiệp theo tinh thần tự chủ tuyển sinh.</p>\n\n<p>Dựa tr&ecirc;n định hướng đ&oacute;, Bộ đề trắc nghiệm luyện thi THPT Quốc gia 2021 &ndash; Khoa học x&atilde; hội tập 1 nằm trong &ldquo;Bộ đề luyện thi THPT Quốc gia 2021&rdquo; được Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam bi&ecirc;n soạn v&agrave; xuất bản nhằm đ&aacute;p ứng kịp thời nhu cầu &ocirc;n tập v&agrave; r&egrave;n luyện cấp thiết cho c&aacute;c em học sinh lớp 12 kho&aacute; 2020 &ndash; 2021, chuẩn bị cho Kỳ thi THPT Quốc gia 2021.</p>\n\n<p><strong>CẤU TR&Uacute;C BỘ ĐỀ TRẮC NGHIỆM LUYỆN THI THPT QUỐC GIA 2021 &ndash; KHOA HỌC X&Atilde; HỘI TẬP 1</strong></p>\n\n<p><em>Phần một:</em>&nbsp;<strong>Phương ph&aacute;p l&agrave;m b&agrave;i trắc nghiệm</strong></p>\n\n<p>Hướng dẫn cụ thể phương ph&aacute;p l&agrave;m b&agrave;i trắc nghiệm ph&ugrave; hợp với từng m&ocirc;n (Lịch sử, Địa l&iacute; v&agrave; GDCD); Nhưng lưu &yacute; cho học sinh đối với một số dạng c&acirc;u trắc nghiệm m&agrave; học sinh dễ mắc lỗi khi l&agrave;m b&agrave;i.</p>\n\n<p><em>Phần hai:</em>&nbsp;<strong>C&aacute;c đề &ocirc;n luyện (9 đề)</strong></p>\n\n<p>Mỗi đề m&ocirc;n&nbsp;<em>Lịch sử, Địa l&iacute;, Gi&aacute;o dục c&ocirc;ng d&acirc;n (GDCD)</em>&nbsp;gồm 40 c&acirc;u trắc nghiệm, được ph&acirc;n h&oacute;a theo 4 mức độ: nhận biết, th&ocirc;ng hiểu, vận dụng v&agrave; vận dụng cao; nội dung nằm chủ yếu trong chương tr&igrave;nh lớp 12 Trung học phổ th&ocirc;ng.</p>\n\n<p><em>Phần ba:</em>&nbsp;<strong>Đ&aacute;p &aacute;n</strong></p>\n\n<p>Phần n&agrave;y gồm bảng đ&aacute;p &aacute;n của tất cả c&aacute;c c&acirc;u hỏi, b&agrave;i tập để học sinh so v&agrave; đối chiếu với b&agrave;i l&agrave;m trong qu&aacute; tr&igrave;nh &ocirc;n luyện của m&igrave;nh.</p>\n\n<p><strong>TH&Ocirc;NG TIN TH&Ecirc;M VỀ BỘ ĐỀ TRẮC NGHIỆM LUYỆN THI THPT QUỐC GIA 2021</strong></p>\n\n<p>Bộ đề luyện thi THPT Quốc gia 2021 &ndash; cung cấp những c&acirc;u hỏi thực tế, b&aacute;m s&aacute;t chương tr&igrave;nh học v&agrave; quy chế ra đề của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo. Với đội ngũ t&aacute;c giả l&agrave; c&aacute;c thầy c&ocirc; gi&aacute;o, c&aacute;c chuy&ecirc;n gia đầy kinh nghiệm từng tham gia c&ocirc;ng t&aacute;c bi&ecirc;n soạn ng&acirc;n h&agrave;ng đề thi, đ&acirc;y l&agrave; bộ s&aacute;ch định hướng rất cần thiết v&agrave; bổ &iacute;ch, đ&aacute;p ứng nhu cầu của học sinh trong việc &ocirc;n luyện v&agrave; chuẩn bị cho kỳ thi Trung học phổ th&ocirc;ng năm 2021.</p>\n', 2021, 200, 204),
(101, 64, 'Nhã Nam', 'NXB Văn Học', 'Ray Bradbury', 'Bìa cứng', 'Dick Trương', '<p>H&atilde;y mường tượng một thế giới nơi truyền h&igrave;nh thống trị v&agrave; văn chương ngấp ngh&eacute; tr&ecirc;n bờ tuyệt chủng, nơi th&ocirc;ng tin&nbsp;n&ocirc;ng cạn được tung h&ocirc; c&ograve;n tri thức v&agrave; &yacute; tưởng th&igrave; bị ruồng rẫy, nơi t&agrave;ng trữ s&aacute;ch l&agrave; phạm ph&aacute;p, ta c&oacute; thể bị bắt chỉ v&igrave;&nbsp;tản bộ tr&ecirc;n vỉa h&egrave;, c&ograve;n nhiệm vụ của những người l&iacute;nh kh&ocirc;ng phải cứu hỏa m&agrave; l&agrave; ch&acirc;m mồi cho những đ&aacute;m ch&aacute;y&hellip;&nbsp;</p>\n', '<p>H&atilde;y mường tượng một thế giới nơi truyền h&igrave;nh thống trị v&agrave; văn chương ngấp ngh&eacute; tr&ecirc;n bờ tuyệt chủng, nơi th&ocirc;ng tin&nbsp;n&ocirc;ng cạn được tung h&ocirc; c&ograve;n tri thức v&agrave; &yacute; tưởng th&igrave; bị ruồng rẫy, nơi t&agrave;ng trữ s&aacute;ch l&agrave; phạm ph&aacute;p, ta c&oacute; thể bị bắt chỉ v&igrave;&nbsp;tản bộ tr&ecirc;n vỉa h&egrave;, c&ograve;n nhiệm vụ của những người l&iacute;nh kh&ocirc;ng phải cứu hỏa m&agrave; l&agrave; ch&acirc;m mồi cho những đ&aacute;m ch&aacute;y&hellip; Khi khắc họa c&aacute;i x&atilde; hội giả tưởng ấy qua con mắt nh&igrave;n khủng hoảng niềm tin của anh l&iacute;nh ph&oacute;ng hỏa Montag, Ray&nbsp; Bradbury chắc kh&ocirc;ng thể ngờ v&agrave;o t&iacute;nh ti&ecirc;n tri khủng khiếp của cuốn s&aacute;ch. X&atilde; hội ch&uacute;ng ta đang sống ng&agrave;y nay giống&nbsp;với những g&igrave; Bradbury m&ocirc; tả đến r&ugrave;ng m&igrave;nh: một nền văn minh lệ thuộc qu&aacute; nhiều v&agrave;o truyền th&ocirc;ng, giải tr&iacute; v&agrave; c&ocirc;ng&nbsp;nghệ. Bởi lẽ đ&oacute;, hơn s&aacute;u chục năm kể từ lần đầu xuất bản, 451 độ F vẫn đủ sức khiến bất kỳ ai đọc n&oacute; phải sửng sốt&nbsp;v&agrave; cho&aacute;ng v&aacute;ng.</p>\n', 2021, 300, 232),
(102, 65, 'Minh Long', 'NXB Văn học', 'Jules Verne', 'Bìa mềm', 'Không có', '<p>Hai Vạn Dặm Dưới Đ&aacute;y Biển Hai vạn dặm dưới đ&aacute;y biển l&agrave; một trong những t&aacute;c phẩm th&agrave;nh c&ocirc;ng nhất của Jules Verne. Rất nhiều năm đ&atilde; qua, những hiểu biết mọi mặt của con người về biển đ&atilde; tiến những bước d&agrave;i</p>\n', '<p>Hai Vạn Dặm Dưới Đ&aacute;y Biển Hai vạn dặm dưới đ&aacute;y biển l&agrave; một trong những t&aacute;c phẩm th&agrave;nh c&ocirc;ng nhất của Jules Verne. Rất nhiều năm đ&atilde; qua, những hiểu biết mọi mặt của con người về biển đ&atilde; tiến những bước d&agrave;i. Nhiều kh&aacute;i niệm đ&atilde; đổi thay về căn bản. Nhưng sự ph&aacute;t triển của khoa học, kỹ thuật kh&ocirc;ng l&agrave;m ch&uacute;ng ta giảm l&ograve;ng y&ecirc;u mến v&agrave; k&iacute;nh phục Jules Verne, v&igrave; cuốn s&aacute;ch n&agrave;y đ&atilde; g&oacute;p phần hướng biết bao thanh thiếu ni&ecirc;n tiến v&agrave;o khoa học, v&agrave; bao người sau n&agrave;y đ&atilde; trở th&agrave;nh những nh&agrave; hải dương học, ngư học v&agrave; chế tạo t&agrave;u ngầm!</p>\n', 2016, 550, 412),
(103, 66, 'Cty VPP Trung Kiên', 'Không có', 'Không có', 'Mời chọn', 'Không có', '<p><strong>Bấm Kim Size 10 CL-ST200 + Kim Bấm</strong></p>\n\n<p>Sản phẩm c&oacute; thiết kế đẹp mắt, inox s&aacute;ng b&oacute;ng kết hợp với c&aacute;c thanh nhựa mang t&iacute;nh thẩm mỹ cao.</p>\n\n<p>Sản phẩm c&oacute; k&iacute;ch thước nhỏ nhắn, dễ d&agrave;ng mang theo, sử dụng kim bấm số 10 th&ocirc;ng dụng.</p>\n\n<p>Cấu tạo sản phẩm cho lực bấm nhẹ, c&oacute; thể bấm nhiều tờ giấy c&ugrave;ng l&uacute;c rất tiện lợi.</p>\n', '<p><strong>Bấm Kim Size 10 CL-ST200 + Kim Bấm</strong></p>\n\n<p>Sản phẩm c&oacute; thiết kế đẹp mắt, inox s&aacute;ng b&oacute;ng kết hợp với c&aacute;c thanh nhựa mang t&iacute;nh thẩm mỹ cao.</p>\n\n<p>Sản phẩm c&oacute; k&iacute;ch thước nhỏ nhắn, dễ d&agrave;ng mang theo, sử dụng kim bấm số 10 th&ocirc;ng dụng.</p>\n\n<p>Cấu tạo sản phẩm cho lực bấm nhẹ, c&oacute; thể bấm nhiều tờ giấy c&ugrave;ng l&uacute;c rất tiện lợi.</p>\n\n<p>Cỗ tay bấm c&oacute; bọc lớp nhựa &ecirc;m &aacute;i, tr&aacute;nh đau tay v&agrave; bị phạm v&agrave;o tay khi thao t&aacute;c.</p>\n\n<p>Th&acirc;n sản phẩm l&agrave;m bằng chất liệu inox s&aacute;ng b&oacute;ng, kh&ocirc;ng bị gỉ s&eacute;t sau một thời gian sử dụng, an to&agrave;n cho người d&ugrave;ng.</p>\n', 0, 0, 0);

